import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import styled from 'styled-components';
import { GlobalStyle } from './styles/GlobalStyle';
import { AppContext } from './context/AppContext';
import { AuthProvider } from './context/AuthContext';
import ErrorBoundary from './components/ErrorBoundary';


// User Components
import PlatformHomePage from './components/user/PlatformHomePage';
import PricingPage from './components/user/PricingPage';
import LoginPage from './components/auth/LoginPage';
import RegisterPage from './components/auth/RegisterPage';
import StartBuilding from './components/StartBuilding';

// Shared Components
import ScrollToTop from './components/shared/ScrollToTop';
import SmartRouter from './components/SmartRouter';

// Hotel Module
// Note: HotelModule removed as it's not used in current routing

// Mock data
import {
  hotels as hotelModuleData,
  hotelBookings,
  ownerHotels as hotelOwnerData,
} from './DummyData';

// Category Landing Pages
import HotelCategoryLanding from './components/category/HotelCategoryLanding';
import EcommerceCategoryLanding from './components/category/EcommerceCategoryLanding';
import WeddingCategoryLanding from './components/category/WeddingCategoryLanding';
import AutomobileCategoryLanding from './components/category/AutomobileCategoryLanding';
import BusinessCategoryLanding from './components/category/BusinessCategoryLanding';

// Store Listing Pages
import OwnerDashboard from './components/owner/OwnerDashboard';
import RoomDetail from './hotel/pages/RoomDetail';
import Booking from './hotel/pages/Booking';
import BookingConfirmation from './hotel/pages/BookingConfirmation';
import { NotificationProvider } from './ecommerce/components/NotificationSystem';
import RoomList from './hotel/pages/RoomList';
import ProductDetail from './ecommerce/pages/ProductDetail';
import ProductList from './ecommerce/pages/ProductList';
import SellerDashboard from './ecommerce/pages/SellerDashboard';
import VendorPortfolio from './weddings/pages/VendorPortfolio';
import VendorDashboard from './weddings/pages/VendorDashboard';
import Vehicles from './automobiles/pages/Vehicles';
import VehicleDetail from './automobiles/pages/VehicleDetail';
import DealerDashboard from './automobiles/pages/DealerDashboard';
import Wishlist from './automobiles/pages/Wishlist';
import BuisnessAdminDashboard from './business/pages/BuisnessAdminDashboard';

const AppContainer = styled.div`
  min-height: 100vh;
  background-color: #f8fafc;
`;

function App() {
  const [user, setUser] = useState(null);
  const [userType, setUserType] = useState('customer'); // 'customer' or 'owner'
  const [hotels, setHotels] = useState(hotelModuleData);
  const [bookings, setBookings] = useState(hotelBookings);
  const [ownerHotels, setOwnerHotels] = useState(hotelOwnerData);

  const contextValue = {
    user,
    setUser,
    userType,
    setUserType,
    hotels,
    setHotels,
    bookings,
    setBookings,
    ownerHotels,
    setOwnerHotels,
  };

  return (
    <ErrorBoundary>
      <AuthProvider>
        <NotificationProvider>
          <AppContext.Provider value={contextValue}>
            <Router>
              <AppContainer>
                <GlobalStyle />
                <ScrollToTop />
                <Routes>
                    {/* Main Platform Routes (Highest Priority) */}
                    <Route path="/" element={<PlatformHomePage />} />
                    <Route path="/pricing" element={<PricingPage />} />
                    <Route path="/login" element={<LoginPage />} />
                    <Route path="/register" element={<RegisterPage />} />
                    <Route path="/start-building" element={<StartBuilding />} />

                    {/* categories for website examples*/}
                    <Route path="/website-examples">
                      <Route path="hotel" element={<HotelCategoryLanding />} />
                      <Route
                        path="wedding"
                        element={<WeddingCategoryLanding />}
                      />
                      <Route
                        path="ecommerce"
                        element={<EcommerceCategoryLanding />}
                      />
                      <Route
                        path="automobile"
                        element={<AutomobileCategoryLanding />}
                      />
                      <Route
                        path="business"
                        element={<BusinessCategoryLanding />}
                      />
                    </Route>

                    {/* Generic slug routes (lowest priority) */}
                    <Route path="/:slug">
                      {/* smart router identifies vendor and give homepage of same */}
                      <Route index element={<SmartRouter />} />

                      {/*  hotel website routes */}
                      <Route path="hoteladmin" element={<OwnerDashboard />} />
                      <Route path="rooms" element={<RoomList />} />
                      <Route path="rooms/:roomId" element={<RoomDetail />} />
                      <Route
                        path="rooms/booking/:roomId"
                        element={<Booking />}
                      />
                      <Route
                        path="bookingconfirmation"
                        element={<BookingConfirmation />}
                      />

                      {/*  Ecommerce website routes */}

                      <Route path="products" element={<ProductList />} />
                      <Route
                        path="productdetail/:productId"
                        element={<ProductDetail />}
                      />
                      <Route
                        path="selleradminpanel"
                        element={<SellerDashboard />}
                      />
                      {/*  Wedding website routes */}
                      <Route path="portfolio" element={<VendorPortfolio />} />
                      <Route
                        path="weddingadminpanel"
                        element={<VendorDashboard />}
                      />
                      {/*  automobile website routes */}
                      <Route path="vehicles" element={<Vehicles />} />
                      <Route
                        path="vehicledetail/:vehicleId"
                        element={<VehicleDetail />}
                      />
                      <Route path="wishlist" element={<Wishlist />} />
                      <Route
                        path="autoadmindasboard"
                        element={<DealerDashboard />}
                      />

                      {/*  buisness website routes */}
                      <Route
                        path="adminpanel"
                        element={<BuisnessAdminDashboard />}
                      />
                    </Route>
                  </Routes>
                </AppContainer>
              </Router>
            </AppContext.Provider>
          </NotificationProvider>
        </AuthProvider>
    </ErrorBoundary>
  );
}

export default App;

import React, { useState } from 'react';
import styled from 'styled-components';
import {
  FaCar,
  FaUpload,
  FaSave,
  FaImage,
  FaTimes,
  FaCheck,
  FaEdit,
  FaClock,
  FaExclamationTriangle,
  FaInfoCircle,
  FaDollarSign,
  FaRoad,
  FaGasPump,
  FaCogs,
  FaPalette,
  FaIdCard,
} from 'react-icons/fa';
import { theme } from '../../styles/GlobalStyle';

const Container = styled.div`
  background: ${theme.colors.white};
  border-radius: ${theme.borderRadius.lg};
  box-shadow: ${theme.shadows.sm};
  overflow: hidden;
`;

const Header = styled.div`
  padding: ${theme.spacing.xl};
  border-bottom: 1px solid ${theme.colors.gray200};
  background: ${theme.colors.gray50};
`;

const Title = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: ${theme.colors.gray900};
  margin-bottom: ${theme.spacing.sm};
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
`;

const Description = styled.p`
  color: ${theme.colors.gray600};
  font-size: 1rem;
  line-height: 1.5;
`;

const FormContainer = styled.div`
  padding: ${theme.spacing.xl};
  max-width: 1200px;
  margin: 0 auto;
`;

const FormGrid = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: ${theme.spacing.xl};

  @media (max-width: ${theme.breakpoints.tablet}) {
    grid-template-columns: 1fr;
    gap: ${theme.spacing.lg};
  }
`;

const FormSection = styled.div`
  background: ${theme.colors.white};
  border: 1px solid ${theme.colors.gray200};
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.xl};
  margin-bottom: ${theme.spacing.lg};
`;

const SectionTitle = styled.h3`
  font-size: 1.2rem;
  font-weight: 600;
  color: ${theme.colors.gray900};
  margin-bottom: ${theme.spacing.lg};
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: ${theme.spacing.lg};
  margin-bottom: ${theme.spacing.lg};

  &.single {
    grid-template-columns: 1fr;
  }

  &.two-col {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (max-width: ${theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
    gap: ${theme.spacing.md};
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  font-weight: 600;
  color: ${theme.colors.gray700};
  margin-bottom: ${theme.spacing.sm};
  font-size: 0.9rem;
  display: flex;
  align-items: center;
  gap: ${theme.spacing.xs};

  .required {
    color: ${theme.colors.error};
  }
`;

const Input = styled.input.withConfig({
  shouldForwardProp: prop => prop !== 'hasError',
})`
  padding: ${theme.spacing.md};
  border: 2px solid
    ${props => (props.hasError ? theme.colors.error : theme.colors.gray200)};
  border-radius: ${theme.borderRadius.md};
  font-size: 1rem;
  transition: border-color 0.2s ease;
  background: ${theme.colors.white};

  &:focus {
    outline: none;
    border-color: ${props =>
      props.hasError ? theme.colors.error : theme.colors.primary};
  }

  &::placeholder {
    color: ${theme.colors.gray500};
  }

  &:disabled {
    background: ${theme.colors.gray100};
    cursor: not-allowed;
  }
`;

const Select = styled.select.withConfig({
  shouldForwardProp: prop => prop !== 'hasError',
})`
  padding: ${theme.spacing.md};
  border: 2px solid
    ${props => (props.hasError ? theme.colors.error : theme.colors.gray200)};
  border-radius: ${theme.borderRadius.md};
  font-size: 1rem;
  transition: border-color 0.2s ease;
  background: ${theme.colors.white};
  cursor: pointer;

  &:focus {
    outline: none;
    border-color: ${props =>
      props.hasError ? theme.colors.error : theme.colors.primary};
  }

  &:disabled {
    background: ${theme.colors.gray100};
    cursor: not-allowed;
  }
`;

const TextArea = styled.textarea.withConfig({
  shouldForwardProp: prop => prop !== 'hasError',
})`
  padding: ${theme.spacing.md};
  border: 2px solid
    ${props => (props.hasError ? theme.colors.error : theme.colors.gray200)};
  border-radius: ${theme.borderRadius.md};
  font-size: 1rem;
  transition: border-color 0.2s ease;
  background: ${theme.colors.white};
  resize: vertical;
  min-height: 100px;
  font-family: inherit;

  &:focus {
    outline: none;
    border-color: ${props =>
      props.hasError ? theme.colors.error : theme.colors.primary};
  }

  &::placeholder {
    color: ${theme.colors.gray500};
  }
`;

const ErrorMessage = styled.div`
  color: ${theme.colors.error};
  font-size: 0.8rem;
  margin-top: ${theme.spacing.xs};
  display: flex;
  align-items: center;
  gap: ${theme.spacing.xs};
`;

const HelpText = styled.div`
  color: ${theme.colors.gray500};
  font-size: 0.8rem;
  margin-top: ${theme.spacing.xs};
`;

const ImageUploadSection = styled.div`
  border: 2px dashed ${theme.colors.gray300};
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.xl};
  text-align: center;
  transition: all 0.2s ease;
  cursor: pointer;
  background: ${theme.colors.gray50};

  &:hover {
    border-color: ${theme.colors.primary};
    background: ${theme.colors.primary}10;
  }

  &.drag-over {
    border-color: ${theme.colors.primary};
    background: ${theme.colors.primary}20;
  }
`;

const ImageUploadIcon = styled.div`
  font-size: 3rem;
  color: ${theme.colors.gray400};
  margin-bottom: ${theme.spacing.md};
`;

const ImageUploadText = styled.div`
  font-size: 1.1rem;
  font-weight: 600;
  color: ${theme.colors.gray700};
  margin-bottom: ${theme.spacing.sm};
`;

const ImageUploadSubtext = styled.div`
  color: ${theme.colors.gray500};
  font-size: 0.9rem;
`;

const FileInput = styled.input`
  display: none;
`;

const ImagePreviewGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(120px, 1fr));
  gap: ${theme.spacing.md};
  margin-top: ${theme.spacing.lg};
`;

const ImagePreview = styled.div`
  position: relative;
  border-radius: ${theme.borderRadius.md};
  overflow: hidden;
  border: 2px solid ${theme.colors.gray200};
  background: ${theme.colors.white};
`;

const PreviewImage = styled.img`
  width: 100%;
  height: 100px;
  object-fit: cover;
`;

const RemoveImageButton = styled.button`
  position: absolute;
  top: ${theme.spacing.xs};
  right: ${theme.spacing.xs};
  background: ${theme.colors.error};
  color: ${theme.colors.white};
  border: none;
  border-radius: 50%;
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 0.8rem;
  transition: all 0.2s ease;

  &:hover {
    background: ${theme.colors.errorDark};
    transform: scale(1.1);
  }
`;

const PricingSection = styled.div`
  background: ${theme.colors.gray50};
  padding: ${theme.spacing.lg};
  border-radius: ${theme.borderRadius.md};
  border: 1px solid ${theme.colors.gray200};
`;

const PriceDisplay = styled.div`
  text-align: center;
  padding: ${theme.spacing.lg};
  background: ${theme.colors.white};
  border-radius: ${theme.borderRadius.md};
  border: 2px solid ${theme.colors.primary}20;
  margin-bottom: ${theme.spacing.lg};
`;

const PriceAmount = styled.div`
  font-size: 2rem;
  font-weight: 700;
  color: ${theme.colors.primary};
  margin-bottom: ${theme.spacing.xs};
`;

const PriceLabel = styled.div`
  color: ${theme.colors.gray600};
  font-size: 0.9rem;
`;

const StatusSection = styled.div`
  background: ${theme.colors.gray50};
  padding: ${theme.spacing.lg};
  border-radius: ${theme.borderRadius.md};
  border: 1px solid ${theme.colors.gray200};
  margin-bottom: ${theme.spacing.lg};
`;

const StatusIndicator = styled.div.withConfig({
  shouldForwardProp: prop => prop !== 'status',
})`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
  padding: ${theme.spacing.md};
  border-radius: ${theme.borderRadius.md};
  background: ${props => {
    switch (props.status) {
      case 'draft':
        return theme.colors.gray100;
      case 'active':
        return theme.colors.success + '20';
      case 'pending':
        return theme.colors.warning + '20';
      default:
        return theme.colors.gray100;
    }
  }};
  color: ${props => {
    switch (props.status) {
      case 'draft':
        return theme.colors.gray700;
      case 'active':
        return theme.colors.success;
      case 'pending':
        return theme.colors.warning;
      default:
        return theme.colors.gray700;
    }
  }};
  font-weight: 600;
  margin-bottom: ${theme.spacing.md};
`;

const ActionButtons = styled.div`
  display: flex;
  gap: ${theme.spacing.md};
  justify-content: flex-end;
  padding: ${theme.spacing.xl};
  border-top: 1px solid ${theme.colors.gray200};
  background: ${theme.colors.gray50};

  @media (max-width: ${theme.breakpoints.mobile}) {
    flex-direction: column;
  }
`;

const Button = styled.button.withConfig({
  shouldForwardProp: prop => prop !== 'variant',
})`
  padding: ${theme.spacing.md} ${theme.spacing.xl};
  border-radius: ${theme.borderRadius.md};
  font-weight: 600;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
  min-width: 140px;
  justify-content: center;

  background: ${props => {
    switch (props.variant) {
      case 'primary':
        return theme.colors.primary;
      case 'success':
        return theme.colors.success;
      case 'secondary':
        return theme.colors.white;
      default:
        return theme.colors.gray200;
    }
  }};

  color: ${props => {
    switch (props.variant) {
      case 'primary':
        return theme.colors.white;
      case 'success':
        return theme.colors.white;
      case 'secondary':
        return theme.colors.gray700;
      default:
        return theme.colors.gray700;
    }
  }};

  border: ${props => {
    switch (props.variant) {
      case 'primary':
        return 'none';
      case 'success':
        return 'none';
      case 'secondary':
        return `2px solid ${theme.colors.gray200}`;
      default:
        return `1px solid ${theme.colors.gray300}`;
    }
  }};

  &:hover:not(:disabled) {
    transform: translateY(-1px);
    box-shadow: ${theme.shadows.md};

    background: ${props => {
      switch (props.variant) {
        case 'primary':
          return theme.colors.primaryDark;
        case 'success':
          return theme.colors.successDark;
        case 'secondary':
          return theme.colors.gray50;
        default:
          return theme.colors.gray300;
      }
    }};
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
  }
`;

const AddVehicleTab = ({ dealer }) => {
  const [formData, setFormData] = useState({
    // Basic Information
    vin: '',
    year: '',
    make: '',
    model: '',
    trim: '',
    bodyStyle: '',

    // Engine & Performance
    engine: '',
    transmission: '',
    drivetrain: '',
    fuelType: '',
    mpgCity: '',
    mpgHighway: '',

    // Exterior & Interior
    exteriorColor: '',
    interiorColor: '',
    interiorMaterial: '',

    // Condition & History
    condition: '',
    mileage: '',
    previousOwners: '1',
    accidentHistory: 'no',
    serviceHistory: 'yes',

    // Pricing
    msrp: '',
    price: '',
    costPrice: '',

    // Inventory
    stock: '1',
    location: '',

    // Description & Features
    description: '',
    features: '',

    // Status
    status: 'draft',
  });

  const [images, setImages] = useState([]);
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState('');

  const currentYear = new Date().getFullYear();
  const years = Array.from({ length: 30 }, (_, i) => currentYear - i);

  const makes = [
    'Audi',
    'BMW',
    'Chevrolet',
    'Ford',
    'Honda',
    'Hyundai',
    'Kia',
    'Lexus',
    'Mercedes-Benz',
    'Nissan',
    'Porsche',
    'Tesla',
    'Toyota',
    'Volkswagen',
    'Volvo',
  ];

  const bodyStyles = [
    'Sedan',
    'SUV',
    'Hatchback',
    'Coupe',
    'Convertible',
    'Wagon',
    'Pickup Truck',
    'Van',
    'Crossover',
  ];

  const fuelTypes = [
    'Gasoline',
    'Diesel',
    'Hybrid',
    'Electric',
    'Plug-in Hybrid',
  ];

  const transmissions = [
    'Manual',
    'Automatic',
    'CVT',
    'Semi-Automatic',
    'Dual-Clutch',
  ];

  const drivetrains = ['FWD', 'RWD', 'AWD', '4WD'];

  const conditions = ['New', 'Used', 'Certified Pre-Owned'];

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const handleImageUpload = e => {
    const files = Array.from(e.target.files);

    files.forEach(file => {
      if (file.type.startsWith('image/')) {
        const reader = new FileReader();
        reader.onload = event => {
          setImages(prev => [
            ...prev,
            {
              id: Date.now() + Math.random(),
              file,
              url: event.target.result,
              name: file.name,
            },
          ]);
        };
        reader.readAsDataURL(file);
      }
    });
  };

  const removeImage = imageId => {
    setImages(prev => prev.filter(img => img.id !== imageId));
  };

  const validateForm = () => {
    const newErrors = {};

    // Required fields
    const requiredFields = [
      'vin',
      'year',
      'make',
      'model',
      'condition',
      'mileage',
      'price',
    ];

    requiredFields.forEach(field => {
      if (!formData[field]) {
        newErrors[field] = 'This field is required';
      }
    });

    // VIN validation (basic)
    if (formData.vin && formData.vin.length !== 17) {
      newErrors.vin = 'VIN must be exactly 17 characters';
    }

    // Year validation
    if (
      formData.year &&
      (formData.year < 1900 || formData.year > currentYear + 1)
    ) {
      newErrors.year = `Year must be between 1900 and ${currentYear + 1}`;
    }

    // Mileage validation
    if (formData.mileage && formData.mileage < 0) {
      newErrors.mileage = 'Mileage cannot be negative';
    }

    // Price validation
    if (formData.price && formData.price <= 0) {
      newErrors.price = 'Price must be greater than 0';
    }

    // MPG validation
    if (formData.mpgCity && (formData.mpgCity <= 0 || formData.mpgCity > 100)) {
      newErrors.mpgCity = 'City MPG must be between 1 and 100';
    }

    if (
      formData.mpgHighway &&
      (formData.mpgHighway <= 0 || formData.mpgHighway > 100)
    ) {
      newErrors.mpgHighway = 'Highway MPG must be between 1 and 100';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async e => {
    e.preventDefault();

    if (!validateForm()) {
      setSubmitStatus('error');
      return;
    }

    setIsSubmitting(true);
    setSubmitStatus('');

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      setSubmitStatus('success');

      // Reset form after successful submission
      setTimeout(() => {
        setFormData({
          vin: '',
          year: '',
          make: '',
          model: '',
          trim: '',
          bodyStyle: '',
          engine: '',
          transmission: '',
          drivetrain: '',
          fuelType: '',
          mpgCity: '',
          mpgHighway: '',
          exteriorColor: '',
          interiorColor: '',
          interiorMaterial: '',
          condition: '',
          mileage: '',
          previousOwners: '1',
          accidentHistory: 'no',
          serviceHistory: 'yes',
          msrp: '',
          price: '',
          costPrice: '',
          stock: '1',
          location: '',
          description: '',
          features: '',
          status: 'draft',
        });
        setImages([]);
        setSubmitStatus('');
      }, 3000);
    } catch (error) {
      setSubmitStatus('error');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleSaveDraft = () => {
    setFormData(prev => ({ ...prev, status: 'draft' }));
    handleSubmit();
  };

  const handlePublish = () => {
    setFormData(prev => ({ ...prev, status: 'active' }));
    handleSubmit();
  };

  const formatPrice = price => {
    if (!price) return '$0';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(price);
  };

  return (
    <Container>
      <Header>
        <Title>
          <FaCar />
          Add New Vehicle
        </Title>
        <Description>
          Add a new vehicle to your inventory with detailed specifications,
          pricing, and images. All required fields must be completed before
          publishing.
        </Description>
      </Header>

      <form onSubmit={handleSubmit}>
        <FormContainer>
          <FormGrid>
            <div>
              {/* Basic Information */}
              <FormSection>
                <SectionTitle>
                  <FaIdCard />
                  Basic Information
                </SectionTitle>

                <FormRow>
                  <FormGroup>
                    <Label>
                      VIN <span className="required">*</span>
                    </Label>
                    <Input
