                  </FormGroup>
                </FormRow>

                <FormRow>
                  <FormGroup>
                    <Label>
                      Model <span className="required">*</span>
                    </Label>
                    <Input
                      type="text"
                      name="model"
                      value={formData.model}
                      onChange={handleInputChange}
                      placeholder="Enter model name"
                      hasError={!!errors.model}
                    />
                    {errors.model && (
                      <ErrorMessage>
                        <FaExclamationTriangle />
                        {errors.model}
                      </ErrorMessage>
                    )}
                  </FormGroup>

                  <FormGroup>
                    <Label>Trim Level</Label>
                    <Input
                      type="text"
                      name="trim"
                      value={formData.trim}
                      onChange={handleInputChange}
                      placeholder="e.g., LX, EX, Sport"
                    />
                  </FormGroup>
                </FormRow>

                <FormRow>
                  <FormGroup>
                    <Label>Body Style</Label>
                    <Select
                      name="bodyStyle"
                      value={formData.bodyStyle}
                      onChange={handleInputChange}
                    >
                      <option value="">Select Body Style</option>
                      {bodyStyles.map(style => (
                        <option key={style} value={style}>
                          {style}
                        </option>
                      ))}
                    </Select>
                  </FormGroup>

                  <FormGroup>
                    <Label>
                      Condition <span className="required">*</span>
                    </Label>
                    <Select
                      name="condition"
                      value={formData.condition}
                      onChange={handleInputChange}
                      hasError={!!errors.condition}
                    >
                      <option value="">Select Condition</option>
                      {conditions.map(condition => (
                        <option key={condition} value={condition}>
                          {condition}
                        </option>
                      ))}
                    </Select>
                    {errors.condition && (
                      <ErrorMessage>
                        <FaExclamationTriangle />
                        {errors.condition}
                      </ErrorMessage>
                    )}
                  </FormGroup>
                </FormRow>
              </FormSection>

              {/* Engine & Performance */}
              <FormSection>
                <SectionTitle>
                  <FaCogs />
                  Engine & Performance
                </SectionTitle>

                <FormRow>
                  <FormGroup>
                    <Label>Engine</Label>
                    <Input
                      type="text"
                      name="engine"
                      value={formData.engine}
                      onChange={handleInputChange}
                      placeholder="e.g., 2.0L Turbo I4"
                    />
                  </FormGroup>

                  <FormGroup>
                    <Label>Transmission</Label>
                    <Select
                      name="transmission"
                      value={formData.transmission}
                      onChange={handleInputChange}
                    >
                      <option value="">Select Transmission</option>
                      {transmissions.map(trans => (
                        <option key={trans} value={trans}>
                          {trans}
                        </option>
                      ))}
                    </Select>
                  </FormGroup>
                </FormRow>

                <FormRow>
                  <FormGroup>
                    <Label>Drivetrain</Label>
                    <Select
                      name="drivetrain"
                      value={formData.drivetrain}
                      onChange={handleInputChange}
                    >
                      <option value="">Select Drivetrain</option>
                      {drivetrains.map(drive => (
                        <option key={drive} value={drive}>
                          {drive}
                        </option>
                      ))}
                    </Select>
                  </FormGroup>

                  <FormGroup>
                    <Label>Fuel Type</Label>
                    <Select
                      name="fuelType"
                      value={formData.fuelType}
                      onChange={handleInputChange}
                    >
                      <option value="">Select Fuel Type</option>
                      {fuelTypes.map(fuel => (
                        <option key={fuel} value={fuel}>
                          {fuel}
                        </option>
                      ))}
                    </Select>
                  </FormGroup>
                </FormRow>

                <FormRow>
                  <FormGroup>
                    <Label>
                      <FaGasPump />
                      City MPG
                    </Label>
                    <Input
                      type="number"
                      name="mpgCity"
                      value={formData.mpgCity}
                      onChange={handleInputChange}
                      placeholder="25"
                      min="1"
                      max="100"
                      hasError={!!errors.mpgCity}
                    />
                    {errors.mpgCity && (
                      <ErrorMessage>
                        <FaExclamationTriangle />
                        {errors.mpgCity}
                      </ErrorMessage>
                    )}
                  </FormGroup>

                  <FormGroup>
                    <Label>
                      <FaRoad />
                      Highway MPG
                    </Label>
                    <Input
                      type="number"
                      name="mpgHighway"
                      value={formData.mpgHighway}
                      onChange={handleInputChange}
                      placeholder="32"
                      min="1"
                      max="100"
                      hasError={!!errors.mpgHighway}
                    />
                    {errors.mpgHighway && (
                      <ErrorMessage>
                        <FaExclamationTriangle />
                        {errors.mpgHighway}
                      </ErrorMessage>
                    )}
                  </FormGroup>
                </FormRow>
              </FormSection>

              {/* Exterior & Interior */}
              <FormSection>
                <SectionTitle>
                  <FaPalette />
                  Exterior & Interior
                </SectionTitle>

                <FormRow>
                  <FormGroup>
                    <Label>Exterior Color</Label>
                    <Input
                      type="text"
                      name="exteriorColor"
                      value={formData.exteriorColor}
                      onChange={handleInputChange}
                      placeholder="e.g., Crystal Black Pearl"
                    />
                  </FormGroup>

                  <FormGroup>
                    <Label>Interior Color</Label>
                    <Input
                      type="text"
                      name="interiorColor"
                      value={formData.interiorColor}
                      onChange={handleInputChange}
                      placeholder="e.g., Black"
                    />
                  </FormGroup>
                </FormRow>

                <FormRow>
                  <FormGroup>
                    <Label>Interior Material</Label>
                    <Select
                      name="interiorMaterial"
                      value={formData.interiorMaterial}
                      onChange={handleInputChange}
                    >
                      <option value="">Select Material</option>
                      <option value="Cloth">Cloth</option>
                      <option value="Leather">Leather</option>
                      <option value="Synthetic Leather">
                        Synthetic Leather
                      </option>
                      <option value="Alcantara">Alcantara</option>
                    </Select>
                  </FormGroup>

                  <FormGroup>
                    <Label>
                      Mileage <span className="required">*</span>
                    </Label>
                    <Input
                      type="number"
                      name="mileage"
                      value={formData.mileage}
                      onChange={handleInputChange}
                      placeholder="25000"
                      min="0"
                      hasError={!!errors.mileage}
                    />
                    {errors.mileage && (
                      <ErrorMessage>
                        <FaExclamationTriangle />
                        {errors.mileage}
                      </ErrorMessage>
                    )}
                  </FormGroup>
                </FormRow>
              </FormSection>

              {/* Vehicle History */}
              <FormSection>
                <SectionTitle>
                  <FaInfoCircle />
                  Vehicle History
                </SectionTitle>

                <FormRow>
                  <FormGroup>
                    <Label>Previous Owners</Label>
                    <Select
                      name="previousOwners"
                      value={formData.previousOwners}
                      onChange={handleInputChange}
                    >
                      <option value="1">1 Owner</option>
                      <option value="2">2 Owners</option>
                      <option value="3">3 Owners</option>
                      <option value="4+">4+ Owners</option>
                    </Select>
                  </FormGroup>

                  <FormGroup>
                    <Label>Accident History</Label>
                    <Select
                      name="accidentHistory"
                      value={formData.accidentHistory}
                      onChange={handleInputChange}
                    >
                      <option value="no">No Accidents</option>
                      <option value="minor">Minor Accident</option>
                      <option value="major">Major Accident</option>
                    </Select>
                  </FormGroup>
                </FormRow>

                <FormRow>
                  <FormGroup>
                    <Label>Service History</Label>
                    <Select
                      name="serviceHistory"
                      value={formData.serviceHistory}
                      onChange={handleInputChange}
                    >
                      <option value="yes">Complete Service Records</option>
                      <option value="partial">Partial Service Records</option>
                      <option value="no">No Service Records</option>
                    </Select>
                  </FormGroup>

                  <FormGroup>
                    <Label>Stock Quantity</Label>
                    <Input
                      type="number"
                      name="stock"
                      value={formData.stock}
                      onChange={handleInputChange}
                      min="1"
                      placeholder="1"
                    />
                  </FormGroup>
                </FormRow>
              </FormSection>

              {/* Description & Features */}
              <FormSection>
                <SectionTitle>
                  <FaInfoCircle />
                  Description & Features
                </SectionTitle>

                <FormRow className="single">
                  <FormGroup>
                    <Label>Vehicle Description</Label>
                    <TextArea
                      name="description"
                      value={formData.description}
                      onChange={handleInputChange}
                      placeholder="Describe the vehicle's condition, special features, and any additional information..."
                      rows="4"
                    />
                    <HelpText>
                      Provide a detailed description that will help customers
                      understand the vehicle's condition and features.
                    </HelpText>
                  </FormGroup>
                </FormRow>

                <FormRow className="single">
                  <FormGroup>
                    <Label>Key Features</Label>
                    <TextArea
                      name="features"
                      value={formData.features}
                      onChange={handleInputChange}
                      placeholder="e.g., Sunroof, Heated Seats, Navigation System, Backup Camera..."
                      rows="3"
                    />
                    <HelpText>
                      List the key features and options that come with this
                      vehicle, separated by commas.
                    </HelpText>
                  </FormGroup>
                </FormRow>
              </FormSection>
            </div>

            <div>
              {/* Pricing Section */}
              <FormSection>
                <SectionTitle>
                  <FaDollarSign />
                  Pricing
                </SectionTitle>

                <PriceDisplay>
                  <PriceAmount>{formatPrice(formData.price)}</PriceAmount>
                  <PriceLabel>Selling Price</PriceLabel>
                </PriceDisplay>

                <FormRow className="single">
                  <FormGroup>
                    <Label>MSRP</Label>
                    <Input
                      type="number"
                      name="msrp"
                      value={formData.msrp}
                      onChange={handleInputChange}
                      placeholder="35000"
                      min="0"
                    />
                    <HelpText>Manufacturer's Suggested Retail Price</HelpText>
                  </FormGroup>
                </FormRow>

                <FormRow className="single">
                  <FormGroup>
                    <Label>
                      Selling Price <span className="required">*</span>
                    </Label>
                    <Input
                      type="number"
                      name="price"
                      value={formData.price}
                      onChange={handleInputChange}
                      placeholder="32000"
                      min="0"
                      hasError={!!errors.price}
                    />
                    {errors.price && (
                      <ErrorMessage>
                        <FaExclamationTriangle />
                        {errors.price}
                      </ErrorMessage>
                    )}
                  </FormGroup>
                </FormRow>

                <FormRow className="single">
                  <FormGroup>
                    <Label>Cost Price</Label>
                    <Input
                      type="number"
                      name="costPrice"
                      value={formData.costPrice}
                      onChange={handleInputChange}
                      placeholder="28000"
                      min="0"
                    />
                    <HelpText>Your acquisition cost (internal use)</HelpText>
                  </FormGroup>
                </FormRow>

                {formData.price && formData.costPrice && (
                  <PricingSection>
                    <div
                      style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        marginBottom: theme.spacing.sm,
                        fontWeight: 600,
                      }}
                    >
                      <span>Profit Margin:</span>
                      <span style={{ color: theme.colors.success }}>
                        {formatPrice(formData.price - formData.costPrice)}
                      </span>
                    </div>
                    <div
                      style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        fontSize: '0.9rem',
                        color: theme.colors.gray600,
                      }}
                    >
                      <span>Margin %:</span>
                      <span>
                        {(
                          ((formData.price - formData.costPrice) /
                            formData.price) *
                          100
                        ).toFixed(1)}
                        %
                      </span>
                    </div>
                  </PricingSection>
                )}
              </FormSection>

              {/* Status Section */}
              <StatusSection>
                <SectionTitle>Status</SectionTitle>

                <StatusIndicator status={formData.status}>
                  {formData.status === 'draft' && <FaEdit />}
                  {formData.status === 'active' && <FaCheck />}
                  {formData.status === 'pending' && <FaClock />}

                  {formData.status === 'draft' && 'Draft - Not Published'}
                  {formData.status === 'active' &&
                    'Active - Visible to Customers'}
                  {formData.status === 'pending' && 'Pending Review'}
                </StatusIndicator>

                <HelpText>
                  {formData.status === 'draft' &&
                    'This vehicle is saved as a draft and not visible to customers yet.'}
                  {formData.status === 'active' &&
                    'This vehicle is published and visible to customers on your website.'}
                  {formData.status === 'pending' &&
                    'This vehicle is pending review before going live.'}
                </HelpText>
              </StatusSection>

              {/* Image Upload Section */}
              <FormSection>
                <SectionTitle>
                  <FaImage />
                  Vehicle Images
                </SectionTitle>

                <ImageUploadSection
                  onClick={() => document.getElementById('imageInput').click()}
                >
                  <ImageUploadIcon>
                    <FaUpload />
                  </ImageUploadIcon>
                  <ImageUploadText>Upload Vehicle Images</ImageUploadText>
                  <ImageUploadSubtext>
                    Drag & drop images here or click to browse
                    <br />
                    Supports: JPG, PNG, WebP (Max 10MB each)
                  </ImageUploadSubtext>
                </ImageUploadSection>

                <FileInput
                  id="imageInput"
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={handleImageUpload}
                />

                {images.length > 0 && (
                  <ImagePreviewGrid>
                    {images.map(image => (
                      <ImagePreview key={image.id}>
                        <PreviewImage src={image.url} alt={image.name} />
                        <RemoveImageButton
                          onClick={e => {
                            e.stopPropagation();
                            removeImage(image.id);
                          }}
                        >
                          <FaTimes />
                        </RemoveImageButton>
                      </ImagePreview>
                    ))}
                  </ImagePreviewGrid>
                )}

                <HelpText>
                  Upload high-quality images from multiple angles. The first
                  image will be used as the main listing photo.
                </HelpText>
              </FormSection>
            </div>
          </FormGrid>
        </FormContainer>

        <ActionButtons>
          {submitStatus === 'success' && (
            <div
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: theme.spacing.sm,
                color: theme.colors.success,
                fontWeight: 600,
              }}
            >
              <FaCheck />
              Vehicle saved successfully!
            </div>
          )}

          {submitStatus === 'error' && (
            <div
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: theme.spacing.sm,
                color: theme.colors.error,
                fontWeight: 600,
              }}
            >
              <FaExclamationTriangle />
              Please fix the errors above
            </div>
          )}

          <Button
            type="button"
            variant="secondary"
            onClick={handleSaveDraft}
            disabled={isSubmitting}
          >
            <FaSave />
            Save Draft
          </Button>

          <Button
            type="submit"
            variant="primary"
            disabled={isSubmitting}
            onClick={handlePublish}
          >
            {isSubmitting ? (
              <>
                <FaClock />
                Publishing...
              </>
            ) : (
              <>
                <FaCheck />
                Publish Vehicle
              </>
            )}
          </Button>
        </ActionButtons>
      </form>
    </Container>
  );
};

