const BulkImportTab = ({ dealer }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isDragOver, setIsDragOver] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [results, setResults] = useState(null);
  const fileInputRef = useRef(null);

  const handleFileSelect = file => {
    if (file && file.type === 'text/csv') {
      setSelectedFile(file);
      setResults(null);
    } else {
      alert('Please select a valid CSV file.');
    }
  };

  const handleDragOver = e => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = e => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = e => {
    e.preventDefault();
    setIsDragOver(false);
    const file = e.dataTransfer.files[0];
    handleFileSelect(file);
  };

  const handleFileChange = e => {
    const file = e.target.files[0];
    handleFileSelect(file);
  };

  const removeFile = () => {
    setSelectedFile(null);
    setResults(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const processFile = async () => {
    if (!selectedFile) return;

    setIsProcessing(true);

    // Simulate file processing
    await new Promise(resolve => setTimeout(resolve, 3000));

    // Mock results
    const mockResults = {
      totalRows: 150,
      successfulImports: 142,
      failedImports: 8,
      errors: [
        {
          row: 5,
          message: 'Missing required field: VIN',
        },
        {
          row: 12,
          message: "Invalid year format: 'Twenty Twenty Three'",
        },
        {
          row: 23,
          message: 'Price must be a number',
        },
        {
          row: 34,
          message: 'Make field cannot be empty',
        },
        {
          row: 67,
          message: 'Invalid mileage value: -500',
        },
        {
          row: 89,
          message: 'Model field cannot be empty',
        },
        {
          row: 101,
          message: "Invalid condition value: 'terrible'",
        },
        {
          row: 128,
          message: 'Missing required field: Price',
        },
      ],
    };

    setResults(mockResults);
    setIsProcessing(false);
  };

  const downloadTemplate = () => {
    const csvContent = `VIN,Year,Make,Model,Trim,Body Style,Transmission,Engine,Fuel Type,Drivetrain,Exterior Color,Interior Color,Mileage,Condition,Price,Stock,Description,Features
1HGBH41JXMN109186,2023,Honda,Civic,Sport,Sedan,Manual,1.5L Turbo,Gasoline,FWD,Crystal Black Pearl,Black,15000,New,28500,5,"Sporty and fuel-efficient compact car","Turbo Engine, Sport Suspension, Manual Transmission"
5NPE24AF4FH123456,2022,Hyundai,Elantra,SEL,Sedan,CVT,2.0L,Gasoline,FWD,Phantom Black,Gray,25000,Used,22000,3,"Reliable sedan with great fuel economy","Heated Seats, Backup Camera, Apple CarPlay"
KMHL14JA8MA123456,2021,Hyundai,Elantra,Limited,Sedan,CVT,2.0L,Gasoline,FWD,White,Black,35000,Used,20500,2,"Fully loaded Elantra with premium features","Leather Seats, Sunroof, Premium Audio"`;

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'vehicle_import_template.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const formatFileSize = bytes => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const requiredFields = [
    'VIN',
    'Year',
    'Make',
    'Model',
    'Trim',
    'Body Style',
    'Transmission',
    'Engine',
    'Fuel Type',
    'Drivetrain',
    'Exterior Color',
    'Interior Color',
    'Mileage',
    'Condition',
    'Price',
    'Stock',
    'Description',
    'Features',
  ];

  return (
    <Container>
      <Header>
        <Title>
          <FaFileImport />
          Bulk Vehicle Import
        </Title>
        <Description>
          Import multiple vehicles at once using a CSV file. Download the
          template below, fill in your vehicle data, and upload it to quickly
          add vehicles to your inventory.
        </Description>
      </Header>

      <Content>
        <RequiredFields>
          <RequiredFieldsTitle>
            <FaInfoCircle />
            Required CSV Fields
          </RequiredFieldsTitle>
          <FieldsList>
            {requiredFields.map((field, index) => (
              <FieldItem key={index}>{field}</FieldItem>
            ))}
          </FieldsList>
        </RequiredFields>

        <StepsContainer>
          <StepCard>
            <StepNumber>1</StepNumber>
            <StepTitle>Download Template</StepTitle>
            <StepDescription>
              Download the CSV template with all required fields and sample data
              to get started.
            </StepDescription>
            <ActionButton onClick={downloadTemplate}>
              <FaDownload />
              Download Template
            </ActionButton>
          </StepCard>

          <StepCard>
            <StepNumber>2</StepNumber>
            <StepTitle>Fill Vehicle Data</StepTitle>
            <StepDescription>
              Open the template in Excel or any spreadsheet application and fill
              in your vehicle information.
            </StepDescription>
            <ActionButton>
              <FaTable />
              Open in Excel
            </ActionButton>
          </StepCard>

          <StepCard>
            <StepNumber>3</StepNumber>
            <StepTitle>Upload & Import</StepTitle>
            <StepDescription>
              Upload your completed CSV file and we'll process it to add
              vehicles to your inventory.
            </StepDescription>
            <ActionButton primary disabled={!selectedFile || isProcessing}>
              <FaUpload />
              {selectedFile ? 'Ready to Upload' : 'Select File First'}
            </ActionButton>
          </StepCard>
        </StepsContainer>

        <UploadSection>
          <UploadArea
            isDragOver={isDragOver}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            onClick={() => fileInputRef.current?.click()}
          >
            <FileInput
              ref={fileInputRef}
              type="file"
              accept=".csv"
              onChange={handleFileChange}
            />
            <UploadIcon>
              <FaCloud />
            </UploadIcon>
            <UploadText>
              {isDragOver
                ? 'Drop your CSV file here'
                : 'Click to select or drag & drop your CSV file'}
            </UploadText>
            <UploadSubtext>
              Maximum file size: 10MB  Supported format: CSV
            </UploadSubtext>
          </UploadArea>

          {selectedFile && (
            <FileInfo>
              <FileDetails>
                <FileIcon>
                  <FaFileCsv />
                </FileIcon>
                <div>
                  <FileName>{selectedFile.name}</FileName>
                  <FileSize>{formatFileSize(selectedFile.size)}</FileSize>
                </div>
              </FileDetails>
              <RemoveButton onClick={removeFile}>
                <FaTimes />
              </RemoveButton>
            </FileInfo>
          )}

          {selectedFile && (
            <ProcessButton onClick={processFile} disabled={isProcessing}>
              {isProcessing ? (
                <>
                  <FaSpinner className="fa-spin" />
                  Processing...
                </>
              ) : (
                <>
                  <FaUpload />
                  Process Import
                </>
              )}
            </ProcessButton>
          )}
        </UploadSection>

        {results && (
          <ResultsSection>
            <ResultsHeader>
              <ResultsTitle>
                <FaCheck />
                Import Results
              </ResultsTitle>
            </ResultsHeader>

            <StatsGrid>
              <StatCard>
                <StatValue>{results.totalRows}</StatValue>
                <StatLabel>Total Rows</StatLabel>
              </StatCard>
              <StatCard>
                <StatValue color={theme.colors.success}>
                  {results.successfulImports}
                </StatValue>
                <StatLabel>Successful</StatLabel>
              </StatCard>
              <StatCard>
                <StatValue color={theme.colors.error}>
                  {results.failedImports}
                </StatValue>
                <StatLabel>Failed</StatLabel>
              </StatCard>
              <StatCard>
                <StatValue color={theme.colors.primary}>
                  {Math.round(
                    (results.successfulImports / results.totalRows) * 100
                  )}
                  %
                </StatValue>
                <StatLabel>Success Rate</StatLabel>
              </StatCard>
            </StatsGrid>

            {results.errors.length > 0 && (
              <div>
                <h4
                  style={{
                    fontSize: '1rem',
                    fontWeight: 600,
                    color: theme.colors.gray900,
                    marginBottom: theme.spacing.md,
                    display: 'flex',
                    alignItems: 'center',
                    gap: theme.spacing.sm,
                  }}
                >
                  <FaExclamationTriangle />
                  Import Errors ({results.errors.length})
                </h4>
                <ErrorsList>
                  {results.errors.map((error, index) => (
                    <ErrorItem key={index}>
                      <ErrorIcon>
                        <FaExclamationTriangle />
                      </ErrorIcon>
                      <ErrorDetails>
                        <ErrorRow>Row {error.row}</ErrorRow>
                        <ErrorMessage>{error.message}</ErrorMessage>
                      </ErrorDetails>
                    </ErrorItem>
                  ))}
                </ErrorsList>
              </div>
            )}

            <div
              style={{
                marginTop: theme.spacing.xl,
                display: 'flex',
                gap: theme.spacing.md,
              }}
            >
              <ActionButton primary>
                <FaEye />
                View Imported Vehicles
              </ActionButton>
              <ActionButton>
                <FaDownload />
                Download Error Report
              </ActionButton>
            </div>
          </ResultsSection>
        )}
      </Content>
    </Container>
  );
};

export default BulkImportTab;

