        location: 'Chicago, IL',
        joinDate: '2022-08-10',
        totalPurchases: 3,
        totalSpent: 125000,
        lastPurchase: '2024-02-05',
        rating: 4.9,
        status: 'VIP',
        notes: 'Fleet buyer for business, prefers trucks and SUVs',
        vehicles: ['2022 Ford F-150', '2023 Chevy Tahoe', '2024 GMC Sierra'],
      },
      {
        id: 4,
        name: 'Emily Davis',
        email: 'emily.davis@email.com',
        phone: '(555) 456-7890',
        location: 'Miami, FL',
        joinDate: '2023-06-12',
        totalPurchases: 1,
        totalSpent: 32000,
        lastPurchase: '2023-06-12',
        rating: 4.2,
        status: 'New',
        notes: 'Young professional, budget-conscious',
        vehicles: ['2021 Honda Civic'],
      },
      {
        id: 5,
        name: 'Robert Wilson',
        email: 'r.wilson@email.com',
        phone: '(555) 567-8901',
        location: 'Dallas, TX',
        joinDate: '2022-12-03',
        totalPurchases: 2,
        totalSpent: 95000,
        lastPurchase: '2023-12-15',
        rating: 4.7,
        status: 'VIP',
        notes: 'Enthusiast collector, prefers classic and sports cars',
        vehicles: ['2023 Porsche 911', '2022 Mustang GT'],
      },
      {
        id: 6,
        name: 'Lisa Anderson',
        email: 'lisa.anderson@email.com',
        phone: '(555) 678-9012',
        location: 'Seattle, WA',
        joinDate: '2023-09-18',
        totalPurchases: 1,
        totalSpent: 58000,
        lastPurchase: '2023-09-18',
        rating: 4.6,
        status: 'Regular',
        notes: 'Family-oriented, safety is top priority',
        vehicles: ['2023 Volvo XC90'],
      },
    ];

    setCustomers(mockCustomers);
    setFilteredCustomers(mockCustomers);
  }, []);

  useEffect(() => {
    let filtered = [...customers];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(
        customer =>
          customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          customer.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
          customer.phone.includes(searchTerm)
      );
    }

    // Status filter
    if (filterBy) {
      filtered = filtered.filter(customer => customer.status === filterBy);
    }

    setFilteredCustomers(filtered);
    setCurrentPage(1);
  }, [customers, searchTerm, filterBy]);

  const formatCurrency = amount => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = dateString => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
    });
  };

  const getInitials = name => {
    return name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase();
  };

  const stats = {
    total: customers.length,
    vip: customers.filter(c => c.status === 'VIP').length,
    regular: customers.filter(c => c.status === 'Regular').length,
    new: customers.filter(c => c.status === 'New').length,
    totalRevenue: customers.reduce((sum, c) => sum + c.totalSpent, 0),
    avgSpending:
      customers.length > 0
        ? customers.reduce((sum, c) => sum + c.totalSpent, 0) / customers.length
        : 0,
  };

  // Pagination
  const indexOfLastCustomer = currentPage * customersPerPage;
  const indexOfFirstCustomer = indexOfLastCustomer - customersPerPage;
  const currentCustomers = filteredCustomers.slice(
    indexOfFirstCustomer,
    indexOfLastCustomer
  );
  const totalPages = Math.ceil(filteredCustomers.length / customersPerPage);

  const handleExportCustomers = () => {
    // Mock export functionality
    const csvContent = [
      [
        'Name',
        'Email',
        'Phone',
        'Total Purchases',
        'Total Spent',
        'Status',
        'Join Date',
      ],
      ...customers.map(customer => [
        customer.name,
        customer.email,
        customer.phone,
        customer.totalPurchases,
        customer.totalSpent,
        customer.status,
        customer.joinDate,
      ]),
    ]
      .map(row => row.join(','))
      .join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'customers.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <Container>
      <Header>
        <HeaderTop>
          <Title>
            <FaUsers />
            Customer Management ({filteredCustomers.length})
          </Title>
          <HeaderActions>
            <ExportButton onClick={handleExportCustomers}>
              <FaDownload />
              Export
            </ExportButton>
            <AddButton>
              <FaUserPlus />
              Add Customer
            </AddButton>
          </HeaderActions>
        </HeaderTop>

        <FiltersRow>
          <SearchContainer>
            <SearchIcon>
              <FaSearch />
            </SearchIcon>
            <SearchInput
              type="text"
              placeholder="Search customers by name, email, or phone..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </SearchContainer>

          <FilterSelect
            value={filterBy}
            onChange={e => setFilterBy(e.target.value)}
          >
            <option value="">All Customers</option>
            <option value="VIP">VIP Customers</option>
            <option value="Regular">Regular Customers</option>
            <option value="New">New Customers</option>
          </FilterSelect>

          <FilterSelect defaultValue="recent">
            <option value="recent">Recent First</option>
            <option value="spending">High Spending</option>
            <option value="purchases">Most Purchases</option>
            <option value="rating">Highest Rated</option>
          </FilterSelect>
        </FiltersRow>
      </Header>

      <StatsRow>
        <StatCard>
          <StatValue>{stats.total}</StatValue>
          <StatLabel>Total Customers</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue>{stats.vip}</StatValue>
          <StatLabel>VIP Customers</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue>{stats.new}</StatValue>
          <StatLabel>New This Month</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue>{formatCurrency(stats.totalRevenue)}</StatValue>
          <StatLabel>Total Revenue</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue>{formatCurrency(stats.avgSpending)}</StatValue>
          <StatLabel>Avg. Spending</StatLabel>
        </StatCard>
      </StatsRow>

      <CustomersGrid>
        {currentCustomers.length > 0 ? (
          currentCustomers.map(customer => (
            <CustomerCard key={customer.id}>
              <CustomerHeader>
                <div style={{ display: 'flex', alignItems: 'flex-start' }}>
                  <CustomerAvatar>{getInitials(customer.name)}</CustomerAvatar>
                  <CustomerInfo>
                    <CustomerName>{customer.name}</CustomerName>
                    <CustomerEmail>
                      <FaEnvelope
                        style={{ marginRight: '6px', fontSize: '0.8rem' }}
                      />
                      {customer.email}
                    </CustomerEmail>
                    <CustomerPhone>
                      <FaPhone
                        style={{ marginRight: '6px', fontSize: '0.8rem' }}
                      />
                      {customer.phone}
                    </CustomerPhone>
                  </CustomerInfo>
                </div>
                <CustomerRating>
                  <FaStar />
                  {customer.rating}
                </CustomerRating>
              </CustomerHeader>

              <CustomerStats>
                <CustomerStatItem>
                  <CustomerStatValue>
                    {customer.totalPurchases}
                  </CustomerStatValue>
                  <CustomerStatLabel>Purchases</CustomerStatLabel>
                </CustomerStatItem>
                <CustomerStatItem>
                  <CustomerStatValue>
                    {formatCurrency(customer.totalSpent)}
                  </CustomerStatValue>
                  <CustomerStatLabel>Total Spent</CustomerStatLabel>
                </CustomerStatItem>
                <CustomerStatItem>
                  <CustomerStatValue>{customer.status}</CustomerStatValue>
                  <CustomerStatLabel>Status</CustomerStatLabel>
                </CustomerStatItem>
              </CustomerStats>

              <CustomerMeta>
                <div>
                  <FaMapMarkerAlt style={{ marginRight: '4px' }} />
                  {customer.location}
                </div>
                <div>
                  <FaCalendar style={{ marginRight: '4px' }} />
                  Joined {formatDate(customer.joinDate)}
                </div>
              </CustomerMeta>

              <ActionButtons>
                <ActionButton className="primary">
                  <FaEye />
                  View Profile
                </ActionButton>
                <ActionButton>
                  <FaPhone />
                  Call
                </ActionButton>
                <ActionButton>
                  <FaEnvelope />
                  Email
                </ActionButton>
                <ActionButton>
                  <FaEdit />
                  Edit
                </ActionButton>
              </ActionButtons>
            </CustomerCard>
          ))
        ) : (
          <EmptyState>
            <FaUsers className="icon" />
            <h3>No customers found</h3>
            <p>
              Try adjusting your search criteria or add new customers to your
              database.
            </p>
          </EmptyState>
        )}
      </CustomersGrid>

      {totalPages > 1 && (
        <Pagination>
          <PaginationButton
            onClick={() => setCurrentPage(currentPage - 1)}
            disabled={currentPage === 1}
          >
            <FaChevronLeft />
            Previous
          </PaginationButton>

          <PaginationInfo>
            Page {currentPage} of {totalPages} ({filteredCustomers.length}{' '}
            customers)
          </PaginationInfo>

          <PaginationButton
            onClick={() => setCurrentPage(currentPage + 1)}
            disabled={currentPage === totalPages}
          >
            Next
            <FaChevronRight />
          </PaginationButton>
        </Pagination>
      )}
    </Container>
  );
};

export default CustomersTab;

