const EnquiriesTab = () => {
  const [enquiries, setEnquiries] = useState([]);

  useEffect(() => {
    // Load enquiries from localStorage (demo data)
    const storedEnquiries = JSON.parse(
      localStorage.getItem('vehicleEnquiries') || '[]'
    );
    setEnquiries(storedEnquiries);
  }, []);

  const stats = {
    total: enquiries.length,
    new: enquiries.filter(e => e.status === 'pending').length,
    responded: enquiries.filter(e => e.status === 'responded').length,
    thisWeek: enquiries.filter(e => {
      const enquiryDate = new Date(e.timestamp);
      const weekAgo = new Date();
      weekAgo.setDate(weekAgo.getDate() - 7);
      return enquiryDate > weekAgo;
    }).length,
  };

  const formatDate = timestamp => {
    return new Date(timestamp).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const formatPrice = price => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(price);
  };

  return (
    <Container>
      <Header>
        <Title>
          <FaEnvelope />
          Customer Enquiries ({enquiries.length})
        </Title>

        <StatsRow>
          <StatCard>
            <StatValue>{stats.total}</StatValue>
            <StatLabel>Total</StatLabel>
          </StatCard>
          <StatCard>
            <StatValue>{stats.new}</StatValue>
            <StatLabel>New</StatLabel>
          </StatCard>
          <StatCard>
            <StatValue>{stats.responded}</StatValue>
            <StatLabel>Responded</StatLabel>
          </StatCard>
          <StatCard>
            <StatValue>{stats.thisWeek}</StatValue>
            <StatLabel>This Week</StatLabel>
          </StatCard>
        </StatsRow>
      </Header>

      <EnquiriesList>
        {enquiries.length > 0 ? (
          enquiries.map(enquiry => (
            <EnquiryItem key={enquiry.id}>
              <EnquiryHeader>
                <CustomerInfo>
                  <CustomerName>
                    <FaUser />
                    {enquiry.name}
                  </CustomerName>
                  <CustomerContact>{enquiry.email}</CustomerContact>
                  <CustomerContact>{enquiry.phone}</CustomerContact>
                </CustomerInfo>
                <EnquiryMeta>
                  <div>
                    <FaClock /> {formatDate(enquiry.timestamp)}
                  </div>
                  <div>Interest: {enquiry.interestType}</div>
                </EnquiryMeta>
              </EnquiryHeader>

              {enquiry.vehicleName && (
                <VehicleInfo>
                  <VehicleName>
                    <FaCar /> {enquiry.vehicleYear} {enquiry.vehicleMake}{' '}
                    {enquiry.vehicleModel}
                  </VehicleName>
                  <VehiclePrice>
                    {formatPrice(enquiry.vehiclePrice)}
                  </VehiclePrice>
                </VehicleInfo>
              )}

              {enquiry.message && (
                <EnquiryMessage>"{enquiry.message}"</EnquiryMessage>
              )}

              <ActionButtons>
                <ActionButton className="primary">
                  <FaReply />
                  Reply
                </ActionButton>
                <ActionButton className="secondary">
                  <FaPhone />
                  Call
                </ActionButton>
                <ActionButton className="secondary">
                  <FaEye />
                  View Details
                </ActionButton>
              </ActionButtons>
            </EnquiryItem>
          ))
        ) : (
          <EmptyState>
            <FaEnvelope className="icon" />
            <h3>No enquiries yet</h3>
            <p>
              Customer enquiries will appear here when customers are interested
              in your vehicles.
            </p>
          </EmptyState>
        )}
      </EnquiriesList>
    </Container>
  );
};

