const EnquiryModal = ({ isOpen, onClose, vehicle, userInfo = {} }) => {
  const [formData, setFormData] = useState({
    name: userInfo.name || '',
    phone: userInfo.phone || '',
    email: userInfo.email || '',
    message: '',
    interestType: 'purchase',
    agreeToTerms: false,
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [errors, setErrors] = useState({});

  const handleInputChange = e => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Phone number is required';
    } else if (!/^\+?[\d\s\-()]{10,}$/.test(formData.phone.trim())) {
      newErrors.phone = 'Please enter a valid phone number';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (!formData.agreeToTerms) {
      newErrors.agreeToTerms = 'You must agree to the terms';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async e => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Here you would typically send the enquiry to your backend
      const enquiryData = {
        ...formData,
        vehicleId: vehicle.id,
        vehicleName: vehicle.name,
        vehiclePrice: vehicle.price,
        vehicleYear: vehicle.year,
        vehicleMake: vehicle.make,
        vehicleModel: vehicle.model,
        timestamp: new Date().toISOString(),
        status: 'pending',
      };

      // Store in localStorage for demo (in real app, this would be in your database)
      const existingEnquiries = JSON.parse(
        localStorage.getItem('vehicleEnquiries') || '[]'
      );
      const newEnquiry = {
        id: Date.now(),
        ...enquiryData,
      };
      localStorage.setItem(
        'vehicleEnquiries',
        JSON.stringify([...existingEnquiries, newEnquiry])
      );

      setIsSuccess(true);
    } catch (error) {
      console.error('Error submitting enquiry:', error);
      alert('Failed to submit enquiry. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClose = () => {
    if (isSuccess) {
      setIsSuccess(false);
      setFormData({
        name: userInfo.name || '',
        phone: userInfo.phone || '',
        email: userInfo.email || '',
        message: '',
        interestType: 'purchase',
        agreeToTerms: false,
      });
      setErrors({});
    }
    onClose();
  };

  const formatPrice = price => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(price);
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay onClick={handleClose}>
      <ModalContainer onClick={e => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>
            {isSuccess ? 'Enquiry Submitted!' : 'Vehicle Enquiry'}
          </ModalTitle>
          <CloseButton onClick={handleClose}>
            <FaTimes />
          </CloseButton>
        </ModalHeader>

        <ModalBody>
          {isSuccess ? (
            <SuccessMessage>
              <SuccessIcon>
                <FaCheck />
              </SuccessIcon>
              <SuccessTitle>Enquiry Sent Successfully!</SuccessTitle>
              <SuccessText>
                Thank you for your interest in the {vehicle.year} {vehicle.make}{' '}
                {vehicle.model}. The dealer will contact you soon via phone or
                email with pricing details and availability.
              </SuccessText>
              <SubmitButton onClick={handleClose}>
                Continue Browsing
              </SubmitButton>
            </SuccessMessage>
          ) : (
            <>
              <VehicleInfo>
                <VehicleImage src={vehicle.image} alt={vehicle.name} />
                <VehicleDetails>
                  <VehicleName>
                    {vehicle.year} {vehicle.make} {vehicle.model}
                  </VehicleName>
                  <VehiclePrice>{formatPrice(vehicle.price)}</VehiclePrice>
                  <VehicleYear>
                    {vehicle.trim} {' '}
                    {vehicle.condition === 'new' ? 'New' : 'Used'}
                  </VehicleYear>
                </VehicleDetails>
              </VehicleInfo>

              <Form onSubmit={handleSubmit}>
                <FormGroup>
                  <Label>
                    <FaUser />
                    Full Name *
                  </Label>
                  <InputWrapper>
                    <InputIcon>
                      <FaUser />
                    </InputIcon>
                    <Input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Enter your full name"
                      style={{
                        borderColor: errors.name
                          ? theme.colors.error
                          : undefined,
                      }}
                    />
                  </InputWrapper>
                  {errors.name && (
                    <span
                      style={{ color: theme.colors.error, fontSize: '0.9rem' }}
                    >
                      {errors.name}
                    </span>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label>
                    <FaPhone />
                    Phone Number *
                  </Label>
                  <InputWrapper>
                    <InputIcon>
                      <FaPhone />
                    </InputIcon>
                    <Input
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      placeholder="Enter your phone number"
                      style={{
                        borderColor: errors.phone
                          ? theme.colors.error
                          : undefined,
                      }}
                    />
                  </InputWrapper>
                  {errors.phone && (
                    <span
                      style={{ color: theme.colors.error, fontSize: '0.9rem' }}
                    >
                      {errors.phone}
                    </span>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label>
                    <FaEnvelope />
                    Email Address *
                  </Label>
                  <InputWrapper>
                    <InputIcon>
                      <FaEnvelope />
                    </InputIcon>
                    <Input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      placeholder="Enter your email address"
                      style={{
                        borderColor: errors.email
                          ? theme.colors.error
                          : undefined,
                      }}
                    />
                  </InputWrapper>
                  {errors.email && (
                    <span
                      style={{ color: theme.colors.error, fontSize: '0.9rem' }}
                    >
                      {errors.email}
                    </span>
                  )}
                </FormGroup>

                <InterestGroup>
                  <Label>
                    <FaCar />
                    I'm interested in *
                  </Label>
                  <InterestOptions>
                    <InterestOption>
                      <input
                        type="radio"
                        name="interestType"
                        value="purchase"
                        checked={formData.interestType === 'purchase'}
                        onChange={handleInputChange}
                      />
                      Purchasing this vehicle
                    </InterestOption>
                    <InterestOption>
                      <input
                        type="radio"
                        name="interestType"
                        value="financing"
                        checked={formData.interestType === 'financing'}
                        onChange={handleInputChange}
                      />
                      Financing options
                    </InterestOption>
                    <InterestOption>
                      <input
                        type="radio"
                        name="interestType"
                        value="trade"
                        checked={formData.interestType === 'trade'}
                        onChange={handleInputChange}
                      />
                      Trade-in value
                    </InterestOption>
                    <InterestOption>
                      <input
                        type="radio"
                        name="interestType"
                        value="test-drive"
                        checked={formData.interestType === 'test-drive'}
                        onChange={handleInputChange}
                      />
                      Scheduling test drive
                    </InterestOption>
                  </InterestOptions>
                </InterestGroup>

                <FormGroup>
                  <Label>
                    <FaComment />
                    Message (Optional)
                  </Label>
                  <InputWrapper>
                    <InputIcon>
                      <FaComment />
                    </InputIcon>
                    <TextArea
                      name="message"
                      value={formData.message}
                      onChange={handleInputChange}
                      placeholder="Tell us about your preferences, trade-in vehicle, financing needs, or any specific questions..."
                    />
                  </InputWrapper>
                </FormGroup>

                <CheckboxWrapper>
                  <Checkbox
                    type="checkbox"
                    id="agreeToTerms"
                    name="agreeToTerms"
                    checked={formData.agreeToTerms}
                    onChange={handleInputChange}
                  />
                  <CheckboxLabel htmlFor="agreeToTerms">
                    I agree to share my contact information with the dealer and
                    receive communications about this vehicle. By submitting
                    this enquiry, I consent to the processing of my personal
                    data.
                  </CheckboxLabel>
                </CheckboxWrapper>
                {errors.agreeToTerms && (
                  <span
                    style={{ color: theme.colors.error, fontSize: '0.9rem' }}
                  >
                    {errors.agreeToTerms}
                  </span>
                )}

                <ButtonGroup>
                  <CancelButton type="button" onClick={handleClose}>
                    Cancel
                  </CancelButton>
                  <SubmitButton type="submit" disabled={isSubmitting}>
                    {isSubmitting ? 'Sending...' : 'Send Enquiry'}
                  </SubmitButton>
                </ButtonGroup>
              </Form>
            </>
          )}
        </ModalBody>
      </ModalContainer>
    </ModalOverlay>
  );
};

export default EnquiryModal;

