const FinancingTab = ({ dealer }) => {
  const [applications, setApplications] = useState([]);
  const [filteredApplications, setFilteredApplications] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [lenders] = useState([
    {
      id: 1,
      name: 'First National Bank',
      minRate: 3.49,
      maxRate: 7.99,
      maxTerm: 84,
      minCreditScore: 650,
      status: 'active',
    },
    {
      id: 2,
      name: 'Capital Auto Finance',
      minRate: 4.25,
      maxRate: 12.99,
      maxTerm: 72,
      minCreditScore: 580,
      status: 'active',
    },
    {
      id: 3,
      name: 'Credit Union Motors',
      minRate: 2.99,
      maxRate: 6.49,
      maxTerm: 60,
      minCreditScore: 700,
      status: 'active',
    },
    {
      id: 4,
      name: 'Quick Approval Loans',
      minRate: 8.99,
      maxRate: 18.99,
      maxTerm: 60,
      minCreditScore: 500,
      status: 'active',
    },
  ]);

  useEffect(() => {
    // Mock financing applications data
    const mockApplications = [
      {
        id: 'FA-001',
        applicationId: '24-FA-001',
        customerId: 1,
        customerName: 'John Smith',
        customerEmail: 'john.smith@email.com',
        vehicleId: 1,
        vehicleName: '2024 BMW X5 M50i',
        vehiclePrice: 75000,
        downPayment: 15000,
        loanAmount: 60000,
        requestedTerm: 60,
        creditScore: 750,
        monthlyIncome: 8500,
        employmentYears: 5,
        applicationDate: '2024-02-15T10:30:00Z',
        status: 'under_review',
        lender: 'First National Bank',
        interestRate: 4.25,
        monthlyPayment: 1120,
        notes: 'Excellent credit, stable employment',
      },
      {
        id: 'FA-002',
        applicationId: '24-FA-002',
        customerId: 2,
        customerName: 'Sarah Johnson',
        customerEmail: 'sarah.j@email.com',
        vehicleId: 2,
        vehicleName: '2023 Tesla Model S Plaid',
        vehiclePrice: 120000,
        downPayment: 25000,
        loanAmount: 95000,
        requestedTerm: 72,
        creditScore: 680,
        monthlyIncome: 12000,
        employmentYears: 3,
        applicationDate: '2024-02-14T14:20:00Z',
        status: 'approved',
        lender: 'Capital Auto Finance',
        interestRate: 5.75,
        monthlyPayment: 1580,
        notes: 'Approved with standard terms',
      },
      {
        id: 'FA-003',
        applicationId: '24-FA-003',
        customerId: 3,
        customerName: 'Michael Brown',
        customerEmail: 'm.brown@email.com',
        vehicleId: 3,
        vehicleName: '2024 Ford F-150 Lightning',
        vehiclePrice: 65000,
        downPayment: 10000,
        loanAmount: 55000,
        requestedTerm: 60,
        creditScore: 720,
        monthlyIncome: 9500,
        employmentYears: 8,
        applicationDate: '2024-02-13T09:15:00Z',
        status: 'conditional',
        lender: 'Credit Union Motors',
        interestRate: 3.99,
        monthlyPayment: 1015,
        notes: 'Conditional approval - needs updated pay stubs',
      },
      {
        id: 'FA-004',
        applicationId: '24-FA-004',
        customerId: 4,
        customerName: 'Emily Davis',
        customerEmail: 'emily.davis@email.com',
        vehicleId: 4,
        vehicleName: '2023 Honda Civic Type R',
        vehiclePrice: 42000,
        downPayment: 5000,
        loanAmount: 37000,
        requestedTerm: 60,
        creditScore: 620,
        monthlyIncome: 4800,
        employmentYears: 2,
        applicationDate: '2024-02-12T16:45:00Z',
        status: 'pending',
        lender: null,
        interestRate: null,
        monthlyPayment: null,
        notes: 'Application submitted, awaiting credit check',
      },
      {
        id: 'FA-005',
        applicationId: '24-FA-005',
        customerId: 5,
        customerName: 'Robert Wilson',
        customerEmail: 'r.wilson@email.com',
        vehicleId: 5,
        vehicleName: '2024 Porsche 911 Turbo S',
        vehiclePrice: 230000,
        downPayment: 80000,
        loanAmount: 150000,
        requestedTerm: 84,
        creditScore: 580,
        monthlyIncome: 15000,
        employmentYears: 1,
        applicationDate: '2024-02-11T11:00:00Z',
        status: 'rejected',
        lender: 'First National Bank',
        interestRate: null,
        monthlyPayment: null,
        notes: 'Insufficient credit history, debt-to-income ratio too high',
      },
    ];

    setApplications(mockApplications);
    setFilteredApplications(mockApplications);
  }, []);

  useEffect(() => {
    let filtered = [...applications];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(
        app =>
          app.applicationId.toLowerCase().includes(searchTerm.toLowerCase()) ||
          app.customerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
          app.vehicleName.toLowerCase().includes(searchTerm.toLowerCase()) ||
          (app.lender &&
            app.lender.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Status filter
    if (statusFilter) {
      filtered = filtered.filter(app => app.status === statusFilter);
    }

    setFilteredApplications(filtered);
  }, [applications, searchTerm, statusFilter]);

  const formatCurrency = amount => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = dateString => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
    });
  };

  const getStatusLabel = status => {
    switch (status) {
      case 'pending':
        return 'Pending';
      case 'under_review':
        return 'Under Review';
      case 'approved':
        return 'Approved';
      case 'rejected':
        return 'Rejected';
      case 'conditional':
        return 'Conditional';
      default:
        return status;
    }
  };

  const stats = {
    total: applications.length,
    pending: applications.filter(a => a.status === 'pending').length,
    approved: applications.filter(a => a.status === 'approved').length,
    rejected: applications.filter(a => a.status === 'rejected').length,
    totalLoanAmount: applications
      .filter(a => a.status === 'approved')
      .reduce((sum, a) => sum + a.loanAmount, 0),
    avgCreditScore:
      applications.length > 0
        ? Math.round(
            applications.reduce((sum, a) => sum + a.creditScore, 0) /
              applications.length
          )
        : 0,
  };

  return (
    <Container>
      <Header>
        <HeaderTop>
          <Title>
            <FaDollarSign />
            Financing & Loans ({filteredApplications.length})
