              placeholder="Search applications by ID, customer, vehicle, or lender..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </SearchContainer>

          <FilterSelect
            value={statusFilter}
            onChange={e => setStatusFilter(e.target.value)}
