const Navbar = ({
  dealerName = 'Auto Dealer',
  dealerLogo,
  dealerSlug = '',
  theme: customTheme = {},
}) => {
  const navigate = useNavigate();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const getBaseUrl = () => (dealerSlug ? `/${dealerSlug}` : '/automobiles');

  const handleSearch = e => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(
        `${getBaseUrl()}/vehicles?search=${encodeURIComponent(searchQuery)}`
      );
      setSearchQuery('');
      setIsMobileMenuOpen(false);
    }
  };

  const handleMobileMenuToggle = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const closeMobileMenu = () => {
    setIsMobileMenuOpen(false);
  };

  return (
    <>
      <NavbarContainer>
        <NavbarContent>
          <Logo to={getBaseUrl()} theme={customTheme}>
            {dealerLogo && (
              <LogoImage
                src={dealerLogo}
                alt={`${dealerName} logo`}
                theme={customTheme}
              />
            )}
            <span>{dealerName}</span>
          </Logo>

          <NavLinks>
            <NavLink to={getBaseUrl()}>
              <FaHome />
              Home
            </NavLink>
            <NavLink to={`${getBaseUrl()}/vehicles`}>
              <FaCar />
              Vehicles
            </NavLink>
            <NavLink to={`${getBaseUrl()}/vehicles?category=luxury-cars`}>
              Luxury
            </NavLink>
            <NavLink to={`${getBaseUrl()}/vehicles?category=electric-vehicles`}>
              Electric
            </NavLink>
          </NavLinks>

          <SearchContainer>
            <form onSubmit={handleSearch}>
              <SearchIcon>
                <FaSearch />
              </SearchIcon>
              <SearchInput
                type="text"
                placeholder="Search vehicles..."
                value={searchQuery}
                onChange={e => setSearchQuery(e.target.value)}
              />
            </form>
          </SearchContainer>

          <ActionButtons>
            <IconButton
              title="Dealer Dashboard"
              onClick={() => navigate(`${getBaseUrl()}/autoadmindasboard`)}
            >
              <FaTachometerAlt />
            </IconButton>
            <IconButton
              title="Wishlist"
              onClick={() => navigate(`${getBaseUrl()}/wishlist`)}
            >
              <FaHeart />
            </IconButton>
            <IconButton title="Contact">
              <FaEnvelope />
            </IconButton>
            <IconButton title="Account">
              <FaUser />
            </IconButton>
          </ActionButtons>

          <MobileMenuButton onClick={handleMobileMenuToggle}>
            <FaBars />
          </MobileMenuButton>
        </NavbarContent>
      </NavbarContainer>

      <MobileMenu isOpen={isMobileMenuOpen}>
        <MobileMenuHeader>
          <Logo to={getBaseUrl()} theme={customTheme} onClick={closeMobileMenu}>
            {dealerLogo && (
              <LogoImage
                src={dealerLogo}
                alt={`${dealerName} logo`}
                theme={customTheme}
              />
            )}
            <span>{dealerName}</span>
          </Logo>
          <IconButton onClick={closeMobileMenu}>
            <FaTimes />
          </IconButton>
        </MobileMenuHeader>

        <MobileMenuContent>
          <MobileSearchContainer>
            <form onSubmit={handleSearch}>
              <SearchIcon>
                <FaSearch />
              </SearchIcon>
              <SearchInput
                type="text"
                placeholder="Search vehicles..."
                value={searchQuery}
                onChange={e => setSearchQuery(e.target.value)}
              />
            </form>
          </MobileSearchContainer>

          <MobileNavLink to={getBaseUrl()} onClick={closeMobileMenu}>
            <FaHome />
            Home
          </MobileNavLink>
          <MobileNavLink
            to={`${getBaseUrl()}/vehicles`}
            onClick={closeMobileMenu}
          >
            <FaCar />
            All Vehicles
          </MobileNavLink>
          <MobileNavLink
            to={`${getBaseUrl()}/vehicles?category=luxury-cars`}
            onClick={closeMobileMenu}
          >
            <FaCar />
            Luxury Cars
          </MobileNavLink>
          <MobileNavLink
            to={`${getBaseUrl()}/vehicles?category=electric-vehicles`}
            onClick={closeMobileMenu}
          >
            <FaCar />
            Electric Vehicles
          </MobileNavLink>
          <MobileNavLink
            to={`${getBaseUrl()}/vehicles?category=suvs-trucks`}
            onClick={closeMobileMenu}
          >
            <FaCar />
            SUVs & Trucks
          </MobileNavLink>
          <MobileNavLink
            to={`${getBaseUrl()}/vehicles?category=classic-cars`}
            onClick={closeMobileMenu}
          >
            <FaCar />
            Classic Cars
          </MobileNavLink>
          <MobileNavLink
            to={`${getBaseUrl()}/dealer-dashboard`}
            onClick={closeMobileMenu}
          >
            <FaTachometerAlt />
            Dealer Dashboard
          </MobileNavLink>
          <MobileNavLink
            to={`${getBaseUrl()}/wishlist`}
            onClick={closeMobileMenu}
          >
            <FaHeart />
            Wishlist
          </MobileNavLink>
          <MobileNavLink to="#" onClick={closeMobileMenu}>
            <FaEnvelope />
            Contact
          </MobileNavLink>
          <MobileNavLink to="#" onClick={closeMobileMenu}>
            <FaUser />
            Account
          </MobileNavLink>
        </MobileMenuContent>
      </MobileMenu>
    </>
  );
};

