  useEffect(() => {
    let filtered = [...promotions];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(
        promotion =>
          promotion.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          promotion.description
            .toLowerCase()
            .includes(searchTerm.toLowerCase()) ||
          promotion.type.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Status filter
    if (statusFilter) {
      filtered = filtered.filter(
        promotion => promotion.status === statusFilter
      );
    }

    // Type filter
    if (typeFilter) {
      filtered = filtered.filter(promotion => promotion.type === typeFilter);
    }

    setFilteredPromotions(filtered);
  }, [promotions, searchTerm, statusFilter, typeFilter]);

  const formatCurrency = amount => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = dateString => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
    });
  };

  const getStatusLabel = status => {
    switch (status) {
      case 'draft':
        return 'Draft';
      case 'active':
        return 'Active';
      case 'scheduled':
        return 'Scheduled';
      case 'paused':
        return 'Paused';
      case 'expired':
        return 'Expired';
      default:
        return status;
    }
  };

  const getTypeLabel = type => {
    switch (type) {
      case 'percentage_discount':
        return 'Percentage Discount';
      case 'fixed_discount':
        return 'Fixed Discount';
      case 'trade_in_bonus':
        return 'Trade-In Bonus';
      case 'financing_deal':
        return 'Financing Deal';
      case 'bulk_discount':
        return 'Bulk Discount';
      case 'student_discount':
        return 'Student Discount';
      default:
        return type;
    }
  };

  const formatDiscountValue = promotion => {
    switch (promotion.discountType) {
      case 'percentage':
        return `${promotion.discountValue}%`;
      case 'fixed':
        return formatCurrency(promotion.discountValue);
      case 'trade_bonus':
        return `+${formatCurrency(promotion.discountValue)}`;
      case 'apr_rate':
        return `${promotion.discountValue}% APR`;
      default:
        return promotion.discountValue;
    }
  };

  const stats = {
    total: promotions.length,
    active: promotions.filter(p => p.status === 'active').length,
    scheduled: promotions.filter(p => p.status === 'scheduled').length,
    paused: promotions.filter(p => p.status === 'paused').length,
    totalRevenue: promotions
      .filter(p => p.status === 'active' || p.status === 'expired')
      .reduce((sum, p) => sum + p.revenue, 0),
    totalUsage: promotions.reduce((sum, p) => sum + p.usageCount, 0),
    avgConversion:
      promotions.length > 0
        ? promotions
            .filter(p => p.conversionRate > 0)
            .reduce((sum, p) => sum + p.conversionRate, 0) /
          promotions.filter(p => p.conversionRate > 0).length
        : 0,
  };

  return (
    <Container>
      <Header>
        <HeaderTop>
          <Title>
            <FaTags />
            Promotions & Deals ({filteredPromotions.length})
          </Title>
          <HeaderActions>
            <ActionButton>
              <FaDownload />
              Export Report
            </ActionButton>
            <ActionButton>
              <FaChartLine />
              Analytics
            </ActionButton>
            <ActionButton primary>
              <FaPlus />
              New Promotion
            </ActionButton>
          </HeaderActions>
        </HeaderTop>

        <FiltersRow>
          <SearchContainer>
            <SearchIcon>
              <FaSearch />
            </SearchIcon>
            <SearchInput
              type="text"
              placeholder="Search promotions by title, description, or type..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </SearchContainer>

          <FilterSelect
            value={statusFilter}
            onChange={e => setStatusFilter(e.target.value)}
          >
            <option value="">All Statuses</option>
            <option value="active">Active</option>
            <option value="scheduled">Scheduled</option>
            <option value="paused">Paused</option>
            <option value="expired">Expired</option>
            <option value="draft">Draft</option>
          </FilterSelect>

          <FilterSelect
            value={typeFilter}
            onChange={e => setTypeFilter(e.target.value)}
          >
            <option value="">All Types</option>
            <option value="percentage_discount">Percentage Discount</option>
            <option value="fixed_discount">Fixed Discount</option>
            <option value="trade_in_bonus">Trade-In Bonus</option>
            <option value="financing_deal">Financing Deal</option>
            <option value="bulk_discount">Bulk Discount</option>
            <option value="student_discount">Student Discount</option>
          </FilterSelect>

          <FilterSelect defaultValue="recent">
            <option value="recent">Recent First</option>
            <option value="ending_soon">Ending Soon</option>
            <option value="performance">Best Performance</option>
            <option value="usage">Most Used</option>
          </FilterSelect>
        </FiltersRow>
      </Header>

      <StatsGrid>
        <StatCard>
          <StatValue>{stats.total}</StatValue>
          <StatLabel>Total Promotions</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.success}>{stats.active}</StatValue>
          <StatLabel>Active</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.info}>{stats.scheduled}</StatValue>
          <StatLabel>Scheduled</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.warning}>{stats.paused}</StatValue>
          <StatLabel>Paused</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.success}>
            {formatCurrency(stats.totalRevenue)}
          </StatValue>
          <StatLabel>Total Revenue</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.primary}>{stats.totalUsage}</StatValue>
          <StatLabel>Total Usage</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.info}>
            {stats.avgConversion.toFixed(1)}%
          </StatValue>
          <StatLabel>Avg Conversion</StatLabel>
        </StatCard>
      </StatsGrid>

      <PromotionsGrid>
        {filteredPromotions.length > 0 ? (
          filteredPromotions.map(promotion => (
            <PromotionCard key={promotion.id}>
              <PromotionHeader>
                <PromotionInfo>
                  <PromotionTitle>{promotion.title}</PromotionTitle>
                  <PromotionType>{getTypeLabel(promotion.type)}</PromotionType>
                </PromotionInfo>
                <StatusBadge status={promotion.status}>
                  {getStatusLabel(promotion.status)}
                </StatusBadge>
              </PromotionHeader>

              <PromotionDescription>
                {promotion.description}
              </PromotionDescription>

              <PromotionDetails>
                <DetailItem>
                  <DetailValue color={theme.colors.success}>
                    {formatDiscountValue(promotion)}
                  </DetailValue>
                  <DetailLabel>Discount Value</DetailLabel>
                </DetailItem>
                <DetailItem>
                  <DetailValue>
                    {formatCurrency(promotion.minPurchaseAmount)}
                  </DetailValue>
                  <DetailLabel>Min Purchase</DetailLabel>
                </DetailItem>
              </PromotionDetails>

              <DateRange>
                <div>
                  <FaCalendar style={{ marginRight: '4px' }} />
                  {formatDate(promotion.startDate)}
                </div>
                <div></div>
                <div>
                  <FaCalendar style={{ marginRight: '4px' }} />
                  {formatDate(promotion.endDate)}
                </div>
              </DateRange>

              <PerformanceMetrics>
                <MetricItem>
                  <MetricValue>
                    {promotion.usageCount}/{promotion.usageLimit}
                  </MetricValue>
                  <MetricLabel>Usage</MetricLabel>
                </MetricItem>
                <MetricItem>
                  <MetricValue>{formatCurrency(promotion.revenue)}</MetricValue>
                  <MetricLabel>Revenue</MetricLabel>
                </MetricItem>
                <MetricItem>
                  <MetricValue>{promotion.conversionRate}%</MetricValue>
                  <MetricLabel>Conversion</MetricLabel>
                </MetricItem>
              </PerformanceMetrics>

              <div
                style={{
                  marginBottom: theme.spacing.md,
                  fontSize: '0.8rem',
                  color: theme.colors.gray600,
                }}
              >
                Applicable to: {promotion.applicableVehicles}
              </div>

              <ActionButtons>
                <ActionButtonSmall>
                  <FaEye />
                  View Details
                </ActionButtonSmall>
                {promotion.status === 'draft' && (
                  <ActionButtonSmall variant="activate">
                    <FaPlay />
                    Activate
                  </ActionButtonSmall>
                )}
                {promotion.status === 'active' && (
                  <ActionButtonSmall variant="pause">
                    <FaPause />
                    Pause
                  </ActionButtonSmall>
                )}
                {promotion.status === 'paused' && (
                  <ActionButtonSmall variant="activate">
                    <FaPlay />
                    Resume
                  </ActionButtonSmall>
                )}
                {(promotion.status === 'active' ||
                  promotion.status === 'paused') && (
                  <ActionButtonSmall variant="stop">
                    <FaStop />
                    Stop
                  </ActionButtonSmall>
                )}
                <ActionButtonSmall>
                  <FaCopy />
                  Duplicate
                </ActionButtonSmall>
                <ActionButtonSmall>
                  <FaEdit />
                  Edit
                </ActionButtonSmall>
              </ActionButtons>
            </PromotionCard>
          ))
        ) : (
          <EmptyState>
            <FaTags className="icon" />
            <h3>No promotions found</h3>
            <p>
              Try adjusting your search criteria or create a new promotional
              campaign.
            </p>
          </EmptyState>
        )}
      </PromotionsGrid>
    </Container>
  );
};

