    setOrders(mockOrders);
    setFilteredOrders(mockOrders);
  }, []);

  useEffect(() => {
    let filtered = [...orders];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(
        order =>
          order.orderNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
          order.customerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
          order.vehicleName.toLowerCase().includes(searchTerm.toLowerCase()) ||
          order.salesPerson.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Status filter
    if (statusFilter) {
      filtered = filtered.filter(order => order.status === statusFilter);
    }

    // Date filter
    if (dateFilter) {
      const today = new Date();
      const filterDate = new Date();

      switch (dateFilter) {
        case 'today':
          filterDate.setHours(0, 0, 0, 0);
          filtered = filtered.filter(
            order => new Date(order.orderDate) >= filterDate
          );
          break;
        case 'week':
          filterDate.setDate(today.getDate() - 7);
          filtered = filtered.filter(
            order => new Date(order.orderDate) >= filterDate
          );
          break;
        case 'month':
          filterDate.setMonth(today.getMonth() - 1);
          filtered = filtered.filter(
            order => new Date(order.orderDate) >= filterDate
          );
          break;
        default:
          break;
      }
    }

    setFilteredOrders(filtered);
    setCurrentPage(1);
  }, [orders, searchTerm, statusFilter, dateFilter]);

  const formatCurrency = amount => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = dateString => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
    });
  };

  const getStatusLabel = status => {
    switch (status) {
      case 'pending':
        return 'Pending';
      case 'confirmed':
        return 'Confirmed';
      case 'processing':
        return 'Processing';
      case 'shipped':
        return 'Shipped';
      case 'delivered':
        return 'Delivered';
      case 'cancelled':
        return 'Cancelled';
      default:
        return status;
    }
  };

  const stats = {
    total: orders.length,
    pending: orders.filter(o => o.status === 'pending').length,
    confirmed: orders.filter(o => o.status === 'confirmed').length,
    shipped: orders.filter(o => o.status === 'shipped').length,
    delivered: orders.filter(o => o.status === 'delivered').length,
    cancelled: orders.filter(o => o.status === 'cancelled').length,
    totalRevenue: orders
      .filter(o => o.paymentStatus === 'paid')
      .reduce((sum, o) => sum + o.vehiclePrice, 0),
    totalCommission: orders
      .filter(o => o.paymentStatus === 'paid')
      .reduce((sum, o) => sum + o.commission, 0),
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(
    indexOfFirstOrder,
    indexOfLastOrder
  );
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  return (
    <Container>
      <Header>
        <HeaderTop>
          <Title>
            <FaShoppingCart />
            Sales & Orders ({filteredOrders.length})
          </Title>
          <HeaderActions>
            <ActionButton>
              <FaDownload />
              Export Orders
            </ActionButton>
            <ActionButton primary>
              <FaPlus />
              New Order
            </ActionButton>
          </HeaderActions>
        </HeaderTop>

        <FiltersRow>
          <SearchContainer>
            <SearchIcon>
              <FaSearch />
            </SearchIcon>
            <SearchInput
              type="text"
              placeholder="Search orders by number, customer, vehicle, or salesperson..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </SearchContainer>

          <FilterSelect
            value={statusFilter}
            onChange={e => setStatusFilter(e.target.value)}
          >
            <option value="">All Statuses</option>
            <option value="pending">Pending</option>
            <option value="confirmed">Confirmed</option>
            <option value="processing">Processing</option>
            <option value="shipped">Shipped</option>
            <option value="delivered">Delivered</option>
            <option value="cancelled">Cancelled</option>
          </FilterSelect>

          <FilterSelect
            value={dateFilter}
            onChange={e => setDateFilter(e.target.value)}
          >
            <option value="">All Time</option>
            <option value="today">Today</option>
            <option value="week">This Week</option>
            <option value="month">This Month</option>
          </FilterSelect>

          <FilterSelect defaultValue="recent">
            <option value="recent">Recent First</option>
            <option value="oldest">Oldest First</option>
            <option value="value">Highest Value</option>
            <option value="customer">Customer A-Z</option>
          </FilterSelect>
        </FiltersRow>
      </Header>

      <StatsRow>
        <StatCard>
          <StatValue>{stats.total}</StatValue>
          <StatLabel>Total Orders</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.warning}>{stats.pending}</StatValue>
          <StatLabel>Pending</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.success}>{stats.delivered}</StatValue>
          <StatLabel>Delivered</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.error}>{stats.cancelled}</StatValue>
          <StatLabel>Cancelled</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.success}>
            {formatCurrency(stats.totalRevenue)}
          </StatValue>
          <StatLabel>Total Revenue</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.info}>
            {formatCurrency(stats.totalCommission)}
          </StatValue>
          <StatLabel>Total Commission</StatLabel>
        </StatCard>
      </StatsRow>

      <OrdersTable>
        {currentOrders.length > 0 ? (
          <Table>
            <TableHeader>
              <TableRow>
                <TableHeaderCell>Order</TableHeaderCell>
                <TableHeaderCell>Customer</TableHeaderCell>
                <TableHeaderCell>Vehicle</TableHeaderCell>
                <TableHeaderCell>Amount</TableHeaderCell>
                <TableHeaderCell>Status</TableHeaderCell>
                <TableHeaderCell>Sales Person</TableHeaderCell>
                <TableHeaderCell>Delivery</TableHeaderCell>
                <TableHeaderCell>Actions</TableHeaderCell>
              </TableRow>
            </TableHeader>
            <tbody>
              {currentOrders.map(order => (
                <TableRow key={order.id}>
                  <TableCell>
                    <OrderInfo>
                      <OrderId>#{order.orderNumber}</OrderId>
                      <OrderDate>
                        <FaCalendar
                          style={{ marginRight: '4px', fontSize: '0.7rem' }}
                        />
                        {formatDate(order.orderDate)}
                      </OrderDate>
                    </OrderInfo>
                  </TableCell>
                  <TableCell>
                    <CustomerInfo>
                      <CustomerName>{order.customerName}</CustomerName>
                      <CustomerContact>{order.customerEmail}</CustomerContact>
                      <CustomerContact>{order.customerPhone}</CustomerContact>
                    </CustomerInfo>
                  </TableCell>
                  <TableCell>
                    <VehicleInfo>
                      <VehicleImage
                        src={order.vehicleImage}
                        alt={order.vehicleName}
                      />
                      <VehicleDetails>
                        <VehicleName>{order.vehicleName}</VehicleName>
                        <VehicleSpec>
                          Commission: {formatCurrency(order.commission)}
                        </VehicleSpec>
                      </VehicleDetails>
                    </VehicleInfo>
                  </TableCell>
                  <TableCell>
                    <PriceCell>{formatCurrency(order.vehiclePrice)}</PriceCell>
                  </TableCell>
                  <TableCell>
                    <StatusBadge status={order.status}>
                      {getStatusLabel(order.status)}
                    </StatusBadge>
                  </TableCell>
                  <TableCell>{order.salesPerson}</TableCell>
                  <TableCell>
                    {order.deliveryDate
                      ? formatDate(order.deliveryDate)
                      : 'TBD'}
                  </TableCell>
                  <TableCell>
                    <ActionButtons>
                      <ActionButtonSmall title="View Details">
                        <FaEye />
                      </ActionButtonSmall>
                      <ActionButtonSmall className="edit" title="Edit Order">
                        <FaEdit />
                      </ActionButtonSmall>
                      <ActionButtonSmall
                        className="print"
                        title="Print Invoice"
                      >
                        <FaPrint />
                      </ActionButtonSmall>
                      <ActionButtonSmall
                        className="delete"
                        title="Cancel Order"
                      >
                        <FaTimes />
                      </ActionButtonSmall>
                    </ActionButtons>
                  </TableCell>
                </TableRow>
              ))}
            </tbody>
          </Table>
        ) : (
          <EmptyState>
            <FaShoppingCart className="icon" />
            <h3>No orders found</h3>
            <p>Try adjusting your search criteria or create a new order.</p>
          </EmptyState>
        )}
      </OrdersTable>

      {totalPages > 1 && (
        <Pagination>
          <PaginationButton
            onClick={() => setCurrentPage(currentPage - 1)}
            disabled={currentPage === 1}
          >
            <FaChevronLeft />
            Previous
          </PaginationButton>

          <PaginationInfo>
            Page {currentPage} of {totalPages} ({filteredOrders.length} orders)
          </PaginationInfo>

          <PaginationButton
            onClick={() => setCurrentPage(currentPage + 1)}
            disabled={currentPage === totalPages}
          >
            Next
            <FaChevronRight />
          </PaginationButton>
        </Pagination>
      )}
    </Container>
  );
};

