        services: ['ECU Update', 'System Check'],
      },
      {
        id: 'SA-006',
        appointmentId: '24-SA-006',
        customerId: 6,
        customerName: 'Lisa Anderson',
        customerEmail: 'lisa.anderson@email.com',
        customerPhone: '(555) 678-9012',
        vehicle: {
          year: 2023,
          make: 'Volvo',
          model: 'XC90',
          trim: 'Recharge',
          color: 'Gray',
          mileage: 18000,
          vin: 'YV4BR0CA6P1123456',
          image:
            'https://images.unsplash.com/photo-1606016872875-84d1177d27bb?w=300&h=200&fit=crop',
        },
        appointmentDate: '2024-02-17T10:00:00Z',
        scheduledDate: '2024-02-10T13:30:00Z',
        status: 'no_show',
        serviceType: 'Warranty Inspection',
        description: 'Warranty inspection for hybrid system',
        estimatedDuration: 120,
        estimatedCost: 0,
        technician: 'Mark Thompson',
        bay: 'Hybrid Bay',
        notes: 'Customer did not show up, attempting to reschedule',
        services: [
          'Hybrid System Check',
          'Battery Inspection',
          'Charging System Test',
        ],
      },
    ];

    setAppointments(mockAppointments);
    setFilteredAppointments(mockAppointments);
  }, []);

  useEffect(() => {
    let filtered = [...appointments];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(
        appointment =>
          appointment.appointmentId
            .toLowerCase()
            .includes(searchTerm.toLowerCase()) ||
          appointment.customerName
            .toLowerCase()
            .includes(searchTerm.toLowerCase()) ||
          `${appointment.vehicle.year} ${appointment.vehicle.make} ${appointment.vehicle.model}`
            .toLowerCase()
            .includes(searchTerm.toLowerCase()) ||
          appointment.serviceType
            .toLowerCase()
            .includes(searchTerm.toLowerCase()) ||
          (appointment.technician &&
            appointment.technician
              .toLowerCase()
              .includes(searchTerm.toLowerCase()))
      );
    }

    // Status filter
    if (statusFilter) {
      filtered = filtered.filter(
        appointment => appointment.status === statusFilter
      );
    }

    // Date filter
    if (dateFilter) {
      const today = new Date();
      const filterDate = new Date();

      switch (dateFilter) {
        case 'today':
          filterDate.setHours(0, 0, 0, 0);
          const tomorrow = new Date(filterDate);
          tomorrow.setDate(tomorrow.getDate() + 1);
          filtered = filtered.filter(appointment => {
            const apptDate = new Date(appointment.appointmentDate);
            return apptDate >= filterDate && apptDate < tomorrow;
          });
          break;
        case 'week':
          filterDate.setDate(today.getDate() - 7);
          filtered = filtered.filter(
            appointment => new Date(appointment.appointmentDate) >= filterDate
          );
          break;
        case 'upcoming':
          filtered = filtered.filter(
            appointment =>
              new Date(appointment.appointmentDate) >= today &&
              (appointment.status === 'scheduled' ||
                appointment.status === 'confirmed')
          );
          break;
        default:
          break;
      }
    }

    setFilteredAppointments(filtered);
  }, [appointments, searchTerm, statusFilter, dateFilter]);

  const formatCurrency = amount => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDateTime = dateString => {
    const date = new Date(dateString);
    return {
      date: date.toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric',
        year: 'numeric',
      }),
      time: date.toLocaleTimeString('en-US', {
        hour: 'numeric',
        minute: '2-digit',
        hour12: true,
      }),
    };
  };

  const getStatusLabel = status => {
    switch (status) {
      case 'scheduled':
        return 'Scheduled';
      case 'confirmed':
        return 'Confirmed';
      case 'in_progress':
        return 'In Progress';
      case 'completed':
        return 'Completed';
      case 'cancelled':
        return 'Cancelled';
      case 'no_show':
        return 'No Show';
      default:
        return status;
    }
  };

  const stats = {
    total: appointments.length,
    today: appointments.filter(a => {
      const today = new Date();
      const apptDate = new Date(a.appointmentDate);
      return apptDate.toDateString() === today.toDateString();
    }).length,
    scheduled: appointments.filter(
      a => a.status === 'scheduled' || a.status === 'confirmed'
    ).length,
    inProgress: appointments.filter(a => a.status === 'in_progress').length,
    completed: appointments.filter(a => a.status === 'completed').length,
    revenue: appointments
      .filter(a => a.status === 'completed')
      .reduce((sum, a) => sum + (a.actualCost || a.estimatedCost), 0),
  };

  return (
    <Container>
      <Header>
        <HeaderTop>
          <Title>
            <FaWrench />
            Service Appointments ({filteredAppointments.length})
          </Title>
          <HeaderActions>
            <ActionButton>
              <FaDownload />
              Export Schedule
            </ActionButton>
            <ActionButton>
              <FaCalendar />
              View Calendar
            </ActionButton>
            <ActionButton primary>
              <FaPlus />
              New Appointment
            </ActionButton>
          </HeaderActions>
        </HeaderTop>

        <FiltersRow>
          <SearchContainer>
            <SearchIcon>
              <FaSearch />
            </SearchIcon>
            <SearchInput
              type="text"
              placeholder="Search by appointment ID, customer, vehicle, service type, or technician..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </SearchContainer>

          <FilterSelect
            value={statusFilter}
            onChange={e => setStatusFilter(e.target.value)}
          >
            <option value="">All Statuses</option>
            <option value="scheduled">Scheduled</option>
            <option value="confirmed">Confirmed</option>
            <option value="in_progress">In Progress</option>
            <option value="completed">Completed</option>
            <option value="cancelled">Cancelled</option>
            <option value="no_show">No Show</option>
          </FilterSelect>

          <FilterSelect
            value={dateFilter}
            onChange={e => setDateFilter(e.target.value)}
          >
            <option value="">All Dates</option>
            <option value="today">Today</option>
            <option value="upcoming">Upcoming</option>
            <option value="week">This Week</option>
          </FilterSelect>

          <FilterSelect defaultValue="date">
            <option value="date">Sort by Date</option>
            <option value="customer">Customer A-Z</option>
            <option value="technician">Technician</option>
            <option value="cost">Highest Cost</option>
          </FilterSelect>
        </FiltersRow>
      </Header>

      <StatsGrid>
        <StatCard>
          <StatValue>{stats.total}</StatValue>
          <StatLabel>Total Appointments</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.primary}>{stats.today}</StatValue>
          <StatLabel>Today</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.info}>{stats.scheduled}</StatValue>
          <StatLabel>Scheduled</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.warning}>{stats.inProgress}</StatValue>
          <StatLabel>In Progress</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.success}>{stats.completed}</StatValue>
          <StatLabel>Completed</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.success}>
            {formatCurrency(stats.revenue)}
          </StatValue>
          <StatLabel>Service Revenue</StatLabel>
        </StatCard>
      </StatsGrid>

      <AppointmentsGrid>
        {filteredAppointments.length > 0 ? (
          filteredAppointments.map(appointment => {
            const dateTime = formatDateTime(appointment.appointmentDate);

            return (
              <AppointmentCard key={appointment.id}>
                <AppointmentHeader>
                  <AppointmentInfo>
                    <AppointmentId>#{appointment.appointmentId}</AppointmentId>
                    <AppointmentTime>
                      <FaClock />
                      {dateTime.time}
                    </AppointmentTime>
                    <AppointmentDate>
                      <FaCalendar />
                      {dateTime.date}
                    </AppointmentDate>
                  </AppointmentInfo>
                  <StatusBadge status={appointment.status}>
                    {getStatusLabel(appointment.status)}
                  </StatusBadge>
                </AppointmentHeader>

                <CustomerSection>
                  <CustomerName>
                    <FaUser />
                    {appointment.customerName}
                  </CustomerName>
                  <CustomerContact>
                    <FaEnvelope />
                    {appointment.customerEmail}
                  </CustomerContact>
                  <CustomerContact>
                    <FaPhone />
                    {appointment.customerPhone}
                  </CustomerContact>
                </CustomerSection>

                <VehicleSection>
                  <VehicleInfo>
                    <VehicleImage
                      src={appointment.vehicle.image}
                      alt={`${appointment.vehicle.year} ${appointment.vehicle.make} ${appointment.vehicle.model}`}
                    />
                    <VehicleDetails>
                      <VehicleName>
                        {appointment.vehicle.year} {appointment.vehicle.make}{' '}
                        {appointment.vehicle.model}
                      </VehicleName>
                      <VehicleSpecs>
                        {appointment.vehicle.trim}  {appointment.vehicle.color}{' '}
                         {appointment.vehicle.mileage.toLocaleString()} mi
                      </VehicleSpecs>
                    </VehicleDetails>
                  </VehicleInfo>
                </VehicleSection>

                <ServiceDetails>
                  <ServiceType>
                    <FaTools />
                    {appointment.serviceType}
                  </ServiceType>
                  <ServiceDescription>
                    {appointment.description}
                  </ServiceDescription>
                  <ServiceMeta>
                    <div>Duration: {appointment.estimatedDuration} min</div>
                    <div>
                      Cost:{' '}
                      {formatCurrency(
                        appointment.actualCost || appointment.estimatedCost
                      )}
                    </div>
                  </ServiceMeta>
                </ServiceDetails>

                {appointment.technician && (
                  <TechnicianInfo>
                    <FaUser />
                    Technician: {appointment.technician}
                    {appointment.bay && `  ${appointment.bay}`}
                  </TechnicianInfo>
                )}

                {appointment.notes && (
                  <div
                    style={{
                      marginBottom: theme.spacing.md,
                      fontSize: '0.9rem',
                      color: theme.colors.gray600,
                      fontStyle: 'italic',
                    }}
                  >
                    Notes: {appointment.notes}
                  </div>
                )}

                <ActionButtons>
                  <ActionButtonSmall>
                    <FaEye />
                    View Details
                  </ActionButtonSmall>
                  {appointment.status === 'scheduled' && (
                    <ActionButtonSmall variant="confirm">
                      <FaCheck />
                      Confirm
                    </ActionButtonSmall>
                  )}
                  {(appointment.status === 'confirmed' ||
                    appointment.status === 'in_progress') && (
                    <ActionButtonSmall variant="complete">
                      <FaCheck />
                      Complete
                    </ActionButtonSmall>
                  )}
                  {appointment.status === 'completed' && (
                    <ActionButtonSmall>
                      <FaPrint />
                      Print Invoice
                    </ActionButtonSmall>
                  )}
                  {(appointment.status === 'scheduled' ||
                    appointment.status === 'confirmed') && (
                    <ActionButtonSmall variant="cancel">
                      <FaTimes />
                      Cancel
                    </ActionButtonSmall>
                  )}
                  <ActionButtonSmall>
                    <FaEdit />
                    Edit
                  </ActionButtonSmall>
                </ActionButtons>
              </AppointmentCard>
            );
          })
        ) : (
          <EmptyState>
            <FaWrench className="icon" />
            <h3>No service appointments found</h3>
            <p>
              Try adjusting your search criteria or schedule a new service
              appointment.
            </p>
          </EmptyState>
        )}
      </AppointmentsGrid>
    </Container>
  );
};

