          <StatLabel>Rejected</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.success}>
            {formatCurrency(stats.totalValue)}
          </StatValue>
          <StatLabel>Total Value</StatLabel>
        </StatCard>
        <StatCard>
          <StatValue color={theme.colors.warning}>
            {stats.avgCondition}
          </StatValue>
