                <CustomerContact>{tradeIn.customerEmail}</CustomerContact>
                <CustomerContact>{tradeIn.customerPhone}</CustomerContact>
              </CustomerSection>

              <VehicleSection>
                <VehicleInfo>
                  <VehicleImage
                    src={tradeIn.vehicle.image}
                    alt={`${tradeIn.vehicle.year} ${tradeIn.vehicle.make} ${tradeIn.vehicle.model}`}
                  />
                  <VehicleDetails>
                    <VehicleName>
                      {tradeIn.vehicle.year} {tradeIn.vehicle.make}{' '}
                      {tradeIn.vehicle.model}
                    </VehicleName>
                    <VehicleSpecs>
                      <div>
                        {tradeIn.vehicle.trim}  {tradeIn.vehicle.color}
                      </div>
                      <div>
                        {tradeIn.vehicle.mileage.toLocaleString()} miles
                      </div>
                      <div>VIN: {tradeIn.vehicle.vin}</div>
                    </VehicleSpecs>
                  </VehicleDetails>
                </VehicleInfo>
              </VehicleSection>

              {tradeIn.condition.overall > 0 && (
                <ConditionRating>
                  <ConditionLabel>Condition Rating:</ConditionLabel>
                  <ConditionStars>
                    {renderStars(tradeIn.condition.overall)}
                  </ConditionStars>
                </ConditionRating>
              )}

              <PricingSection>
                <PriceItem>
                  <PriceValue color={theme.colors.info}>
                    {formatCurrency(tradeIn.pricing.marketValue)}
                  </PriceValue>
                  <PriceLabel>Market Value</PriceLabel>
                </PriceItem>
                <PriceItem>
                  <PriceValue>
                    {formatCurrency(tradeIn.pricing.estimatedValue)}
                  </PriceValue>
                  <PriceLabel>Estimated</PriceLabel>
                </PriceItem>
                <PriceItem>
                  <PriceValue color={theme.colors.success}>
                    {formatCurrency(tradeIn.pricing.offerAmount)}
                  </PriceValue>
                  <PriceLabel>Our Offer</PriceLabel>
                </PriceItem>
              </PricingSection>

              {tradeIn.appraiser && (
                <div
                  style={{
                    marginBottom: theme.spacing.md,
                    fontSize: '0.9rem',
                    color: theme.colors.gray600,
                  }}
                >
                  Appraiser: {tradeIn.appraiser}  {tradeIn.photos} photos
                </div>
              )}

              {tradeIn.notes && (
                <div
                  style={{
                    marginBottom: theme.spacing.md,
                    fontSize: '0.9rem',
                    color: theme.colors.gray600,
                    fontStyle: 'italic',
                  }}
                >
                  Notes: {tradeIn.notes}
                </div>
              )}

              <ActionButtons>
                <ActionButtonSmall>
                  <FaEye />
                  View Details
                </ActionButtonSmall>
                <ActionButtonSmall>
                  <FaCamera />
                  Photos ({tradeIn.photos})
                </ActionButtonSmall>
                {tradeIn.status === 'pending' && (
                  <ActionButtonSmall variant="primary">
                    <FaClipboardList />
                    Start Appraisal
                  </ActionButtonSmall>
                )}
                {tradeIn.status === 'appraised' && (
                  <>
                    <ActionButtonSmall variant="accept">
                      <FaCheck />
                      Accept
                    </ActionButtonSmall>
                    <ActionButtonSmall variant="reject">
                      <FaTimes />
                      Reject
                    </ActionButtonSmall>
                  </>
                )}
                {tradeIn.status === 'accepted' && (
                  <ActionButtonSmall>
                    <FaPrint />
                    Print Contract
                  </ActionButtonSmall>
                )}
                <ActionButtonSmall>
                  <FaEdit />
                  Edit
                </ActionButtonSmall>
              </ActionButtons>
            </TradeInCard>
          ))
        ) : (
          <EmptyState>
            <FaExchangeAlt className="icon" />
            <h3>No trade-ins found</h3>
            <p>
              Try adjusting your search criteria or add a new trade-in
              evaluation.
            </p>
          </EmptyState>
        )}
      </TradeInsGrid>
    </Container>
  );
};

