const VehicleCard = ({
  vehicle,
  onToggleWishlist,
  isInWishlist = false,
  dealerSlug = '',
}) => {
  const [isEnquiryModalOpen, setIsEnquiryModalOpen] = useState(false);

  const renderStars = rating => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(<Star key={i} filled={i <= Math.floor(rating)} />);
    }
    return stars;
  };

  const calculateDiscount = (original, current) => {
    return Math.round(((original - current) / original) * 100);
  };

  const handleEnquireClick = e => {
    e.preventDefault();
    e.stopPropagation();
    setIsEnquiryModalOpen(true);
  };

  const handleWishlistToggle = e => {
    e.preventDefault();
    e.stopPropagation();

    const wishlist = JSON.parse(
      localStorage.getItem('vehicleWishlist') || '[]'
    );
    let newWishlist;

    if (isInWishlist) {
      newWishlist = wishlist.filter(id => id !== vehicle.id);
    } else {
      newWishlist = [...wishlist, vehicle.id];
    }

    localStorage.setItem('vehicleWishlist', JSON.stringify(newWishlist));
    onToggleWishlist?.(vehicle);
  };

  const getBaseUrl = () => `/${dealerSlug}`;

  const formatPrice = price => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(price);
  };

  const formatMileage = mileage => {
    if (mileage === 0) return 'New';
    return new Intl.NumberFormat('en-US').format(mileage) + ' miles';
  };

  const availabilityStatus = getAvailabilityStatus(vehicle);
  const availabilityLabel = getAvailabilityLabel(availabilityStatus);
  const availabilityColor = getAvailabilityColor(availabilityStatus);

  return (
    <Link to={`${getBaseUrl()}/vehicledetail/${vehicle.id}`}>
      <Card>
        <ImageContainer>
          <VehicleImage src={vehicle.image} alt={vehicle.name} />

          {vehicle.onSale && vehicle.originalPrice && (
            <Badge type="sale">Sale</Badge>
          )}

          {vehicle.featured && !vehicle.onSale && (
            <Badge type="featured">Featured</Badge>
          )}

          {vehicle.condition && (
            <ConditionBadge condition={vehicle.condition}>
              {vehicle.condition === 'new'
                ? 'New'
                : vehicle.condition === 'certified'
                  ? 'Certified'
                  : vehicle.condition === 'restored'
                    ? 'Restored'
                    : 'Used'}
            </ConditionBadge>
          )}

          <WishlistButton
            onClick={handleWishlistToggle}
            title={isInWishlist ? 'Remove from wishlist' : 'Add to wishlist'}
          >
            <FaHeart
              style={{
                color: isInWishlist ? theme.colors.error : 'currentColor',
              }}
            />
          </WishlistButton>
        </ImageContainer>

        <CardContent>
          <VehicleHeader>
            <YearMake>
              {vehicle.year} {vehicle.make}
            </YearMake>
            <VehicleName>
              {vehicle.model} {vehicle.trim}
            </VehicleName>
          </VehicleHeader>

          <VehicleSpecs>
            <SpecItem>
              <FaCar className="icon" />
              <span>{vehicle.specifications?.engine || 'N/A'}</span>
            </SpecItem>
            <SpecItem>
              <FaGasPump className="icon" />
              <span>
                {vehicle.specifications?.fuelEconomy ||
                  vehicle.specifications?.range ||
                  'N/A'}
              </span>
            </SpecItem>
            <SpecItem>
              <FaRoad className="icon" />
              <span>{formatMileage(vehicle.mileage)}</span>
            </SpecItem>
          </VehicleSpecs>

          <Description>{vehicle.description}</Description>

          <RatingContainer>
            <StarRating>{renderStars(vehicle.rating)}</StarRating>
            <ReviewCount>({vehicle.reviews} reviews)</ReviewCount>
          </RatingContainer>

          <PriceContainer>
            <CurrentPrice>{formatPrice(vehicle.price)}</CurrentPrice>
            {vehicle.originalPrice && vehicle.originalPrice > vehicle.price && (
              <>
                <OriginalPrice>
                  {formatPrice(vehicle.originalPrice)}
                </OriginalPrice>
                <Discount>
                  -{calculateDiscount(vehicle.originalPrice, vehicle.price)}%
                </Discount>
              </>
            )}
          </PriceContainer>

          <StockIndicator
            availability={availabilityStatus}
            color={availabilityColor}
          >
            {availabilityStatus === 'in_stock' && vehicle.stock > 0
              ? `${vehicle.stock} available`
              : availabilityLabel}
          </StockIndicator>

          <ActionButtons>
            <EnquireButton
              onClick={handleEnquireClick}
              disabled={availabilityStatus === 'out_of_stock'}
            >
              <FaEnvelope />
              {availabilityStatus === 'out_of_stock'
                ? 'Not Available'
                : 'Get Quote'}
            </EnquireButton>
          </ActionButtons>
        </CardContent>
      </Card>

      <EnquiryModal
        isOpen={isEnquiryModalOpen}
        onClose={() => setIsEnquiryModalOpen(false)}
        vehicle={vehicle}
      />
    </Link>
  );
};

export default VehicleCard;

