const VehicleInventoryTab = ({ dealer }) => {
  const [vehicleList, setVehicleList] = useState([]);
  const [filteredVehicles, setFilteredVehicles] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [conditionFilter, setConditionFilter] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [sortDirection, setSortDirection] = useState('asc');

  useEffect(() => {
    // In a real app, this would fetch vehicles for the specific dealer
    setVehicleList(vehicles);
    setFilteredVehicles(vehicles);
  }, []);

  useEffect(() => {
    let filtered = [...vehicleList];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(
        vehicle =>
          vehicle.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          vehicle.make.toLowerCase().includes(searchTerm.toLowerCase()) ||
          vehicle.model.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Category filter
    if (categoryFilter) {
      filtered = filtered.filter(
        vehicle => vehicle.category === categoryFilter
      );
    }

    // Condition filter
    if (conditionFilter) {
      filtered = filtered.filter(
        vehicle => vehicle.condition === conditionFilter
      );
    }

    // Sort
    filtered.sort((a, b) => {
      let aVal, bVal;

      switch (sortBy) {
        case 'price':
          aVal = a.price;
          bVal = b.price;
          break;
        case 'year':
          aVal = a.year;
          bVal = b.year;
          break;
        case 'mileage':
          aVal = a.mileage;
          bVal = b.mileage;
          break;
        case 'name':
        default:
          aVal = a.name.toLowerCase();
          bVal = b.name.toLowerCase();
          break;
      }

      if (sortDirection === 'asc') {
        return aVal > bVal ? 1 : -1;
      } else {
        return aVal < bVal ? 1 : -1;
      }
    });

    setFilteredVehicles(filtered);
  }, [
    vehicleList,
    searchTerm,
    categoryFilter,
    conditionFilter,
    sortBy,
    sortDirection,
  ]);

  const handleSort = field => {
    if (sortBy === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(field);
      setSortDirection('asc');
    }
  };

  const formatPrice = price => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(price);
  };

  const stats = {
    total: vehicleList.length,
    available: vehicleList.filter(v => getAvailabilityStatus(v) === 'in_stock')
      .length,
    sold: vehicleList.filter(v => getAvailabilityStatus(v) === 'out_of_stock')
      .length,
    newVehicles: vehicleList.filter(v => v.condition === 'new').length,
  };

  const categories = [...new Set(vehicleList.map(v => v.category))];
  const conditions = [...new Set(vehicleList.map(v => v.condition))];

  return (
    <InventoryContainer>
      <InventoryHeader>
        <HeaderTop>
          <HeaderTitle>
            <FaCar />
            Vehicle Inventory ({filteredVehicles.length})
          </HeaderTitle>
          <AddButton>
            <FaPlus />
            Add New Vehicle
          </AddButton>
        </HeaderTop>

        <FiltersRow>
          <SearchContainer>
            <SearchIcon>
              <FaSearch />
            </SearchIcon>
            <SearchInput
              type="text"
              placeholder="Search vehicles by name, make, or model..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </SearchContainer>

          <FilterSelect
            value={categoryFilter}
            onChange={e => setCategoryFilter(e.target.value)}
          >
            <option value="">All Categories</option>
            {categories.map(category => (
              <option key={category} value={category}>
                {category
                  .replace('-', ' ')
                  .replace(/\b\w/g, l => l.toUpperCase())}
              </option>
            ))}
          </FilterSelect>

          <FilterSelect
            value={conditionFilter}
            onChange={e => setConditionFilter(e.target.value)}
          >
            <option value="">All Conditions</option>
            {conditions.map(condition => (
              <option key={condition} value={condition}>
                {condition.charAt(0).toUpperCase() + condition.slice(1)}
              </option>
            ))}
          </FilterSelect>

          <SortButton onClick={() => handleSort('price')}>
            {sortBy === 'price' && sortDirection === 'desc' ? (
              <FaSortAmountDown />
            ) : (
              <FaSortAmountUp />
            )}
            Sort by Price
          </SortButton>
        </FiltersRow>
      </InventoryHeader>

      <StatsRow>
        <StatItem>
          <StatValue>{stats.total}</StatValue>
          <StatLabel>Total Vehicles</StatLabel>
        </StatItem>
        <StatItem>
          <StatValue>{stats.available}</StatValue>
          <StatLabel>Available</StatLabel>
        </StatItem>
        <StatItem>
          <StatValue>{stats.sold}</StatValue>
          <StatLabel>Sold</StatLabel>
        </StatItem>
        <StatItem>
          <StatValue>{stats.newVehicles}</StatValue>
          <StatLabel>New Vehicles</StatLabel>
        </StatItem>
      </StatsRow>

      <TableContainer>
        {filteredVehicles.length > 0 ? (
          <Table>
            <TableHeader>
              <TableRow>
                <TableHeaderCell>Vehicle</TableHeaderCell>
                <TableHeaderCell>Category</TableHeaderCell>
                <TableHeaderCell>Condition</TableHeaderCell>
                <TableHeaderCell>Price</TableHeaderCell>
                <TableHeaderCell>Status</TableHeaderCell>
                <TableHeaderCell>Stock</TableHeaderCell>
                <TableHeaderCell>Actions</TableHeaderCell>
              </TableRow>
            </TableHeader>
            <tbody>
              {filteredVehicles.map(vehicle => {
                const status = getAvailabilityStatus(vehicle);
                const statusLabel = getAvailabilityLabel(status);
                const statusColor = getAvailabilityColor(status);

                return (
                  <TableRow key={vehicle.id}>
                    <TableCell>
                      <VehicleInfo>
                        <VehicleImage src={vehicle.image} alt={vehicle.name} />
                        <VehicleDetails>
                          <VehicleName>
                            {vehicle.year} {vehicle.make} {vehicle.model}
                          </VehicleName>
                          <VehicleSpec>
                            {vehicle.trim} {' '}
                            {vehicle.mileage === 0
                              ? 'New'
                              : `${vehicle.mileage.toLocaleString()} mi`}
                          </VehicleSpec>
                        </VehicleDetails>
                      </VehicleInfo>
                    </TableCell>
                    <TableCell>
                      {vehicle.category
                        .replace('-', ' ')
                        .replace(/\b\w/g, l => l.toUpperCase())}
                    </TableCell>
                    <TableCell>
                      {vehicle.condition.charAt(0).toUpperCase() +
                        vehicle.condition.slice(1)}
                    </TableCell>
                    <TableCell>
                      <Price>{formatPrice(vehicle.price)}</Price>
                    </TableCell>
                    <TableCell>
                      <StatusBadge color={statusColor}>
                        {statusLabel}
                      </StatusBadge>
                    </TableCell>
                    <TableCell>{vehicle.stock}</TableCell>
                    <TableCell>
                      <ActionButtons>
                        <ActionButton title="View Details">
                          <FaEye />
                        </ActionButton>
                        <ActionButton className="edit" title="Edit Vehicle">
                          <FaEdit />
                        </ActionButton>
                        <ActionButton className="delete" title="Delete Vehicle">
                          <FaTrash />
                        </ActionButton>
                      </ActionButtons>
                    </TableCell>
                  </TableRow>
                );
              })}
            </tbody>
          </Table>
        ) : (
          <EmptyState>
            <FaCar className="icon" />
            <h3>No vehicles found</h3>
            <p>
              Try adjusting your search criteria or add new vehicles to your
              inventory.
            </p>
          </EmptyState>
        )}
      </TableContainer>
    </InventoryContainer>
  );
};

