const Vehicles = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [searchParams, setSearchParams] = useSearchParams();

  const [selectedDealer, setSelectedDealer] = useState(null);
  const [filteredVehicles, setFilteredVehicles] = useState(vehicles);
  const [filters, setFilters] = useState({
    category: searchParams.get('category') || '',
    make: searchParams.get('make') || '',
    condition: searchParams.get('condition') || '',
    minPrice: searchParams.get('minPrice') || '',
    maxPrice: searchParams.get('maxPrice') || '',
    sortBy: searchParams.get('sort') || 'name',
  });
  const [viewMode, setViewMode] = useState('grid');

  const makes = [...new Set(vehicles.map(v => v.make))].sort();
  const conditions = [...new Set(vehicles.map(v => v.condition))].sort();

  useEffect(() => {
    // Get dealer data from URL
    const path = location.pathname;
    let dealer = null;

    if (path !== '/automobiles/vehicles') {
      const pathSegments = path.split('/').filter(Boolean);
      const dealerSlug = pathSegments[0];
      dealer = getVendorByIdOrSlug(dealerSlug);
    }

    if (dealer) {
      setSelectedDealer(dealer);
    } else {
      // If no dealer found, redirect to dealer listing
      navigate('/auto-dealers');
      return;
    }
  }, [location.pathname, navigate]);

  const applyFilters = useCallback(() => {
    let filtered = [...vehicles];

    // Category filter
    if (filters.category) {
      filtered = getVehiclesByCategory(filters.category);
    }

    // Make filter
    if (filters.make) {
      filtered = filtered.filter(v => v.make === filters.make);
    }

    // Condition filter
    if (filters.condition) {
      filtered = filtered.filter(v => v.condition === filters.condition);
    }

    // Price range filter
    if (filters.minPrice) {
      filtered = filtered.filter(v => v.price >= parseInt(filters.minPrice));
    }
    if (filters.maxPrice) {
      filtered = filtered.filter(v => v.price <= parseInt(filters.maxPrice));
    }

    // Sort
    switch (filters.sortBy) {
      case 'price-low':
        filtered.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        filtered.sort((a, b) => b.price - a.price);
        break;
      case 'year-new':
        filtered.sort((a, b) => b.year - a.year);
        break;
      case 'year-old':
        filtered.sort((a, b) => a.year - b.year);
        break;
      case 'name':
      default:
        filtered.sort((a, b) => a.name.localeCompare(b.name));
        break;
    }

    setFilteredVehicles(filtered);
  }, [filters]);

  useEffect(() => {
    applyFilters();
  }, [applyFilters]);

  const handleFilterChange = (key, value) => {
    const newFilters = { ...filters, [key]: value };
    setFilters(newFilters);

    // Update URL params
    const newParams = new URLSearchParams();
    Object.entries(newFilters).forEach(([k, v]) => {
      if (v) newParams.set(k, v);
    });
    setSearchParams(newParams);
  };

  const clearFilters = () => {
    const clearedFilters = {
      category: '',
      make: '',
      condition: '',
      minPrice: '',
      maxPrice: '',
      sortBy: 'name',
    };
    setFilters(clearedFilters);
    setSearchParams({});
  };

  const handleBackToDealer = () => {
    if (selectedDealer) {
      navigate(`/${selectedDealer.slug}`);
    } else {
      navigate('/auto-dealers');
    }
  };

  if (!selectedDealer) {
    return null; // Will redirect
  }

  const dealerTheme = selectedDealer.theme || {};

  return (
    <PageContainer>
      <BackButton onClick={handleBackToDealer}>
        <FaHome />
        Back to {selectedDealer.name}
      </BackButton>

      <Navbar
        dealerName={selectedDealer.name}
        dealerLogo={selectedDealer.logo}
        dealerSlug={selectedDealer.slug}
        theme={dealerTheme}
      />

      <Container>
        <PageHeader>
          <PageTitle>Vehicle Inventory</PageTitle>
          <PageSubtitle>
            Browse our complete selection of vehicles at {selectedDealer.name}
          </PageSubtitle>
        </PageHeader>

        <FilterSection>
          <FilterHeader>
            <FilterTitle>
              <FaFilter />
              Filter & Search
            </FilterTitle>
            <ViewToggle>
              <ViewButton
                active={viewMode === 'grid'}
                onClick={() => setViewMode('grid')}
              >
                <FaTh />
                Grid
              </ViewButton>
              <ViewButton
                active={viewMode === 'list'}
                onClick={() => setViewMode('list')}
              >
                <FaList />
                List
              </ViewButton>
            </ViewToggle>
          </FilterHeader>

          <FilterGrid>
            <FilterGroup>
              <FilterLabel>Category</FilterLabel>
              <Select
                value={filters.category}
                onChange={e => handleFilterChange('category', e.target.value)}
              >
                <option value="">All Categories</option>
                {categories.map(cat => (
                  <option key={cat.id} value={cat.slug}>
                    {cat.name}
                  </option>
                ))}
              </Select>
            </FilterGroup>

            <FilterGroup>
              <FilterLabel>Make</FilterLabel>
              <Select
                value={filters.make}
                onChange={e => handleFilterChange('make', e.target.value)}
              >
                <option value="">All Makes</option>
                {makes.map(make => (
                  <option key={make} value={make}>
                    {make}
                  </option>
                ))}
              </Select>
            </FilterGroup>

            <FilterGroup>
              <FilterLabel>Condition</FilterLabel>
              <Select
                value={filters.condition}
                onChange={e => handleFilterChange('condition', e.target.value)}
              >
                <option value="">All Conditions</option>
                {conditions.map(condition => (
                  <option key={condition} value={condition}>
                    {condition.charAt(0).toUpperCase() + condition.slice(1)}
                  </option>
                ))}
              </Select>
            </FilterGroup>

            <FilterGroup>
              <FilterLabel>Price Range</FilterLabel>
              <PriceRangeContainer>
                <PriceInput
                  type="number"
                  placeholder="Min"
                  value={filters.minPrice}
                  onChange={e => handleFilterChange('minPrice', e.target.value)}
                />
                <span>-</span>
                <PriceInput
                  type="number"
                  placeholder="Max"
                  value={filters.maxPrice}
                  onChange={e => handleFilterChange('maxPrice', e.target.value)}
                />
              </PriceRangeContainer>
            </FilterGroup>
          </FilterGrid>

          {Object.values(filters).some(v => v && v !== 'name') && (
            <div style={{ marginTop: theme.spacing.lg, textAlign: 'center' }}>
              <ClearFiltersButton onClick={clearFilters}>
                Clear All Filters
              </ClearFiltersButton>
            </div>
          )}
        </FilterSection>

        <ResultsHeader>
          <ResultsCount>
            {filteredVehicles.length} Vehicle
            {filteredVehicles.length !== 1 ? 's' : ''} Found
          </ResultsCount>
          <SortContainer>
            <FaSort />
            <Select
              value={filters.sortBy}
              onChange={e => handleFilterChange('sortBy', e.target.value)}
            >
              <option value="name">Sort by Name</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="year-new">Year: Newest First</option>
              <option value="year-old">Year: Oldest First</option>
            </Select>
          </SortContainer>
        </ResultsHeader>

        {filteredVehicles.length > 0 ? (
          <VehiclesGrid viewMode={viewMode}>
            {filteredVehicles.map(vehicle => (
              <VehicleCard
                key={vehicle.id}
                vehicle={vehicle}
                dealerSlug={selectedDealer.slug}
              />
            ))}
          </VehiclesGrid>
        ) : (
          <EmptyState>
            <h3>No vehicles found</h3>
            <p>
              Try adjusting your filters or search criteria to find more
              vehicles.
            </p>
            <ClearFiltersButton onClick={clearFilters}>
              Clear All Filters
            </ClearFiltersButton>
          </EmptyState>
        )}
      </Container>

      <Footer
        dealerSlug={selectedDealer.slug}
        dealer={selectedDealer}
        theme={dealerTheme}
      />
      <BackToTop />
    </PageContainer>
  );
};

export default Vehicles;

