              })}
            </ListContainer>

            {/* Preview order */}
            <div
              style={{
                marginTop: '24px',
                padding: '16px',
                background: '#f8fafc',
                border: '1px solid #e2e8f0',
                borderRadius: '8px',
              }}
            >
              <h4 style={{ margin: '0 0 12px 0', color: '#374151' }}>
                Section Preview Order
              </h4>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: '6px' }}>
                {sectionOrderData.map((sectionId, index) => (
                  <span
                    key={sectionId}
                    style={{
                      padding: '4px 8px',
                      background: sectionVisibility[sectionId]
                        ? '#3b82f6'
                        : '#6b7280',
                      color: 'white',
                      borderRadius: '4px',
                      fontSize: '0.8rem',
                      opacity: sectionVisibility[sectionId] ? 1 : 0.6,
                    }}
                  >
                    {index + 1}. {sectionId.replace(/-/g, ' ')}
                  </span>
                ))}
              </div>
            </div>
          </ContentSection>
        );

      default:
        return (
          <ContentSection>
            <SectionTitle>
              <FaEdit />
              {activeSection.charAt(0).toUpperCase() +
                activeSection.slice(1).replace(/-/g, ' ')}{' '}
              Management
            </SectionTitle>
            <p>
              This section is under development. Please select another section
              to edit.
            </p>
            <p>
              Available sections: Hero, About Us, Services, Team, Portfolio,
              Skills, Experience, Gallery, Packages, Testimonials, Reviews, FAQ,
              Business Hours, Contact
            </p>
          </ContentSection>
        );
    }
  };

  if (loading) {
    return (
      <DashboardContainer>
        <div style={{ padding: '4rem', textAlign: 'center' }}>
          <h2>Loading Dashboard...</h2>
          <p>Please wait while we load your business dashboard.</p>
        </div>
      </DashboardContainer>
    );
  }

  if (!business) {
    return (
      <DashboardContainer>
        <div style={{ padding: '4rem', textAlign: 'center' }}>
          <h2>Business Not Found</h2>
          <p>The business you're trying to edit doesn't exist.</p>
          <ActionButton onClick={() => navigate('/business-websites')}>
            <FaArrowLeft />
            Back to Business Templates
          </ActionButton>
        </div>
      </DashboardContainer>
    );
  }

  return (
    <DashboardContainer>
      {/* Mobile Sidebar Overlay */}
      <MobileSidebarOverlay
        isOpen={mobileSidebarOpen}
        onClick={closeMobileSidebar}
      />

      {/* Mobile Menu Button */}
      <MobileMenuButton
        onClick={() => setMobileSidebarOpen(!mobileSidebarOpen)}
        aria-label={mobileSidebarOpen ? 'Close menu' : 'Open menu'}
      >
        {mobileSidebarOpen ? <FaTimes /> : <FaBars />}
      </MobileMenuButton>

      {/* Sidebar */}
      <Sidebar mobileOpen={mobileSidebarOpen}>
        <MobileSidebarCloseButton onClick={closeMobileSidebar}>
          <FaTimes />
        </MobileSidebarCloseButton>

        <SidebarHeader>
          <BusinessName>{business.name}</BusinessName>
          <BusinessRole>Business Dashboard</BusinessRole>
        </SidebarHeader>

        <SidebarNav>
          <NavSection>
            <NavSectionTitle>Content Management</NavSectionTitle>
            {navigationItems
              .filter(item => item.section === 'Content Management')
              .map(item => (
                <NavItem
                  key={item.id}
                  active={activeSection === item.id}
                  onClick={() => setActiveSection(item.id)}
                >
                  <item.icon />
                  {item.label}
                </NavItem>
              ))}
          </NavSection>

          <NavSection>
            <NavSectionTitle>Advanced</NavSectionTitle>
            {navigationItems
              .filter(item => item.section === 'Advanced')
              .map(item => (
                <NavItem
                  key={item.id}
                  active={activeSection === item.id}
                  onClick={() => setActiveSection(item.id)}
                >
                  <item.icon />
                  {item.label}
                </NavItem>
              ))}
          </NavSection>

          <NavSection>
            <NavSectionTitle>Account Management</NavSectionTitle>
            {navigationItems
              .filter(item => item.section === 'Account Management')
              .map(item => (
                <NavItem
                  key={item.id}
                  active={activeSection === item.id}
                  onClick={() => setActiveSection(item.id)}
                >
                  <item.icon />
                  {item.label}
                </NavItem>
              ))}
          </NavSection>

          {/* Save Changes Section - Now part of scrollable content */}
          <NavSection>
            <NavSectionTitle>Save Changes</NavSectionTitle>
            <div style={{ padding: '0 16px' }}>
              <SaveActionsContainer>
                {hasUnsavedChanges && (
                  <ChangesIndicator>
                    <FaEdit />
                    Unsaved changes
                  </ChangesIndicator>
                )}

                <SaveButton
                  variant="primary"
                  onClick={handleSaveChanges}
                  disabled={!hasUnsavedChanges}
                  saved={saved && !hasUnsavedChanges}
                >
                  {saved && !hasUnsavedChanges ? (
                    <>
                      <FaCheckCircle />
                      Changes Saved
                    </>
                  ) : (
                    <>
                      <FaSave />
                      Save Changes
                    </>
                  )}
                </SaveButton>

                <SaveButton
                  variant="success"
                  onClick={handleSaveAndGoLive}
                  disabled={hasUnsavedChanges && !saved}
                >
                  <FaCheckCircle />
                  Save & Go Live
                </SaveButton>

                <SaveButton
                  variant="secondary"
                  onClick={handleDiscardChanges}
                  disabled={!hasUnsavedChanges}
                >
                  <FaUndo />
                  Discard Changes
                </SaveButton>

                {changedSections.size > 0 && (
                  <div
                    style={{
                      marginTop: '16px',
                      padding: '12px',
                      background: '#fef3c7',
                      borderRadius: '6px',
                      fontSize: '0.85rem',
                    }}
                  >
                    <strong>Changed sections:</strong>
                    <div style={{ marginTop: '4px' }}>
                      {Array.from(changedSections).map(section => (
                        <span
                          key={section}
                          style={{
                            display: 'inline-block',
                            background: '#f59e0b',
                            color: 'white',
                            padding: '2px 6px',
                            borderRadius: '3px',
                            margin: '2px',
                            fontSize: '0.8rem',
                          }}
                        >
                          {section.replace(/-/g, ' ')}
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </SaveActionsContainer>
            </div>
          </NavSection>
        </SidebarNav>
      </Sidebar>

      {/* Main Content */}
      <MainContent>
        <ContentHeader>
          <PageTitle>Business Dashboard</PageTitle>
          <PageActions>
            <ActionButton
              onClick={() => navigate(`/${businessId}`)}
              aria-label="Preview business website"
            >
              <FaEye />
              Preview Website
            </ActionButton>
            <ActionButton
              variant="primary"
              onClick={() => window.open(`/${businessId}`, '_blank')}
              aria-label="Open business website in new tab"
            >
              <FaExternalLinkAlt />
              Open Website
            </ActionButton>
          </PageActions>
        </ContentHeader>

        {renderContent()}
      </MainContent>
    </DashboardContainer>
  );
};

