class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
    };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    this.setState({
      error,
      errorInfo,
    });

    // Log error to console in development
    if (process.env.NODE_ENV === 'development') {
      console.error('ErrorBoundary caught an error:', error, errorInfo);
    }

    // In production, you would send this to an error reporting service
    if (process.env.NODE_ENV === 'production') {
      // Example: Sentry.captureException(error, { extra: errorInfo });
      console.error('Production error:', error.message);
    }
  }

  handleRefresh = () => {
    window.location.reload();
  };

  handleGoHome = () => {
    window.location.href = '/';
  };

  render() {
    if (this.state.hasError) {
      return (
        <ErrorContainer>
          <ErrorCard>
            <ErrorIcon>
              <FaExclamationTriangle />
            </ErrorIcon>

            <ErrorTitle>Oops! Something went wrong</ErrorTitle>

            <ErrorMessage>
              We're sorry, but something unexpected happened. Our team has been
              notified and is working to fix this issue.
            </ErrorMessage>

            {process.env.NODE_ENV === 'development' && this.state.error && (
              <ErrorDetails>
                <summary>Error Details (Development Mode)</summary>
                <pre>
                  {this.state.error.toString()}
                  {this.state.errorInfo?.componentStack}
                </pre>
              </ErrorDetails>
            )}

            <ButtonGroup>
              <PrimaryButton onClick={this.handleRefresh}>
                <FaRedo />
                Try Again
              </PrimaryButton>

              <SecondaryButton onClick={this.handleGoHome}>
                <FaHome />
                Go Home
              </SecondaryButton>
            </ButtonGroup>
          </ErrorCard>
        </ErrorContainer>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;

import React from 'react';
import { useParams, useLocation } from 'react-router-dom';
import HotelModule from '../hotel';
import EcommerceModule from '../ecommerce';
import AutomobileModule from '../automobiles';
import WeddingModule from '../weddings';
import BusinessModule from '../business';
import {
  getHotelBySlug,
  getVendorBySlug,
  getAutomobileVendorBySlug,
  getVendorById,
  getBusinessTemplate,
} from '../DummyData';
import HotelDetail from '../hotel/pages/HotelDetail';
import EcommerceMain from '../ecommerce/pages/EcommerceMain';

const SmartRouter = () => {
  const { slug } = useParams();
  const location = useLocation();
  const path = location.pathname;

  // Helper function to determine module type based on slug
  const getModuleType = slug => {
    // Check each data source to determine the module type // api call to find type
    const hotel = getHotelBySlug(slug);
    if (hotel) return 'hotel';

    const automobileDealer = getAutomobileVendorBySlug(slug);
    if (automobileDealer) return 'automobile';

    const vendor = getVendorBySlug(slug);
    if (vendor) return 'ecommerce';

    const weddingVendor = getVendorById(slug);
    if (weddingVendor) return 'wedding';

    const businessTemplate = getBusinessTemplate(slug);
    if (businessTemplate) return 'business';

    return null;
  };

  const moduleType = getModuleType(slug);

  // If no module found, show 404
  if (!moduleType) {
    return (
      <div
        style={{
          padding: '4rem',
          textAlign: 'center',
          backgroundColor: '#f8fafc',
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <h2
          style={{ fontSize: '2rem', marginBottom: '1rem', color: '#1f2937' }}
        >
          Page not found
        </h2>
        <p style={{ color: '#6b7280', marginBottom: '2rem' }}>
          The page you're looking for doesn't exist.
        </p>
        <a
          href="/"
          style={{
            color: '#3b82f6',
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            border: '1px solid #3b82f6',
            borderRadius: '0.5rem',
          }}
        >
          Go Home
        </a>
      </div>
    );
  }

  // Route to appropriate module based on detected type
  switch (moduleType) {
    case 'hotel':
      return <HotelModule />;
    case 'ecommerce':
      return <EcommerceModule />;
    case 'automobile':
      return <AutomobileModule />;
    case 'wedding':
      return <WeddingModule />;
    case 'business':
      return <BusinessModule />;
    default:
      return (
        <div style={{ padding: '4rem', textAlign: 'center' }}>
          <h2>Page not found</h2>
          <p>The page you're looking for doesn't exist.</p>
        </div>
      );
  }
};

export default SmartRouter;

