const AuthModal = ({ isOpen, onClose, onSuccess, defaultTab = 'login' }) => {
  const { login, register, loading } = useAuth();
  const [activeTab, setActiveTab] = useState(defaultTab);
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    role: 'customer',
    businessName: '',
    businessType: 'General',
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Prevent body scroll when modal is open
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }

    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isOpen]);

  if (!isOpen) return null;

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (activeTab === 'register') {
      if (!formData.name.trim()) {
        newErrors.name = 'Name is required';
      }

      if (!formData.phone.trim()) {
        newErrors.phone = 'Phone number is required';
      }

      if (formData.password !== formData.confirmPassword) {
        newErrors.confirmPassword = "Passwords don't match";
      }

      if (formData.role === 'seller' && !formData.businessName.trim()) {
        newErrors.businessName = 'Business name is required for sellers';
      }
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (!formData.password.trim()) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async e => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsSubmitting(true);

    try {
      let result;
      if (activeTab === 'login') {
        result = await login({
          email: formData.email,
          password: formData.password,
        });
      } else {
        result = await register(formData);
      }

      if (result.success) {
        // Reset form
        setFormData({
          name: '',
          email: '',
          phone: '',
          password: '',
          confirmPassword: '',
          role: 'customer',
          businessName: '',
          businessType: 'General',
        });

        if (onSuccess) {
          onSuccess();
        } else {
          onClose();
        }
      } else {
        setErrors({ submit: result.error });
      }
    } catch (error) {
      setErrors({ submit: 'Something went wrong. Please try again.' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleSocialLogin = provider => {
    // Simulate social login
    const email = `demo@${provider}.com`;
    login({
      email,
      name: `Demo ${provider.charAt(0).toUpperCase() + provider.slice(1)} User`,
      role: 'customer',
    }).then(result => {
      if (result.success) {
        onClose();
      }
    });
  };

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContainer onClick={e => e.stopPropagation()}>
        <CloseButton onClick={onClose}>
          <FaTimes />
        </CloseButton>

        <ModalHeader>
          <ModalTitle>
            {activeTab === 'login' ? 'Welcome Back!' : 'Join Our Platform'}
          </ModalTitle>
          <ModalSubtitle>
            {activeTab === 'login'
              ? 'Sign in to continue your journey'
              : 'Create an account to get started'}
          </ModalSubtitle>

          <TabContainer>
            <Tab
              active={activeTab === 'login'}
              onClick={() => setActiveTab('login')}
            >
              Sign In
            </Tab>
            <Tab
              active={activeTab === 'register'}
              onClick={() => setActiveTab('register')}
            >
              Sign Up
            </Tab>
          </TabContainer>
        </ModalHeader>

        <ModalBody>
          <Form onSubmit={handleSubmit}>
            {activeTab === 'register' && (
              <>
                <FormGroup>
                  <Label>I want to:</Label>
                  <RoleSelector>
                    <RoleOption
                      selected={formData.role === 'customer'}
                      onClick={() =>
                        setFormData(prev => ({ ...prev, role: 'customer' }))
                      }
                    >
                      <div className="icon">
                        <FaUser />
                      </div>
                      <div className="title">Buy Products</div>
                      <div className="desc">
                        Browse and enquire about products
                      </div>
                    </RoleOption>
                    <RoleOption
                      selected={formData.role === 'seller'}
                      onClick={() =>
                        setFormData(prev => ({ ...prev, role: 'seller' }))
                      }
                    >
                      <div className="icon">
                        <FaStore />
                      </div>
                      <div className="title">Sell Products</div>
                      <div className="desc">Create your own store</div>
                    </RoleOption>
                  </RoleSelector>
                </FormGroup>

                <FormGroup>
                  <Label>Full Name</Label>
                  <InputWrapper>
                    <InputIcon>
                      <FaUser />
                    </InputIcon>
                    <Input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Enter your full name"
                      error={errors.name}
                    />
                  </InputWrapper>
                  {errors.name && <ErrorMessage>{errors.name}</ErrorMessage>}
                </FormGroup>

                {formData.role === 'seller' && (
                  <>
                    <FormGroup>
                      <Label>Business Name</Label>
                      <InputWrapper>
                        <InputIcon>
                          <FaStore />
                        </InputIcon>
                        <Input
                          type="text"
                          name="businessName"
                          value={formData.businessName}
                          onChange={handleInputChange}
                          placeholder="Enter your business name"
                          error={errors.businessName}
                        />
                      </InputWrapper>
                      {errors.businessName && (
                        <ErrorMessage>{errors.businessName}</ErrorMessage>
                      )}
                    </FormGroup>

                    <FormGroup>
                      <Label>Business Type</Label>
                      <InputWrapper>
                        <InputIcon>
                          <FaStore />
                        </InputIcon>
                        <Select
                          name="businessType"
                          value={formData.businessType}
                          onChange={handleInputChange}
                        >
                          <option value="General">General Store</option>
                          <option value="Electronics">Electronics</option>
                          <option value="Fashion">Fashion & Clothing</option>
                          <option value="Home & Garden">Home & Garden</option>
                          <option value="Sports">Sports & Fitness</option>
                          <option value="Books">Books & Media</option>
                          <option value="Health">Health & Beauty</option>
                          <option value="Food">Food & Beverages</option>
                        </Select>
                      </InputWrapper>
                    </FormGroup>
                  </>
                )}

                <FormGroup>
                  <Label>Phone Number</Label>
                  <InputWrapper>
                    <InputIcon>
                      <FaPhone />
                    </InputIcon>
                    <Input
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      placeholder="Enter your phone number"
                      error={errors.phone}
                    />
                  </InputWrapper>
                  {errors.phone && <ErrorMessage>{errors.phone}</ErrorMessage>}
                </FormGroup>
              </>
            )}

            <FormGroup>
              <Label>Email Address</Label>
              <InputWrapper>
                <InputIcon>
                  <FaEnvelope />
                </InputIcon>
                <Input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="Enter your email address"
                  error={errors.email}
                />
              </InputWrapper>
              {errors.email && <ErrorMessage>{errors.email}</ErrorMessage>}
            </FormGroup>

            <FormGroup>
              <Label>Password</Label>
              <InputWrapper>
                <InputIcon>
                  <FaLock />
                </InputIcon>
                <Input
                  type={showPassword ? 'text' : 'password'}
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Enter your password"
                  error={errors.password}
                />
                <PasswordToggle
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? <FaEyeSlash /> : <FaEye />}
                </PasswordToggle>
              </InputWrapper>
              {errors.password && (
                <ErrorMessage>{errors.password}</ErrorMessage>
              )}
            </FormGroup>

            {activeTab === 'register' && (
              <FormGroup>
                <Label>Confirm Password</Label>
                <InputWrapper>
                  <InputIcon>
                    <FaLock />
                  </InputIcon>
                  <Input
                    type={showPassword ? 'text' : 'password'}
                    name="confirmPassword"
                    value={formData.confirmPassword}
                    onChange={handleInputChange}
                    placeholder="Confirm your password"
                    error={errors.confirmPassword}
                  />
                </InputWrapper>
                {errors.confirmPassword && (
                  <ErrorMessage>{errors.confirmPassword}</ErrorMessage>
                )}
              </FormGroup>
            )}

            {errors.submit && <ErrorMessage>{errors.submit}</ErrorMessage>}

            <SubmitButton type="submit" disabled={isSubmitting || loading}>
              {isSubmitting
                ? 'Please wait...'
                : activeTab === 'login'
                  ? 'Sign In'
                  : 'Create Account'}
            </SubmitButton>
          </Form>

          <Divider>
            <span>or continue with</span>
          </Divider>

          <SocialButtons>
            <SocialButton
              className="google"
              onClick={() => handleSocialLogin('google')}
            >
              <FaGoogle />
              Google
            </SocialButton>
            <SocialButton
              className="facebook"
              onClick={() => handleSocialLogin('facebook')}
            >
              <FaFacebook />
              Facebook
            </SocialButton>
            <SocialButton
              className="github"
              onClick={() => handleSocialLogin('github')}
            >
              <FaGithub />
              GitHub
            </SocialButton>
          </SocialButtons>
        </ModalBody>
      </ModalContainer>
    </ModalOverlay>
  );
};

export default AuthModal;

