const RegisterPage = () => {
  const navigate = useNavigate();
  const [userType, setUserType] = useState('buyer');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const [buyerData, setBuyerData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const [sellerData, setSellerData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    businessName: '',
    businessCategory: '',
    phone: '',
    address: '',
    website: '',
  });

  const handleBuyerInputChange = e => {
    const { name, value } = e.target;
    setBuyerData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSellerInputChange = e => {
    const { name, value } = e.target;
    setSellerData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setIsLoading(true);

    const currentData = userType === 'buyer' ? buyerData : sellerData;

    // Validate passwords match
    if (currentData.password !== currentData.confirmPassword) {
      alert("Passwords don't match!");
      setIsLoading(false);
      return;
    }

    // Simulate registration process
    setTimeout(() => {
      setIsLoading(false);
      // Navigate to appropriate page
      if (userType === 'seller') {
        navigate('/seller-dashboard');
      } else {
        navigate('/');
      }
    }, 1500);
  };

  const categories = [
    { value: '', label: 'Select Business Category' },
    { value: 'hotels', label: 'Hotels & Hospitality' },
    { value: 'ecommerce', label: 'Ecommerce & Retail' },
    { value: 'weddings', label: 'Weddings & Events' },
