const AllRoomsManager = () => {
  const dispatch = useDispatch();
  const { slug } = useParams();
  const { editingHotel } = useSelector(state => state.hotelManagement);

  // Auto-select hotel based on URL slug
  useEffect(() => {
    if (slug && !editingHotel) {
      const hotelData = getHotelByIdOrSlug(slug);
      if (hotelData) {
        dispatch(setEditingHotel(hotelData.id));
      }
    }
  }, [slug, editingHotel, dispatch]);

  const [editingRoom, setEditingRoom] = useState(null);
  const [editData, setEditData] = useState({});

  const handleEditRoom = (room) => {
    setEditingRoom(room.id);
    setEditData({ ...room });
  };

  const handleSaveEdit = () => {
    dispatch(updateRoom({ roomId: editingRoom, updates: editData }));
    setEditingRoom(null);
    setEditData({});
    alert('Room updated successfully!');
  };

  const handleDeleteRoom = (roomId) => {
    if (window.confirm('Are you sure you want to delete this room? This action cannot be undone.')) {
      dispatch(removeRoom(roomId));
      alert('Room deleted successfully!');
    }
  };

  const handleCloseEdit = () => {
    setEditingRoom(null);
    setEditData({});
  };

  const updateEditField = (field, value) => {
    setEditData(prev => ({ ...prev, [field]: value }));
  };

  const updateEditImage = (index, value) => {
    const newImages = [...editData.images];
    newImages[index] = value;
    updateEditField('images', newImages);
  };

  const addEditImage = () => {
    updateEditField('images', [...editData.images, '']);
  };

  const removeEditImage = (index) => {
    const newImages = editData.images.filter((_, i) => i !== index);
    updateEditField('images', newImages);
  };

  const updateEditAmenity = (index, value) => {
    const newAmenities = [...editData.amenities];
    newAmenities[index] = value;
    updateEditField('amenities', newAmenities);
  };

  const addEditAmenity = () => {
    updateEditField('amenities', [...editData.amenities, '']);
  };

  const removeEditAmenity = (index) => {
    const newAmenities = editData.amenities.filter((_, i) => i !== index);
    updateEditField('amenities', newAmenities);
  };

  if (!editingHotel) {
    return (
      <Container>
        <Header>
          <Title>All Rooms</Title>
          <Subtitle>Please select a hotel first to view rooms</Subtitle>
        </Header>
      </Container>
    );
  }

  const rooms = editingHotel.rooms || [];

  return (
    <Container>
      <Header>
        <Title>All Rooms</Title>
        <Subtitle>Manage rooms for {editingHotel.name} ({rooms.length} rooms)</Subtitle>
      </Header>

      {rooms.length === 0 ? (
        <EmptyState>
          <FaBed style={{ fontSize: '4rem', marginBottom: theme.spacing.lg, opacity: 0.5 }} />
          <h3>No rooms added yet</h3>
          <p>Start by adding your first room to this hotel.</p>
        </EmptyState>
      ) : (
        <RoomsGrid>
          {rooms.map(room => (
            <RoomCard key={room.id}>
              <RoomImage src={room.images?.[0] || 'https://images.unsplash.com/photo-1631049307264-da0ec9d70304?ixlib=rb-4.0.3'} />
              <RoomInfo>
                <RoomHeader>
                  <div>
                    <RoomName>{room.name}</RoomName>
                    <RoomType>{room.type}</RoomType>
                  </div>
                  <RoomPrice>{room.price?.toLocaleString()}</RoomPrice>
                </RoomHeader>

                <RoomDetails>
                  <div><FaUsers /> {room.maxGuests} guests</div>
                  <div><FaBed /> {room.bedType}</div>
                </RoomDetails>

                <RoomDescription>{room.description}</RoomDescription>

                {room.offer && (
                  <RoomOffer>
                    <strong>Special Offer:</strong> {room.offer}
                  </RoomOffer>
                )}

                <AmenitiesList>
                  {room.amenities?.map((amenity, index) => (
                    <AmenityTag key={index}>{amenity}</AmenityTag>
                  ))}
                </AmenitiesList>

                <RoomActions>
                  <Button size="small" variant="outline" onClick={() => handleEditRoom(room)}>
                    <FaEdit /> Edit
                  </Button>
                  <Button 
                    size="small" 
                    variant="danger" 
                    onClick={() => handleDeleteRoom(room.id)}
                  >
                    <FaTrash /> Delete
                  </Button>
                </RoomActions>
              </RoomInfo>
            </RoomCard>
          ))}
        </RoomsGrid>
      )}

      {/* Edit Modal */}
      {editingRoom && (
        <EditModal onClick={handleCloseEdit}>
          <EditContent onClick={(e) => e.stopPropagation()}>
            <EditHeader>
              <EditTitle>Edit Room: {editData.name}</EditTitle>
              <CloseButton onClick={handleCloseEdit}>
                <FaTimes />
              </CloseButton>
            </EditHeader>

            <FormGrid>
              <FormField>
                <Label>Room Name</Label>
                <Input
                  value={editData.name || ''}
                  onChange={(e) => updateEditField('name', e.target.value)}
                />
              </FormField>
              <FormField>
                <Label>Room Type</Label>
                <Input
                  value={editData.type || ''}
                  onChange={(e) => updateEditField('type', e.target.value)}
                />
              </FormField>
              <FormField>
                <Label>Price ()</Label>
                <Input
                  type="number"
                  value={editData.price || ''}
                  onChange={(e) => updateEditField('price', parseInt(e.target.value))}
                />
              </FormField>
              <FormField>
                <Label>Max Guests</Label>
                <Input
                  type="number"
                  value={editData.maxGuests || ''}
                  onChange={(e) => updateEditField('maxGuests', parseInt(e.target.value))}
                />
              </FormField>
              <FormField>
                <Label>Bed Type</Label>
                <Input
                  value={editData.bedType || ''}
                  onChange={(e) => updateEditField('bedType', e.target.value)}
                />
              </FormField>
            </FormGrid>

            <FormField style={{ marginBottom: theme.spacing.lg }}>
              <Label>Description</Label>
              <TextArea
                value={editData.description || ''}
                onChange={(e) => updateEditField('description', e.target.value)}
              />
            </FormField>

            <FormField style={{ marginBottom: theme.spacing.lg }}>
              <Label>Special Offers</Label>
              <TextArea
                value={editData.offer || ''}
                onChange={(e) => updateEditField('offer', e.target.value)}
                placeholder="Enter special offers or rewards for this room..."
              />
            </FormField>

            <FormField style={{ marginBottom: theme.spacing.lg }}>
              <Label>Images</Label>
              {editData.images?.map((image, index) => (
                <div key={index} style={{ display: 'flex', gap: theme.spacing.sm, marginBottom: theme.spacing.sm }}>
                  <Input
                    value={image}
                    onChange={(e) => updateEditImage(index, e.target.value)}
                    placeholder="Image URL"
                    style={{ flex: 1 }}
                  />
                  <Button size="small" variant="danger" onClick={() => removeEditImage(index)}>
                    <FaTrash />
                  </Button>
                </div>
              ))}
              <Button size="small" onClick={addEditImage}>
                <FaPlus /> Add Image
              </Button>
            </FormField>

            <FormField style={{ marginBottom: theme.spacing.lg }}>
              <Label>Amenities</Label>
              {editData.amenities?.map((amenity, index) => (
                <div key={index} style={{ display: 'flex', gap: theme.spacing.sm, marginBottom: theme.spacing.sm }}>
                  <Input
                    value={amenity}
                    onChange={(e) => updateEditAmenity(index, e.target.value)}
                    placeholder="Amenity"
                    style={{ flex: 1 }}
                  />
                  <Button size="small" variant="danger" onClick={() => removeEditAmenity(index)}>
                    <FaTrash />
                  </Button>
                </div>
              ))}
              <Button size="small" onClick={addEditAmenity}>
                <FaPlus /> Add Amenity
              </Button>
            </FormField>

            <div style={{ display: 'flex', gap: theme.spacing.md, justifyContent: 'flex-end' }}>
              <Button variant="outline" onClick={handleCloseEdit}>
                Cancel
              </Button>
              <Button onClick={handleSaveEdit}>
                <FaSave /> Save Changes
              </Button>
            </div>
          </EditContent>
        </EditModal>
      )}
    </Container>
  );
};

export default AllRoomsManager;

