const BookingsReceivedPage = () => {
  const { bookings, setBookings, ownerHotels } = useAppContext();

  const ownerBookings = bookings.filter(booking =>
    ownerHotels.some(hotel => hotel.id === booking.hotelId)
  );

  const handleBookingAction = (bookingId, action) => {
    setBookings(prev =>
      prev.map(booking =>
        booking.id === bookingId ? { ...booking, status: action } : booking
      )
    );
  };

  const formatDate = dateString => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
    });
  };

  const getStatusVariant = status => {
    switch (status) {
      case 'confirmed':
        return 'success';
      case 'pending':
        return 'warning';
      case 'rejected':
        return 'error';
      default:
        return 'default';
    }
  };

  if (ownerBookings.length === 0) {
    return (
      <PageContainer>
        <Sidebar />
        <MainContent>
          <PageTitle>Bookings Received</PageTitle>
          <Card>
            <EmptyState>
              <div
                style={{
                  fontSize: '4rem',
                  marginBottom: theme.spacing.lg,
                  opacity: 0.5,
                }}
              >

              </div>
              <h3
                style={{
                  fontSize: '1.5rem',
                  fontWeight: 600,
                  marginBottom: theme.spacing.md,
                  color: theme.colors.gray700,
                }}
              >
                No Bookings Yet
              </h3>
              <p style={{ fontSize: '1.125rem', lineHeight: 1.6 }}>
                You haven't received any booking requests yet. Once customers
                book your rooms, they'll appear here.
              </p>
            </EmptyState>
          </Card>
        </MainContent>
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <Sidebar />

      <MainContent>
        <PageTitle>Bookings Received</PageTitle>

        {/* Desktop Table View */}
        <BookingsTable>
          <TableHeader>
            <div>Guest & Contact</div>
            <div>Hotel & Room</div>
            <div>Check-in</div>
            <div>Check-out</div>
            <div>Status</div>
            <div>Actions</div>
          </TableHeader>

          {ownerBookings.map(booking => (
            <TableRow key={booking.id}>
              <BookingInfo>
                <GuestName>{booking.guestName}</GuestName>
                <ContactInfo>{booking.guestEmail}</ContactInfo>
                <ContactInfo>{booking.guestPhone}</ContactInfo>
              </BookingInfo>
              <div>
                <HotelRoom>
                  <strong>{booking.hotelName}</strong>
                  <br />
                  {booking.roomName}
                </HotelRoom>
              </div>
              <div>{formatDate(booking.checkIn)}</div>
              <div>{formatDate(booking.checkOut)}</div>
              <div>
                <Badge variant={getStatusVariant(booking.status)}>
                  {booking.status.charAt(0).toUpperCase() +
                    booking.status.slice(1)}
                </Badge>
              </div>
              <BookingActions>
                {booking.status === 'pending' && (
                  <>
                    <Button
                      variant="success"
                      size="small"
                      onClick={() =>
                        handleBookingAction(booking.id, 'confirmed')
                      }
                    >
                      <FaCheck />
                    </Button>
                    <Button
                      variant="danger"
                      size="small"
                      onClick={() =>
                        handleBookingAction(booking.id, 'rejected')
                      }
                    >
                      <FaTimes />
                    </Button>
                  </>
                )}
                <Button variant="outline" size="small">
                  <FaEye />
                </Button>
              </BookingActions>
            </TableRow>
          ))}
        </BookingsTable>

        {/* Mobile Card View */}
        <div style={{ display: 'none' }}>
          {ownerBookings.map(booking => (
            <BookingCard key={booking.id}>
              <CardContent>
                <div
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'flex-start',
                    marginBottom: theme.spacing.md,
                  }}
                >
                  <div>
                    <GuestName>{booking.guestName}</GuestName>
                    <ContactInfo>{booking.guestEmail}</ContactInfo>
                    <ContactInfo>{booking.guestPhone}</ContactInfo>
                  </div>
                  <Badge variant={getStatusVariant(booking.status)}>
                    {booking.status.charAt(0).toUpperCase() +
                      booking.status.slice(1)}
                  </Badge>
                </div>

                <HotelRoom style={{ marginBottom: theme.spacing.md }}>
                  <strong>{booking.hotelName}</strong> - {booking.roomName}
                </HotelRoom>

                <div
                  style={{
                    display: 'flex',
                    gap: theme.spacing.lg,
                    marginBottom: theme.spacing.md,
                    fontSize: '0.875rem',
                    color: theme.colors.gray600,
                  }}
                >
                  <div>
                    <FaCalendarAlt style={{ marginRight: theme.spacing.xs }} />
                    {formatDate(booking.checkIn)} -{' '}
                    {formatDate(booking.checkOut)}
                  </div>
                  <div>
                    <FaUsers style={{ marginRight: theme.spacing.xs }} />
                    {booking.guests} guests
                  </div>
                </div>

                {booking.specialRequests && (
                  <div
                    style={{
                      marginBottom: theme.spacing.md,
                      padding: theme.spacing.sm,
                      background: theme.colors.gray50,
                      borderRadius: theme.borderRadius.md,
                      fontSize: '0.875rem',
                    }}
                  >
                    <strong>Special Requests:</strong> {booking.specialRequests}
                  </div>
                )}

                <BookingActions>
                  {booking.status === 'pending' && (
                    <>
                      <Button
                        variant="success"
                        size="small"
                        onClick={() =>
                          handleBookingAction(booking.id, 'confirmed')
                        }
                        style={{ flex: 1 }}
                      >
                        <FaCheck />
                        Confirm
                      </Button>
                      <Button
                        variant="danger"
                        size="small"
                        onClick={() =>
                          handleBookingAction(booking.id, 'rejected')
                        }
                        style={{ flex: 1 }}
                      >
                        <FaTimes />
                        Reject
                      </Button>
                    </>
                  )}
                  <Button variant="outline" size="small">
                    <FaEye />
                    View Details
                  </Button>
                </BookingActions>
              </CardContent>
            </BookingCard>
          ))}
        </div>
      </MainContent>
    </PageContainer>
  );
};

export default BookingsReceivedPage;

