const OwnerDashboard = () => {
  const { ownerHotels, bookings } = useAppContext();
  const [activeSection, setActiveSection] = useState('dashboard');

  const ownerBookings = bookings.filter(booking =>
    ownerHotels.some(hotel => hotel.id === booking.hotelId)
  );

  const stats = {
    totalHotels: ownerHotels.length,
    totalRooms: ownerHotels.reduce((sum, hotel) => sum + hotel.totalRooms, 0),
    totalBookings: ownerBookings.length,
    pendingBookings: ownerBookings.filter(b => b.status === 'pending').length,
  };

  const recentBookings = ownerBookings.slice(0, 5);

  const quickActions = [
    {
      title: 'Add New Hotel',
      description: 'Register a new hotel property with rooms and amenities',
      icon: FaHotel,
      action: () => setActiveSection('add-hotel'),
    },
    {
      title: 'Edit Hotel Details',
      description:
        'Edit your hotel page content exactly as it appears to customers',
      icon: FaEdit,
      action: () => setActiveSection('my-hotels'),
    },
    {
      title: 'Add Rooms',
      description: 'Add new rooms to your existing hotel properties',
      icon: FaBed,
      action: () => setActiveSection('my-hotels'),
    },
    {
      title: 'Manage Bookings',
      description: 'Review and manage all incoming booking requests',
      icon: FaCalendarCheck,
      action: () => setActiveSection('bookings'),
    },
  ];

  const formatDate = dateString => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      month: 'short',
      day: 'numeric',
    });
  };

  const getStatusVariant = status => {
    switch (status) {
      case 'confirmed':
        return 'success';
      case 'pending':
        return 'warning';
      case 'cancelled':
        return 'error';
      case 'rejected':
        return 'error';
      default:
        return 'default';
    }
  };

  const renderContent = () => {
    switch (activeSection) {
      case 'add-hotel':
        return (
          <PageContent>
            <AddHotelPageContent />
          </PageContent>
        );
      case 'my-hotels':
        return (
          <SectionBasedHotelEditor setActiveSection={setActiveSection} />
        );
      case 'add-room':
        return (
          <AddRoomManager setActiveSection={setActiveSection} />
        );
      case 'all-rooms':
        return (
          <AllRoomsManager setActiveSection={setActiveSection} />
        );
      case 'bookings':
        return (
          <PageContent>
            <BookingsReceivedPageContent />
          </PageContent>
        );
      case 'profile':
        return (
          <PageContent>
            <ProfileSettingsPageContent />
          </PageContent>
        );
      default:
        return (
          <>
            <DashboardHeader>
              <WelcomeTitle>Welcome Back!</WelcomeTitle>
              <WelcomeSubtitle>
                Here's what's happening with your hotels today
              </WelcomeSubtitle>
            </DashboardHeader>

            <StatsGrid>
              <StatCard>
                <CardContent>
                  <StatIcon
                    iconBg={`${theme.colors.primary}20`}
                    iconColor={theme.colors.primary}
                  >
                    <FaHotel />
                  </StatIcon>
                  <StatValue>{stats.totalHotels}</StatValue>
                  <StatLabel>Total Hotels</StatLabel>
                </CardContent>
              </StatCard>

              <StatCard>
                <CardContent>
                  <StatIcon
                    iconBg={`${theme.colors.success}20`}
                    iconColor={theme.colors.success}
                  >
                    <FaBed />
                  </StatIcon>
                  <StatValue>{stats.totalRooms}</StatValue>
                  <StatLabel>Total Rooms</StatLabel>
                </CardContent>
              </StatCard>

              <StatCard>
                <CardContent>
                  <StatIcon
                    iconBg={`${theme.colors.accent}20`}
                    iconColor={theme.colors.accent}
                  >
                    <FaCalendarCheck />
                  </StatIcon>
                  <StatValue>{stats.totalBookings}</StatValue>
                  <StatLabel>Total Bookings</StatLabel>
                </CardContent>
              </StatCard>

              <StatCard>
                <CardContent>
                  <StatIcon
                    iconBg={`${theme.colors.warning}20`}
                    iconColor={theme.colors.warning}
                  >
                    <FaEye />
                  </StatIcon>
                  <StatValue>{stats.pendingBookings}</StatValue>
                  <StatLabel>Pending Bookings</StatLabel>
                </CardContent>
              </StatCard>
            </StatsGrid>

            <SectionTitle>Quick Actions</SectionTitle>
            <QuickActionsGrid>
              {quickActions.map(action => (
                <ActionCard key={action.title} onClick={action.action}>
                  <CardContent>
                    <ActionIcon>
                      <action.icon />
                    </ActionIcon>
                    <ActionTitle>{action.title}</ActionTitle>
                    <ActionDescription>{action.description}</ActionDescription>
                    <Button variant="secondary">
                      <FaPlus />
                      Get Started
                    </Button>
                  </CardContent>
                </ActionCard>
              ))}
            </QuickActionsGrid>

            <RecentBookingsSection>
              <SectionTitle>Recent Bookings</SectionTitle>
              <BookingsTable>
                <TableHeader>
                  <div>Guest & Hotel</div>
                  <div>Check-in</div>
                  <div>Check-out</div>
                  <div>Status</div>
                  <div>Actions</div>
                </TableHeader>

                {recentBookings.length > 0 ? (
                  recentBookings.map(booking => (
                    <TableRow key={booking.id}>
                      <BookingInfo>
                        <GuestName>{booking.guestName}</GuestName>
                        <HotelRoom>
                          {booking.hotelName} - {booking.roomName}
                        </HotelRoom>
                      </BookingInfo>
                      <div className="desktop-only">
                        {formatDate(booking.checkIn)}
                      </div>
                      <div className="desktop-only">
                        {formatDate(booking.checkOut)}
                      </div>
                      <div className="desktop-only">
                        <Badge variant={getStatusVariant(booking.status)}>
                          {booking.status.charAt(0).toUpperCase() +
                            booking.status.slice(1)}
                        </Badge>
                      </div>
                      <div className="desktop-only">
                        <Button variant="outline" size="small">
                          <FaEye />
                          View
                        </Button>
                      </div>

                      <MobileBookingDetails>
                        <div className="detail">
                          <div className="label">Check-in</div>
                          <div className="value">
                            {formatDate(booking.checkIn)}
                          </div>
                        </div>
                        <div className="detail">
                          <div className="label">Check-out</div>
                          <div className="value">
                            {formatDate(booking.checkOut)}
                          </div>
                        </div>
                        <div className="detail">
                          <div className="label">Status</div>
                          <div className="value">
                            <Badge variant={getStatusVariant(booking.status)}>
                              {booking.status.charAt(0).toUpperCase() +
                                booking.status.slice(1)}
                            </Badge>
                          </div>
                        </div>
                      </MobileBookingDetails>
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <div
                      style={{
                        gridColumn: '1 / -1',
                        textAlign: 'center',
                        color: theme.colors.gray600,
                        padding: theme.spacing.xxl,
                      }}
                    >
                      No bookings yet. Start by adding your first hotel!
                    </div>
                  </TableRow>
                )}
              </BookingsTable>
            </RecentBookingsSection>
          </>
        );
    }
  };

  return (
    <DashboardContainer>
      <EnhancedSidebar
        activeSection={activeSection}
        setActiveSection={setActiveSection}
      />
      <MainContent>{renderContent()}</MainContent>
    </DashboardContainer>
  );
};

export default OwnerDashboard;

