const ProfileSettingsPage = () => {
  const navigate = useNavigate();
  const { user, setUser, setUserType } = useAppContext();

  const [profileData, setProfileData] = useState({
    name: user?.name || 'Hotel Owner',
    email: user?.email || 'owner@example.com',
    phone: '+91 9876543210',
    licenseNumber: 'HL123456789',
  });

  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
  });

  const [isUpdating, setIsUpdating] = useState(false);

  const handleProfileChange = e => {
    setProfileData({
      ...profileData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePasswordChange = e => {
    setPasswordData({
      ...passwordData,
      [e.target.name]: e.target.value,
    });
  };

  const handleProfileSubmit = async e => {
    e.preventDefault();
    setIsUpdating(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Update user context
      setUser(prev => ({
        ...prev,
        name: profileData.name,
        email: profileData.email,
      }));

      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Error updating profile. Please try again.');
    } finally {
      setIsUpdating(false);
    }
  };

  const handlePasswordSubmit = async e => {
    e.preventDefault();

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert('New passwords do not match!');
      return;
    }

    setIsUpdating(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      alert('Password updated successfully!');
      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
      });
    } catch (error) {
      console.error('Error updating password:', error);
      alert('Error updating password. Please try again.');
    } finally {
      setIsUpdating(false);
    }
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      setUser(null);
      setUserType('customer');
      navigate('/');
    }
  };

  return (
    <PageContainer>
      <Sidebar />

      <MainContent>
        <PageTitle>Profile Settings</PageTitle>

        <SettingsGrid>
          <Card>
            <CardContent>
              <SectionTitle>
                <FaUser />
                Personal Information
              </SectionTitle>

              <ProfileForm onSubmit={handleProfileSubmit}>
                <InputGroup>
                  <FormGroup>
                    <Label>Full Name *</Label>
                    <Input
                      type="text"
                      name="name"
                      value={profileData.name}
                      onChange={handleProfileChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label>Email Address *</Label>
                    <Input
                      type="email"
                      name="email"
                      value={profileData.email}
                      onChange={handleProfileChange}
                      required
                    />
                  </FormGroup>
                </InputGroup>

                <InputGroup>
                  <FormGroup>
                    <Label>Phone Number</Label>
                    <Input
                      type="tel"
                      name="phone"
                      value={profileData.phone}
                      onChange={handleProfileChange}
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label>Hotel License Number</Label>
                    <Input
                      type="text"
                      name="licenseNumber"
                      value={profileData.licenseNumber}
                      onChange={handleProfileChange}
                      placeholder="Optional"
                    />
                  </FormGroup>
                </InputGroup>

                <FormActions>
                  <Button type="submit" disabled={isUpdating} size="large">
                    <FaSave />
                    {isUpdating ? 'Updating...' : 'Save Changes'}
                  </Button>
                </FormActions>
              </ProfileForm>
            </CardContent>
          </Card>

          <Card>
            <CardContent>
              <SectionTitle>
                <FaLock />
                Change Password
              </SectionTitle>

              <ProfileForm onSubmit={handlePasswordSubmit}>
                <FormGroup>
                  <Label>Current Password *</Label>
                  <Input
                    type="password"
                    name="currentPassword"
                    value={passwordData.currentPassword}
                    onChange={handlePasswordChange}
                    required
                  />
                </FormGroup>

                <InputGroup>
                  <FormGroup>
                    <Label>New Password *</Label>
                    <Input
                      type="password"
                      name="newPassword"
                      value={passwordData.newPassword}
                      onChange={handlePasswordChange}
                      required
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label>Confirm New Password *</Label>
                    <Input
                      type="password"
                      name="confirmPassword"
                      value={passwordData.confirmPassword}
                      onChange={handlePasswordChange}
                      required
                    />
                  </FormGroup>
                </InputGroup>

                <FormActions>
                  <Button type="submit" disabled={isUpdating} size="large">
                    <FaLock />
                    {isUpdating ? 'Updating...' : 'Update Password'}
                  </Button>
                </FormActions>
              </ProfileForm>
            </CardContent>
          </Card>

          <Card>
            <CardContent>
              <SectionTitle>
                <FaSignOutAlt />
                Account Actions
              </SectionTitle>

              <LogoutSection>
                <LogoutText>
                  Ready to sign out? Click the button below to logout from your
                  account.
                </LogoutText>
                <Button variant="danger" onClick={handleLogout} size="large">
                  <FaSignOutAlt />
                  Logout
                </Button>
              </LogoutSection>
            </CardContent>
          </Card>
        </SettingsGrid>
      </MainContent>
    </PageContainer>
  );
};

