      return;
    }

    setIsUpdating(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      alert('Password updated successfully!');
      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
      });
    } catch (error) {
      console.error('Error updating password:', error);
      alert('Error updating password. Please try again.');
    } finally {
      setIsUpdating(false);
    }
  };



  const handleDeleteAccount = () => {
    setShowDeleteModal(true);
  };

  const confirmDeleteAccount = () => {
    if (deleteConfirmation.toLowerCase() === 'delete account') {
      alert('Account deleted successfully. You will be redirected to the homepage.');
      setUser(null);
      setUserType(null);
      navigate('/');
    } else {
      alert('Please type "DELETE ACCOUNT" exactly to confirm deletion.');
    }
  };

  const cancelDeleteAccount = () => {
    setShowDeleteModal(false);
    setDeleteConfirmation('');
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      setUser(null);
      setUserType('customer');
      navigate('/');
    }
  };

  return (
    <>
      <PageTitle>Profile Settings</PageTitle>

      <SettingsGrid>
        <Card>
          <CardContent>
            <SectionTitle>
              <FaUser />
              Personal Information
            </SectionTitle>

            <ProfileForm onSubmit={handleProfileSubmit}>
              <InputGroup>
                <FormGroup>
                  <Label>Full Name *</Label>
                  <Input
                    type="text"
                    name="name"
                    value={profileData.name}
                    onChange={handleProfileChange}
                    required
                  />
                </FormGroup>
                <FormGroup>
                  <Label>Email Address *</Label>
                  <Input
                    type="email"
                    name="email"
                    value={profileData.email}
                    onChange={handleProfileChange}
                    required
                  />
                </FormGroup>
              </InputGroup>

              <InputGroup>
                <FormGroup>
                  <Label>Phone Number</Label>
                  <Input
                    type="tel"
                    name="phone"
                    value={profileData.phone}
                    onChange={handleProfileChange}
                  />
                </FormGroup>
                <FormGroup>
                  <Label>Hotel License Number</Label>
                  <Input
                    type="text"
                    name="licenseNumber"
                    value={profileData.licenseNumber}
                    onChange={handleProfileChange}
                    placeholder="Optional"
                  />
                </FormGroup>
              </InputGroup>

              <FormActions>
                <Button type="submit" disabled={isUpdating} size="large">
                  <FaSave />
                  {isUpdating ? 'Updating...' : 'Save Changes'}
                </Button>
              </FormActions>
            </ProfileForm>
          </CardContent>
        </Card>

        <Card>
          <CardContent>
            <SectionTitle>
              <FaLock />
              Change Password
            </SectionTitle>

            <ProfileForm onSubmit={handlePasswordSubmit}>
              <FormGroup>
                <Label>Current Password *</Label>
                <Input
                  type="password"
                  name="currentPassword"
                  value={passwordData.currentPassword}
                  onChange={handlePasswordChange}
                  required
                />
              </FormGroup>

              <InputGroup>
                <FormGroup>
                  <Label>New Password *</Label>
                  <Input
                    type="password"
                    name="newPassword"
                    value={passwordData.newPassword}
                    onChange={handlePasswordChange}
                    required
                  />
                </FormGroup>
                <FormGroup>
                  <Label>Confirm New Password *</Label>
                  <Input
                    type="password"
                    name="confirmPassword"
                    value={passwordData.confirmPassword}
                    onChange={handlePasswordChange}
                    required
                  />
                </FormGroup>
              </InputGroup>

              <FormActions>
                <Button type="submit" disabled={isUpdating} size="large">
                  <FaLock />
                  {isUpdating ? 'Updating...' : 'Update Password'}
                </Button>
              </FormActions>
            </ProfileForm>
          </CardContent>
        </Card>

        <Card>
          <CardContent>
            <SectionTitle>
              <FaSignOutAlt />
              Account Actions
            </SectionTitle>

            <LogoutSection>
              <LogoutText>
                Ready to sign out? Click the button below to logout from your
                account.
              </LogoutText>
              <div style={{ display: 'flex', gap: theme.spacing.md, justifyContent: 'center' }}>
                <Button variant="danger" onClick={handleLogout} size="large">
                  <FaSignOutAlt />
                  Logout
                </Button>
                <Button variant="error" onClick={handleDeleteAccount} size="large">
                  <FaTrashAlt />
                  Delete Account
                </Button>
              </div>
            </LogoutSection>
          </CardContent>
        </Card>
      </SettingsGrid>

      {/* Delete Account Confirmation Modal */}
      {showDeleteModal && (
        <DeleteModal onClick={cancelDeleteAccount}>
          <ModalContent onClick={(e) => e.stopPropagation()}>
            <ModalIcon>
              <FaExclamationTriangle />
            </ModalIcon>
            <ModalTitle>Delete Account</ModalTitle>
            <ModalText>
              <strong>This action cannot be undone!</strong>
              <br /><br />
              Deleting your account will permanently remove:
              <br />
               All your hotel data and information
              <br />
               All room listings and details
              <br />
               All booking history
              <br />
               Your personal profile and settings
              <br /><br />
              To confirm deletion, please type <strong>"DELETE ACCOUNT"</strong> below:
            </ModalText>
            <ConfirmationInput
              type="text"
              value={deleteConfirmation}
              onChange={(e) => setDeleteConfirmation(e.target.value)}
              placeholder="Type DELETE ACCOUNT to confirm"
            />
            <ModalActions>
              <Button variant="outline" onClick={cancelDeleteAccount} size="large">
                Cancel
              </Button>
              <Button
                variant="error"
                onClick={confirmDeleteAccount}
                size="large"
                disabled={deleteConfirmation.toLowerCase() !== 'delete account'}
              >
                <FaTrashAlt />
                Delete Account
              </Button>
            </ModalActions>
          </ModalContent>
        </DeleteModal>
      )}
    </>
  );
};

export default ProfileSettingsPageContent;

