        </Overlay>
      )}

      {/* Save & Go Live Bar - appears when there are unsaved changes */}
      {hasUnsavedChanges && (
        <SaveGoLiveBar>
          <ChangesIndicator>
            <FaEdit />
            {Object.keys(hasUnsavedChanges ? changes : {}).length} unsaved
