const Sidebar = ({ activeSection, setActiveSection }) => {
  const location = useLocation();
  const [isOpen, setIsOpen] = useState(false);

  // Extract hotel slug from URL path like "/taj-palace/owner"
  const hotelSlug = useMemo(() => {
    const pathSegments = location.pathname.split('/').filter(Boolean);
    return pathSegments.length > 0 ? pathSegments[0] : '';
  }, [location.pathname]);

  const isActive = sectionKey => activeSection === sectionKey;

  const navigationItems = useMemo(
    () => [
      {
        section: 'Dashboard',
        items: [
          {
            key: 'dashboard',
            icon: FaHome,
            label: 'Dashboard Home',
            action: () => setActiveSection('dashboard'),
          },
        ],
      },
      {
        section: 'Hotel Management',
        items: [
          {
            key: 'add-hotel',
            icon: FaPlus,
            label: 'Add Hotel',
            action: () => setActiveSection('add-hotel'),
          },
          {
            key: 'my-hotels',
            icon: FaHotel,
            label: 'My Hotels',
            action: () => setActiveSection('my-hotels'),
          },
        ],
      },
      {
        section: 'Room Management',
        items: [
          {
            key: 'add-room',
            icon: FaBed,
            label: 'Add Rooms',
            action: () => setActiveSection('add-room'),
          },
        ],
      },
      {
        section: 'Bookings',
        items: [
          {
            key: 'bookings',
            icon: FaCalendarCheck,
            label: 'Bookings Received',
            action: () => setActiveSection('bookings'),
          },
        ],
      },
      {
        section: 'Account',
        items: [
          {
            key: 'profile',
            icon: FaUser,
            label: 'Profile Settings',
            action: () => setActiveSection('profile'),
          },
        ],
      },
    ],
    [setActiveSection]
  );

  const toggleSidebar = () => setIsOpen(!isOpen);
  const closeSidebar = () => setIsOpen(false);

  const handleNavClick = action => {
    action();
    closeSidebar();
  };

  return (
    <>
      <MobileToggle onClick={toggleSidebar}>
        {isOpen ? <FaTimes /> : <FaBars />}
      </MobileToggle>

      <Overlay isOpen={isOpen} onClick={closeSidebar} />

      <SidebarContainer isOpen={isOpen}>
        <SidebarHeader>
          <Logo>
            <FaHotel />
            StayEase
          </Logo>
        </SidebarHeader>

        <SidebarNav>
          {navigationItems.map(section => (
            <NavSection key={section.section}>
              <SectionTitle>{section.section}</SectionTitle>
              {section.items.map(item => (
                <NavItem
                  key={item.key}
                  active={isActive(item.key)}
                  onClick={() => handleNavClick(item.action)}
                >
                  <NavIcon>
                    <item.icon />
                  </NavIcon>
                  {item.label}
                </NavItem>
              ))}
            </NavSection>
          ))}

          <NavSection>
            <NavLink to={`/${hotelSlug}`} onClick={closeSidebar}>
              <NavIcon>
                <FaSignOutAlt />
              </NavIcon>
              Back to Customer View
            </NavLink>
          </NavSection>
        </SidebarNav>
      </SidebarContainer>
    </>
  );
};

export default Sidebar;

