const Header = ({ isOwnerView = false }) => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isInHero, setIsInHero] = useState(false);
  const dropdownRef = useRef(null);

  const closeMobileMenu = () => setMobileMenuOpen(false);
  const toggleDropdown = () => setDropdownOpen(!dropdownOpen);
  const closeDropdown = () => setDropdownOpen(false);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = event => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        closeDropdown();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Handle escape key to close mobile menu and dropdown
  useEffect(() => {
    const handleEscapeKey = event => {
      if (event.key === 'Escape') {
        if (mobileMenuOpen) {
          closeMobileMenu();
        }
        if (dropdownOpen) {
          closeDropdown();
        }
      }
    };

    document.addEventListener('keydown', handleEscapeKey);
    return () => {
      document.removeEventListener('keydown', handleEscapeKey);
    };
  }, [mobileMenuOpen, dropdownOpen]);

  // Prevent body scroll when mobile menu is open
  useEffect(() => {
    if (mobileMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }

    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [mobileMenuOpen]);

  // Handle scroll detection for navbar transparency and hero detection
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;

      // Check if scrolled
      setIsScrolled(scrollPosition > 50);

      // Check if in hero section (approximately first 75% of viewport height)
      const heroHeight = windowHeight * 0.75;
      setIsInHero(scrollPosition < heroHeight);
    };

    handleScroll(); // Call once on mount
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleDropdownItemClick = () => {
    closeDropdown();
    closeMobileMenu();
  };

  return (
    <>
      <MobileMenuOverlay isOpen={mobileMenuOpen} onClick={closeMobileMenu} />
      <HeaderContainer isScrolled={isScrolled} isInHero={isInHero}>
        <HeaderContent>
          <Logo
            to={isOwnerView ? '/' : '/'}
            onClick={closeMobileMenu}
            isScrolled={isScrolled}
            isInHero={isInHero}
          >
            <FaHotel />
            StoreBuilder
          </Logo>

          {/* Desktop Navigation */}
          <DesktopNav>
            {isOwnerView ? (
              <>
                <NavLink to="/" isScrolled={isScrolled}>
                  Dashboard
                </NavLink>
                <NavLink to="/owner/my-hotels" isScrolled={isScrolled}>
                  My Hotels
                </NavLink>
                <NavLink to="/owner/bookings" isScrolled={isScrolled}>
                  Bookings
                </NavLink>
                <NavLink to="/owner/profile" isScrolled={isScrolled}>
                  Profile
                </NavLink>
              </>
            ) : (
              <>
                <NavLink to="/" isScrolled={isScrolled}>
                  Home
                </NavLink>
                <NavLink to="/pricing" isScrolled={isScrolled}>
                  Pricing
                </NavLink>
                <DropdownContainer ref={dropdownRef}>
                  <DropdownButton
                    onClick={toggleDropdown}
                    isOpen={dropdownOpen}
                    isScrolled={isScrolled}
                    aria-expanded={dropdownOpen}
                    aria-haspopup="true"
                    aria-label="Explore Stores menu"
                  >
                    Explore Stores
                    <FaChevronDown />
                  </DropdownButton>
                  <DropdownMenu isOpen={dropdownOpen} role="menu">
                    <DropdownItem
                      to="/hotels"
                      onClick={handleDropdownItemClick}
                      role="menuitem"
                    >
                       Hotels
                    </DropdownItem>
                    <DropdownItem
                      to="/ecommerce"
                      onClick={handleDropdownItemClick}
                      role="menuitem"
                    >
                       Ecommerce
                    </DropdownItem>
                    <DropdownItem
                      to="/weddings"
                      onClick={handleDropdownItemClick}
                      role="menuitem"
                    >
                       Weddings
                    </DropdownItem>
                    <DropdownItem
                      to="/automobiles"
                      onClick={handleDropdownItemClick}
                      role="menuitem"
                    >
                       Automobiles
                    </DropdownItem>
                  </DropdownMenu>
                </DropdownContainer>
              </>
            )}
          </DesktopNav>

          {/* Mobile Navigation */}
          <MobileNav isOpen={mobileMenuOpen}>
            {isOwnerView ? (
              <>
                <NavLink to="/" onClick={closeMobileMenu} isMobile={true}>
                  Dashboard
                </NavLink>
                <NavLink
                  to="/owner/my-hotels"
                  onClick={closeMobileMenu}
                  isMobile={true}
                >
                  My Hotels
                </NavLink>
                <NavLink
                  to="/owner/bookings"
                  onClick={closeMobileMenu}
                  isMobile={true}
                >
                  Bookings
                </NavLink>
                <NavLink
                  to="/owner/profile"
                  onClick={closeMobileMenu}
                  isMobile={true}
                >
                  Profile
                </NavLink>
              </>
            ) : (
              <>
                <NavLink to="/" onClick={closeMobileMenu} isMobile={true}>
                  Home
                </NavLink>
                <NavLink
                  to="/pricing"
                  onClick={closeMobileMenu}
                  isMobile={true}
                >
                  Pricing
                </NavLink>
                <DropdownContainer isMobile={true}>
                  <DropdownButton
                    onClick={toggleDropdown}
                    isOpen={dropdownOpen}
                    isMobile={true}
                    aria-expanded={dropdownOpen}
                    aria-haspopup="true"
                    aria-label="Explore Stores menu"
                  >
                    Explore Stores
                    <FaChevronDown />
                  </DropdownButton>
                  <DropdownMenu
                    isOpen={dropdownOpen}
                    isMobile={true}
                    role="menu"
                  >
                    <DropdownItem
                      to="/hotels"
                      onClick={handleDropdownItemClick}
                      role="menuitem"
                    >
                       Hotels
                    </DropdownItem>
                    <DropdownItem
                      to="/ecommerce"
                      onClick={handleDropdownItemClick}
                      role="menuitem"
                    >
                       Ecommerce
                    </DropdownItem>
                    <DropdownItem
                      to="/weddings"
                      onClick={handleDropdownItemClick}
                      role="menuitem"
                    >
                       Weddings
                    </DropdownItem>
                    <DropdownItem
                      to="/automobiles"
                      onClick={handleDropdownItemClick}
                      role="menuitem"
                    >
                       Automobiles
                    </DropdownItem>
                  </DropdownMenu>
                </DropdownContainer>
              </>
            )}

            <MobileAuthButtons>
              <MobileAuthButton
                to="/login"
                className="login"
                onClick={closeMobileMenu}
              >
                Login
              </MobileAuthButton>
              <MobileAuthButton
                to="/register"
                className="register"
                onClick={closeMobileMenu}
              >
                Register
              </MobileAuthButton>
            </MobileAuthButtons>
          </MobileNav>

          <UserSection>
            <LoginButton
              to="/login"
              onClick={closeMobileMenu}
              isScrolled={isScrolled}
            >
              Login
            </LoginButton>
            <RegisterButton
              as={Link}
              to="/register"
              onClick={closeMobileMenu}
              isScrolled={isScrolled}
              style={{ textDecoration: 'none' }}
            >
              Register
            </RegisterButton>

            <MobileMenuButton
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              aria-label={mobileMenuOpen ? 'Close menu' : 'Open menu'}
              aria-expanded={mobileMenuOpen}
              isScrolled={isScrolled}
              isInHero={isInHero}
            >
              {mobileMenuOpen ? <FaTimes /> : <FaBars />}
            </MobileMenuButton>
          </UserSection>
        </HeaderContent>
      </HeaderContainer>
    </>
  );
};

export default Header;

