const UserProfile = ({ onClose }) => {
  const { user, updateProfile, switchRole } = useAuth();
  const [activeTab, setActiveTab] = useState('personal');
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.name || '',
    email: user?.email || '',
    phone: user?.phone || '',
    bio: user?.profile?.bio || '',
    location: user?.profile?.location || '',
    website: user?.profile?.website || '',
    socialLinks: {
      linkedin: user?.profile?.socialLinks?.linkedin || '',
      twitter: user?.profile?.socialLinks?.twitter || '',
      instagram: user?.profile?.socialLinks?.instagram || '',
      facebook: user?.profile?.socialLinks?.facebook || '',
    },
    preferences: {
      notifications: {
        email: user?.preferences?.notifications?.email ?? true,
        sms: user?.preferences?.notifications?.sms ?? false,
        push: user?.preferences?.notifications?.push ?? true,
      },
      language: user?.preferences?.language || 'en',
      currency: user?.preferences?.currency || 'USD',
    },
  });

  const handleInputChange = e => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value,
        },
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleToggle = (section, setting) => {
    setFormData(prev => ({
      ...prev,
      preferences: {
        ...prev.preferences,
        [section]: {
          ...prev.preferences[section],
          [setting]: !prev.preferences[section][setting],
        },
      },
    }));
  };

  const handleSave = async () => {
    const result = await updateProfile({
      name: formData.name,
      phone: formData.phone,
      profile: {
        bio: formData.bio,
        location: formData.location,
        website: formData.website,
        socialLinks: formData.socialLinks,
      },
      preferences: formData.preferences,
    });

    if (result.success) {
      setIsEditing(false);
      // Show success message
    }
  };

  const getUserStats = () => {
    if (user?.role === 'seller') {
      return [
        { number: user.seller?.totalProducts || 0, label: 'Products' },
        { number: user.seller?.totalSales || 0, label: 'Sales' },
        { number: user.seller?.rating || 0, label: 'Rating' },
      ];
    } else {
      return [
        {
          number: JSON.parse(localStorage.getItem('userEnquiries') || '[]')
            .length,
          label: 'Enquiries',
        },
        {
          number: JSON.parse(localStorage.getItem('recentlyViewed') || '[]')
            .length,
          label: 'Viewed',
        },
        { number: '4.8', label: 'Rating' },
      ];
    }
  };

  if (!user) return null;

  return (
    <ProfileContainer>
      <ProfileHeader>
        <AvatarContainer>
          <Avatar src={user.avatar} alt={user.name} />
          {isEditing && (
            <AvatarUpload>
              <FaCamera />
              <input type="file" accept="image/*" />
            </AvatarUpload>
          )}
        </AvatarContainer>

        <UserInfo>
          <UserName>{user.name}</UserName>
          <UserRole>
            {user.role === 'seller' ? <FaStore /> : <FaUser />}
            {user.role.charAt(0).toUpperCase() + user.role.slice(1)}
            {user.seller?.verified && '  Verified'}
          </UserRole>
          <UserStats>
            {getUserStats().map((stat, index) => (
              <StatItem key={index}>
                <div className="number">{stat.number}</div>
                <div className="label">{stat.label}</div>
              </StatItem>
            ))}
          </UserStats>
        </UserInfo>
      </ProfileHeader>

      <TabContainer>
        <Tab
          active={activeTab === 'personal'}
          onClick={() => setActiveTab('personal')}
        >
          Personal Info
        </Tab>
        <Tab
          active={activeTab === 'preferences'}
          onClick={() => setActiveTab('preferences')}
        >
          Preferences
        </Tab>
        <Tab
          active={activeTab === 'security'}
          onClick={() => setActiveTab('security')}
        >
          Security
        </Tab>
        {user.role === 'customer' && (
          <Tab
            active={activeTab === 'seller'}
            onClick={() => setActiveTab('seller')}
          >
            Become Seller
          </Tab>
        )}
      </TabContainer>

      <TabContent active={activeTab === 'personal'}>
        <FormSection>
          <SectionTitle>
            <FaUser />
            Basic Information
          </SectionTitle>
          <FormGrid>
            <FormGroup>
              <Label>Full Name</Label>
              <Input
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                disabled={!isEditing}
              />
            </FormGroup>
            <FormGroup>
              <Label>Email Address</Label>
              <Input name="email" value={formData.email} disabled />
            </FormGroup>
            <FormGroup>
              <Label>Phone Number</Label>
              <Input
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
                disabled={!isEditing}
              />
            </FormGroup>
            <FormGroup>
              <Label>Location</Label>
              <Input
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                disabled={!isEditing}
                placeholder="City, Country"
              />
            </FormGroup>
          </FormGrid>
        </FormSection>

        <FormSection>
          <SectionTitle>
            <FaGlobe />
            About & Links
          </SectionTitle>
          <FormGroup>
            <Label>Bio</Label>
            <TextArea
              name="bio"
              value={formData.bio}
              onChange={handleInputChange}
              disabled={!isEditing}
              placeholder="Tell us about yourself..."
            />
          </FormGroup>
          <FormGroup>
            <Label>Website</Label>
            <Input
              name="website"
              value={formData.website}
              onChange={handleInputChange}
              disabled={!isEditing}
              placeholder="https://yourwebsite.com"
            />
          </FormGroup>
          <Label>Social Links</Label>
          <SocialLinksGrid>
            <SocialInput>
              <div className="icon">
                <FaLinkedin />
              </div>
              <input
                name="socialLinks.linkedin"
                value={formData.socialLinks.linkedin}
                onChange={handleInputChange}
                disabled={!isEditing}
                placeholder="LinkedIn profile URL"
              />
            </SocialInput>
            <SocialInput>
              <div className="icon">
                <FaTwitter />
              </div>
              <input
                name="socialLinks.twitter"
                value={formData.socialLinks.twitter}
                onChange={handleInputChange}
                disabled={!isEditing}
                placeholder="Twitter profile URL"
              />
            </SocialInput>
            <SocialInput>
              <div className="icon">
                <FaInstagram />
              </div>
              <input
                name="socialLinks.instagram"
                value={formData.socialLinks.instagram}
                onChange={handleInputChange}
                disabled={!isEditing}
                placeholder="Instagram profile URL"
              />
            </SocialInput>
            <SocialInput>
              <div className="icon">
                <FaFacebook />
              </div>
              <input
                name="socialLinks.facebook"
                value={formData.socialLinks.facebook}
                onChange={handleInputChange}
                disabled={!isEditing}
                placeholder="Facebook profile URL"
              />
            </SocialInput>
          </SocialLinksGrid>
        </FormSection>
      </TabContent>

      <TabContent active={activeTab === 'preferences'}>
        <FormSection>
          <SectionTitle>
            <FaBell />
            Notifications
          </SectionTitle>
          <div
            style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}
          >
            <ToggleSwitch
              onClick={() => handleToggle('notifications', 'email')}
            >
              <ToggleLabel>
                <div className="title">Email Notifications</div>
                <div className="desc">Receive updates via email</div>
              </ToggleLabel>
              <ToggleIcon enabled={formData.preferences.notifications.email}>
                {formData.preferences.notifications.email ? (
                  <FaToggleOn />
                ) : (
                  <FaToggleOff />
                )}
              </ToggleIcon>
            </ToggleSwitch>

            <ToggleSwitch onClick={() => handleToggle('notifications', 'sms')}>
              <ToggleLabel>
                <div className="title">SMS Notifications</div>
                <div className="desc">Receive updates via SMS</div>
              </ToggleLabel>
              <ToggleIcon enabled={formData.preferences.notifications.sms}>
                {formData.preferences.notifications.sms ? (
                  <FaToggleOn />
                ) : (
                  <FaToggleOff />
                )}
              </ToggleIcon>
            </ToggleSwitch>

            <ToggleSwitch onClick={() => handleToggle('notifications', 'push')}>
              <ToggleLabel>
                <div className="title">Push Notifications</div>
                <div className="desc">Receive browser notifications</div>
              </ToggleLabel>
              <ToggleIcon enabled={formData.preferences.notifications.push}>
                {formData.preferences.notifications.push ? (
                  <FaToggleOn />
                ) : (
                  <FaToggleOff />
                )}
              </ToggleIcon>
            </ToggleSwitch>
          </div>
        </FormSection>

        <FormSection>
          <SectionTitle>
            <FaGlobe />
            Regional Settings
          </SectionTitle>
          <FormGrid>
            <FormGroup>
              <Label>Language</Label>
              <Select
                name="preferences.language"
                value={formData.preferences.language}
                onChange={handleInputChange}
                disabled={!isEditing}
              >
                <option value="en">English</option>
                <option value="es">Espaol</option>
                <option value="fr">Franais</option>
                <option value="de">Deutsch</option>
                <option value="it">Italiano</option>
                <option value="pt">Portugus</option>
              </Select>
            </FormGroup>
            <FormGroup>
              <Label>Currency</Label>
              <Select
                name="preferences.currency"
                value={formData.preferences.currency}
                onChange={handleInputChange}
                disabled={!isEditing}
              >
                <option value="USD">USD ($)</option>
                <option value="EUR">EUR ()</option>
                <option value="GBP">GBP ()</option>
                <option value="CAD">CAD ($)</option>
                <option value="AUD">AUD ($)</option>
                <option value="JPY">JPY ()</option>
              </Select>
            </FormGroup>
          </FormGrid>
        </FormSection>
      </TabContent>

      <TabContent active={activeTab === 'security'}>
        <FormSection>
          <SectionTitle>
            <FaLock />
            Security Settings
          </SectionTitle>
          <p style={{ color: '#6b7280', marginBottom: '2rem' }}>
            Security features are coming soon. For now, you can update your
            basic information above.
          </p>
        </FormSection>
      </TabContent>

      {user.role === 'customer' && (
        <TabContent active={activeTab === 'seller'}>
          <FormSection>
            <SectionTitle>
              <FaStore />
              Become a Seller
            </SectionTitle>
            <p style={{ color: '#6b7280', marginBottom: '2rem' }}>
              Join thousands of sellers on our platform and start selling your
              products today!
            </p>
            <PrimaryButton onClick={() => switchRole('seller')}>
              <FaStore />
              Switch to Seller Account
            </PrimaryButton>
          </FormSection>
        </TabContent>
      )}

      <ButtonGroup>
        {isEditing ? (
          <>
            <SecondaryButton onClick={() => setIsEditing(false)}>
              Cancel
            </SecondaryButton>
            <PrimaryButton onClick={handleSave}>
              <FaSave />
              Save Changes
            </PrimaryButton>
          </>
        ) : (
          <PrimaryButton onClick={() => setIsEditing(true)}>
            <FaEdit />
            Edit Profile
          </PrimaryButton>
        )}
      </ButtonGroup>
    </ProfileContainer>
  );
};

