const CategoriesTab = () => {
  const [categories, setCategories] = useState([
    {
      id: 1,
      name: 'Electronics',
      description: 'Digital devices and gadgets',
      image:
        'https://images.unsplash.com/photo-1498049794561-7780e7231661?w=100&q=80',
      productCount: 45,
      isActive: true,
      sortOrder: 1,
    },
    {
      id: 2,
      name: 'Fashion',
      description: 'Clothing and accessories',
      image: null,
      productCount: 32,
      isActive: true,
      sortOrder: 2,
    },
    {
      id: 3,
      name: 'Home & Garden',
      description: 'Home improvement and gardening',
      image:
        'https://images.unsplash.com/photo-1586023492125-27b2c045efd7?w=100&q=80',
      productCount: 28,
      isActive: true,
      sortOrder: 3,
    },
  ]);

  const [showModal, setShowModal] = useState(false);
  const [editingCategory, setEditingCategory] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    image: null,
  });

  const handleAddCategory = () => {
    setEditingCategory(null);
    setFormData({ name: '', description: '', image: null });
    setShowModal(true);
  };

  const handleEditCategory = category => {
    setEditingCategory(category);
    setFormData({
      name: category.name,
      description: category.description,
      image: category.image,
    });
    setShowModal(true);
  };

  const handleDeleteCategory = categoryId => {
    if (window.confirm('Are you sure you want to delete this category?')) {
      setCategories(categories.filter(cat => cat.id !== categoryId));
    }
  };

  const handleSubmit = e => {
    e.preventDefault();

    if (editingCategory) {
      // Update existing category
      setCategories(
        categories.map(cat =>
          cat.id === editingCategory.id ? { ...cat, ...formData } : cat
        )
      );
    } else {
      // Add new category
      const newCategory = {
        id: Date.now(),
        ...formData,
        productCount: 0,
        isActive: true,
        sortOrder: categories.length + 1,
      };
      setCategories([...categories, newCategory]);
    }

    setShowModal(false);
  };

  const handleImageUpload = e => {
    const file = e.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setFormData(prev => ({ ...prev, image: imageUrl }));
    }
  };

  const modalFooter = (
    <>
      <Button variant="ghost" onClick={() => setShowModal(false)}>
        Cancel
      </Button>
      <Button variant="primary" onClick={handleSubmit}>
        {editingCategory ? 'Update Category' : 'Add Category'}
      </Button>
    </>
  );

  return (
    <>
      <CategoriesContainer>
        <CategoriesHeader>
          <HeaderLeft>
            <HeaderTitle>Categories</HeaderTitle>
            <HeaderSubtitle>
              Organize your products into categories
            </HeaderSubtitle>
          </HeaderLeft>

          <Button variant="primary" onClick={handleAddCategory}>
            <FaPlus />
            Add Category
          </Button>
        </CategoriesHeader>

        <CategoriesList>
          {categories.length === 0 ? (
            <EmptyState>
              <EmptyIcon>
                <FaFolder />
              </EmptyIcon>
              <EmptyTitle>No categories yet</EmptyTitle>
              <EmptyText>
                Create your first category to organize your products
              </EmptyText>
              <Button variant="primary" onClick={handleAddCategory}>
                <FaPlus />
                Add Your First Category
              </Button>
            </EmptyState>
          ) : (
            categories.map(category => (
              <CategoryItem key={category.id}>
                <DragHandle>
                  <FaGripVertical />
                </DragHandle>

                {category.image ? (
                  <CategoryImage src={category.image} alt={category.name} />
                ) : (
                  <CategoryIcon>
                    <FaFolder />
                  </CategoryIcon>
                )}

                <CategoryInfo>
                  <CategoryName>{category.name}</CategoryName>
                  <CategoryDescription>
                    {category.description}
                  </CategoryDescription>
                </CategoryInfo>

                <CategoryStats>
                  <StatItem>
                    <StatValue>{category.productCount}</StatValue>
                    <StatLabel>Products</StatLabel>
                  </StatItem>
                </CategoryStats>

                <ActionButtons>
                  <ActionButton onClick={() => handleEditCategory(category)}>
                    <FaEdit />
                  </ActionButton>
                  <ActionButton
                    className="danger"
                    onClick={() => handleDeleteCategory(category.id)}
                  >
                    <FaTrash />
                  </ActionButton>
                </ActionButtons>
              </CategoryItem>
            ))
          )}
        </CategoriesList>
      </CategoriesContainer>

      <Modal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        title={editingCategory ? 'Edit Category' : 'Add New Category'}
        footer={modalFooter}
      >
        <form onSubmit={handleSubmit}>
          <FormField
            label="Category Name"
            name="name"
            value={formData.name}
            onChange={e =>
              setFormData(prev => ({ ...prev, name: e.target.value }))
            }
            placeholder="Enter category name"
            required
          />

          <FormField
            label="Description"
            type="textarea"
            name="description"
            value={formData.description}
            onChange={e =>
              setFormData(prev => ({ ...prev, description: e.target.value }))
            }
            placeholder="Describe this category"
            rows={3}
          />

          <FormField label="Category Image">
            <ImageUploadArea
              onClick={() =>
                document.getElementById('categoryImageInput').click()
              }
            >
              {formData.image ? (
                <img
                  src={formData.image}
                  alt="Preview"
                  style={{
                    width: '100px',
                    height: '100px',
                    objectFit: 'cover',
                    borderRadius: '8px',
                  }}
                />
              ) : (
                <>
                  <FaImage
                    style={{
                      fontSize: '2rem',
                      color: theme.colors.gray400,
                      marginBottom: '8px',
                    }}
                  />
                  <p style={{ margin: 0, color: theme.colors.gray600 }}>
                    Click to upload image
                  </p>
                </>
              )}
            </ImageUploadArea>
            <HiddenFileInput
              id="categoryImageInput"
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
            />
          </FormField>
        </form>
      </Modal>
    </>
  );
};

