const DiscountsTab = () => {
  const [discounts, setDiscounts] = useState([
    {
      id: 1,
      code: 'WELCOME20',
      description: 'Welcome discount for new customers',
      type: 'percentage',
      value: 20,
      minOrderAmount: 50,
      maxUsagePerCustomer: 1,
      totalUsage: 245,
      maxUsage: 1000,
      startDate: '2024-01-01',
      endDate: '2024-12-31',
      isActive: true,
    },
    {
      id: 2,
      code: 'SAVE10',
      description: 'Fixed discount for orders above $100',
      type: 'fixed',
      value: 10,
      minOrderAmount: 100,
      maxUsagePerCustomer: 3,
      totalUsage: 89,
      maxUsage: 500,
      startDate: '2024-01-15',
      endDate: '2024-03-15',
      isActive: true,
    },
    {
      id: 3,
      code: 'EXPIRED50',
      description: 'Old promotional discount',
      type: 'percentage',
      value: 50,
      minOrderAmount: 0,
      maxUsagePerCustomer: 1,
      totalUsage: 156,
      maxUsage: 200,
      startDate: '2023-12-01',
      endDate: '2023-12-31',
      isActive: false,
    },
  ]);

  const [showModal, setShowModal] = useState(false);
  const [editingDiscount, setEditingDiscount] = useState(null);
  const [formData, setFormData] = useState({
    code: '',
    description: '',
    type: 'percentage',
    value: '',
    minOrderAmount: '',
    maxUsagePerCustomer: '',
    maxUsage: '',
    startDate: '',
    endDate: '',
  });

  const handleAddDiscount = () => {
    setEditingDiscount(null);
    setFormData({
      code: '',
      description: '',
      type: 'percentage',
      value: '',
      minOrderAmount: '',
      maxUsagePerCustomer: '',
      maxUsage: '',
      startDate: '',
      endDate: '',
    });
    setShowModal(true);
  };

  const handleEditDiscount = discount => {
    setEditingDiscount(discount);
    setFormData({
      code: discount.code,
      description: discount.description,
      type: discount.type,
      value: discount.value.toString(),
      minOrderAmount: discount.minOrderAmount.toString(),
      maxUsagePerCustomer: discount.maxUsagePerCustomer.toString(),
      maxUsage: discount.maxUsage.toString(),
      startDate: discount.startDate,
      endDate: discount.endDate,
    });
    setShowModal(true);
  };

  const handleDeleteDiscount = discountId => {
    if (window.confirm('Are you sure you want to delete this discount?')) {
      setDiscounts(discounts.filter(d => d.id !== discountId));
    }
  };

  const handleToggleStatus = discountId => {
    setDiscounts(
      discounts.map(d =>
        d.id === discountId ? { ...d, isActive: !d.isActive } : d
      )
    );
  };

  const handleCopyCode = code => {
    navigator.clipboard.writeText(code);
    alert(`Coupon code "${code}" copied to clipboard!`);
  };

  const handleSubmit = e => {
    e.preventDefault();

    const discountData = {
      ...formData,
      value: parseFloat(formData.value),
      minOrderAmount: parseFloat(formData.minOrderAmount) || 0,
      maxUsagePerCustomer: parseInt(formData.maxUsagePerCustomer) || 1,
      maxUsage: parseInt(formData.maxUsage) || 1000,
    };

    if (editingDiscount) {
      setDiscounts(
        discounts.map(d =>
          d.id === editingDiscount.id ? { ...d, ...discountData } : d
        )
      );
    } else {
      const newDiscount = {
        id: Date.now(),
        ...discountData,
        totalUsage: 0,
        isActive: true,
      };
      setDiscounts([...discounts, newDiscount]);
    }

    setShowModal(false);
  };

  const formatCurrency = value => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(value);
  };

  const formatDate = dateString => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const modalFooter = (
    <>
      <Button variant="ghost" onClick={() => setShowModal(false)}>
        Cancel
      </Button>
      <Button variant="primary" onClick={handleSubmit}>
        {editingDiscount ? 'Update Discount' : 'Create Discount'}
      </Button>
    </>
  );

  return (
    <>
      <DiscountsContainer>
        <DiscountsHeader>
          <HeaderLeft>
            <HeaderTitle>Discounts & Coupons</HeaderTitle>
            <HeaderSubtitle>
              Create and manage discount codes for your customers
            </HeaderSubtitle>
          </HeaderLeft>

          <Button variant="primary" onClick={handleAddDiscount}>
            <FaPlus />
            Create Discount
          </Button>
        </DiscountsHeader>

        <DiscountsList>
          {discounts.length === 0 ? (
            <EmptyState>
              <EmptyIcon>
                <FaPercent />
              </EmptyIcon>
              <h3>No discounts created yet</h3>
              <p>
                Create your first discount to start offering deals to your
                customers
              </p>
              <Button variant="primary" onClick={handleAddDiscount}>
                <FaPlus />
                Create Your First Discount
              </Button>
            </EmptyState>
          ) : (
            discounts.map(discount => (
              <DiscountCard key={discount.id}>
                <DiscountHeader>
                  <DiscountInfo>
                    <DiscountCode>
                      {discount.code}
                      <CopyButton onClick={() => handleCopyCode(discount.code)}>
                        <FaCopy />
                      </CopyButton>
                    </DiscountCode>
                    <DiscountDescription>
                      {discount.description}
                    </DiscountDescription>
                  </DiscountInfo>

                  <DiscountActions>
                    <StatusToggle
                      isActive={discount.isActive}
                      onClick={() => handleToggleStatus(discount.id)}
                    >
                      {discount.isActive ? <FaToggleOn /> : <FaToggleOff />}
                      {discount.isActive ? 'Active' : 'Inactive'}
                    </StatusToggle>

                    <ActionButton onClick={() => handleEditDiscount(discount)}>
                      <FaEdit />
                    </ActionButton>

                    <ActionButton
                      className="danger"
                      onClick={() => handleDeleteDiscount(discount.id)}
                    >
                      <FaTrash />
                    </ActionButton>
                  </DiscountActions>
                </DiscountHeader>

                <DiscountDetails>
                  <DetailItem>
                    <DetailIcon>
                      {discount.type === 'percentage' ? (
                        <FaPercent />
                      ) : (
                        <FaDollarSign />
                      )}
                    </DetailIcon>
                    <DetailContent>
                      <DetailLabel>Discount Value</DetailLabel>
                      <DetailValue>
                        {discount.type === 'percentage'
                          ? `${discount.value}%`
                          : formatCurrency(discount.value)}
                      </DetailValue>
                    </DetailContent>
                  </DetailItem>

                  <DetailItem>
                    <DetailIcon>
                      <FaDollarSign />
                    </DetailIcon>
                    <DetailContent>
                      <DetailLabel>Min Order Amount</DetailLabel>
                      <DetailValue>
                        {formatCurrency(discount.minOrderAmount)}
                      </DetailValue>
                    </DetailContent>
                  </DetailItem>

                  <DetailItem>
                    <DetailIcon>
                      <FaUsers />
                    </DetailIcon>
                    <DetailContent>
                      <DetailLabel>Usage</DetailLabel>
                      <DetailValue>
                        {discount.totalUsage} / {discount.maxUsage}
                      </DetailValue>
                    </DetailContent>
                  </DetailItem>

                  <DetailItem>
                    <DetailIcon>
                      <FaCalendarAlt />
                    </DetailIcon>
                    <DetailContent>
                      <DetailLabel>Valid Period</DetailLabel>
                      <DetailValue>
                        {formatDate(discount.startDate)} -{' '}
                        {formatDate(discount.endDate)}
                      </DetailValue>
                    </DetailContent>
                  </DetailItem>
                </DiscountDetails>
              </DiscountCard>
            ))
          )}
        </DiscountsList>
      </DiscountsContainer>

      <Modal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        title={editingDiscount ? 'Edit Discount' : 'Create New Discount'}
        footer={modalFooter}
        size="lg"
      >
        <form onSubmit={handleSubmit}>
          <FormRow>
            <FormField
              label="Coupon Code"
              name="code"
              value={formData.code}
              onChange={e =>
                setFormData(prev => ({
                  ...prev,
                  code: e.target.value.toUpperCase(),
                }))
              }
              placeholder="e.g., SAVE20"
              required
            />

            <FormField
              label="Discount Type"
              type="select"
              name="type"
              value={formData.type}
              onChange={e =>
                setFormData(prev => ({ ...prev, type: e.target.value }))
              }
              options={[
                { value: 'percentage', label: 'Percentage' },
                { value: 'fixed', label: 'Fixed Amount' },
              ]}
              required
            />
          </FormRow>

          <FormField
            label="Description"
            type="textarea"
            name="description"
            value={formData.description}
            onChange={e =>
              setFormData(prev => ({ ...prev, description: e.target.value }))
            }
            placeholder="Describe this discount offer"
            rows={2}
          />

          <FormRow>
            <FormField
              label={`Discount Value (${formData.type === 'percentage' ? '%' : '$'})`}
              type="number"
              name="value"
              value={formData.value}
              onChange={e =>
                setFormData(prev => ({ ...prev, value: e.target.value }))
              }
              placeholder={formData.type === 'percentage' ? '20' : '10.00'}
              min="0"
              max={formData.type === 'percentage' ? '100' : undefined}
              step={formData.type === 'percentage' ? '1' : '0.01'}
              required
            />

            <FormField
              label="Minimum Order Amount ($)"
              type="number"
              name="minOrderAmount"
              value={formData.minOrderAmount}
              onChange={e =>
                setFormData(prev => ({
                  ...prev,
                  minOrderAmount: e.target.value,
                }))
              }
              placeholder="0.00"
              min="0"
              step="0.01"
            />
          </FormRow>

          <FormRow>
            <FormField
              label="Max Usage Per Customer"
              type="number"
              name="maxUsagePerCustomer"
              value={formData.maxUsagePerCustomer}
              onChange={e =>
                setFormData(prev => ({
                  ...prev,
                  maxUsagePerCustomer: e.target.value,
                }))
              }
              placeholder="1"
              min="1"
            />

            <FormField
              label="Total Usage Limit"
              type="number"
              name="maxUsage"
              value={formData.maxUsage}
              onChange={e =>
                setFormData(prev => ({ ...prev, maxUsage: e.target.value }))
              }
              placeholder="1000"
              min="1"
            />
          </FormRow>

          <FormRow>
            <FormField
              label="Start Date"
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={e =>
                setFormData(prev => ({ ...prev, startDate: e.target.value }))
              }
              required
            />

            <FormField
              label="End Date"
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={e =>
                setFormData(prev => ({ ...prev, endDate: e.target.value }))
              }
              required
            />
          </FormRow>
        </form>
      </Modal>
    </>
  );
};

export default DiscountsTab;

