const EnquiryModal = ({ isOpen, onClose, product, userInfo = {} }) => {
  const { showSuccessNotification, showEnquiryNotification } =
    useNotifications();
  const { user, isAuthenticated } = useAuth();

  // Use authenticated user data if available, otherwise use passed userInfo
  const userData = isAuthenticated ? user : userInfo;

  const [formData, setFormData] = useState({
    name: userData.name || '',
    phone: userData.phone || '',
    email: userData.email || '',
    message: '',
    agreeToTerms: false,
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [errors, setErrors] = useState({});

  const handleInputChange = e => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Phone number is required';
    } else if (!/^\+?[\d\s\-()]{10,}$/.test(formData.phone.trim())) {
      newErrors.phone = 'Please enter a valid phone number';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (!formData.agreeToTerms) {
      newErrors.agreeToTerms = 'You must agree to the terms';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async e => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Here you would typically send the enquiry to your backend
      const enquiryData = {
        ...formData,
        productId: product.id,
        productName: product.name,
        productPrice: product.price,
        timestamp: new Date().toISOString(),
        status: 'pending',
      };

      // Store in localStorage for demo (in real app, this would be in your database)
      const existingEnquiries = JSON.parse(
        localStorage.getItem('userEnquiries') || '[]'
      );
      const newEnquiry = {
        id: Date.now(),
        ...enquiryData,
      };
      localStorage.setItem(
        'userEnquiries',
        JSON.stringify([...existingEnquiries, newEnquiry])
      );

      // Show success notification
      showSuccessNotification(
        'Enquiry Sent Successfully!',
        `Your enquiry for ${product.name} has been sent to the seller.`
      );

      // Trigger enquiry notification for seller (simulated)
      showEnquiryNotification(newEnquiry);

      setIsSuccess(true);
    } catch (error) {
      alert('Failed to submit enquiry. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClose = () => {
    if (isSuccess) {
      setIsSuccess(false);
      setFormData({
        name: userInfo.name || '',
        phone: userInfo.phone || '',
        email: userInfo.email || '',
        message: '',
        agreeToTerms: false,
      });
      setErrors({});
    }
    onClose();
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay onClick={handleClose}>
      <ModalContainer onClick={e => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>
            {isSuccess ? 'Enquiry Submitted!' : 'Product Enquiry'}
          </ModalTitle>
          <CloseButton onClick={handleClose}>
            <FaTimes />
          </CloseButton>
        </ModalHeader>

        <ModalBody>
          {isSuccess ? (
            <SuccessMessage>
              <SuccessIcon>
                <FaCheck />
              </SuccessIcon>
              <SuccessTitle>Enquiry Sent Successfully!</SuccessTitle>
              <SuccessText>
                Thank you for your interest in {product.name}. The seller will
                contact you soon via phone or email.
              </SuccessText>
              <SubmitButton onClick={handleClose}>
                Continue Shopping
              </SubmitButton>
            </SuccessMessage>
          ) : (
            <>
              <ProductInfo>
                <ProductImage src={product.image} alt={product.name} />
                <ProductDetails>
                  <ProductName>{product.name}</ProductName>
                  <ProductPrice>${product.price}</ProductPrice>
                </ProductDetails>
              </ProductInfo>

              <Form onSubmit={handleSubmit}>
                <FormGroup>
                  <Label>
                    <FaUser />
                    Full Name *
                  </Label>
                  <InputWrapper>
                    <InputIcon>
                      <FaUser />
                    </InputIcon>
                    <Input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Enter your full name"
                      style={{
                        borderColor: errors.name
                          ? theme.colors.error
                          : undefined,
                      }}
                    />
                  </InputWrapper>
                  {errors.name && (
                    <span
                      style={{ color: theme.colors.error, fontSize: '0.9rem' }}
                    >
                      {errors.name}
                    </span>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label>
                    <FaPhone />
                    Phone Number *
                  </Label>
                  <InputWrapper>
                    <InputIcon>
                      <FaPhone />
                    </InputIcon>
                    <Input
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      placeholder="Enter your phone number"
                      style={{
                        borderColor: errors.phone
                          ? theme.colors.error
                          : undefined,
                      }}
                    />
                  </InputWrapper>
                  {errors.phone && (
                    <span
                      style={{ color: theme.colors.error, fontSize: '0.9rem' }}
                    >
                      {errors.phone}
                    </span>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label>
                    <FaEnvelope />
                    Email Address *
                  </Label>
                  <InputWrapper>
                    <InputIcon>
                      <FaEnvelope />
                    </InputIcon>
                    <Input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      placeholder="Enter your email address"
                      style={{
                        borderColor: errors.email
                          ? theme.colors.error
                          : undefined,
                      }}
                    />
                  </InputWrapper>
                  {errors.email && (
                    <span
                      style={{ color: theme.colors.error, fontSize: '0.9rem' }}
                    >
                      {errors.email}
                    </span>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label>
                    <FaComment />
                    Message (Optional)
                  </Label>
                  <InputWrapper>
                    <InputIcon>
                      <FaComment />
                    </InputIcon>
                    <TextArea
                      name="message"
                      value={formData.message}
                      onChange={handleInputChange}
                      placeholder="Tell us about your requirements, quantity needed, or any specific questions..."
                    />
                  </InputWrapper>
                </FormGroup>

                <CheckboxWrapper>
                  <Checkbox
                    type="checkbox"
                    id="agreeToTerms"
                    name="agreeToTerms"
                    checked={formData.agreeToTerms}
                    onChange={handleInputChange}
                  />
                  <CheckboxLabel htmlFor="agreeToTerms">
                    I agree to share my contact information with the seller and
                    receive communications about this product. By submitting
                    this enquiry, I consent to the processing of my personal
                    data.
                  </CheckboxLabel>
                </CheckboxWrapper>
                {errors.agreeToTerms && (
                  <span
                    style={{ color: theme.colors.error, fontSize: '0.9rem' }}
                  >
                    {errors.agreeToTerms}
                  </span>
                )}

                <ButtonGroup>
                  <CancelButton type="button" onClick={handleClose}>
                    Cancel
                  </CancelButton>
                  <SubmitButton type="submit" disabled={isSubmitting}>
                    {isSubmitting ? 'Sending...' : 'Send Enquiry'}
                  </SubmitButton>
                </ButtonGroup>
              </Form>
            </>
          )}
        </ModalBody>
      </ModalContainer>
    </ModalOverlay>
  );
};

export default EnquiryModal;

