    if (showAuthModal) {
      setIsMenuOpen(false);
    }
  }, [showAuthModal]);

  const handleSearch = e => {
    e.preventDefault();
    if (searchQuery.trim()) {
      const baseUrl = storeSlug ? `/${storeSlug}` : '/ecommerce';
      navigate(
        `${baseUrl}/products?search=${encodeURIComponent(searchQuery.trim())}`
      );
      setSearchQuery('');
      setIsMenuOpen(false);
    }
  };

  const handleMobileSearch = e => {
    e.preventDefault();
    if (e.target.value.trim()) {
      navigate(
