        <MobileNavLink
          to={`${getBaseUrl()}/products?category=electronics`}
          onClick={() => setIsMenuOpen(false)}
        >
          Electronics
        </MobileNavLink>
        <MobileNavLink
          to={`${getBaseUrl()}/products?category=fashion`}
          onClick={() => setIsMenuOpen(false)}
        >
          Fashion
