        <MobileNavLink
          to="/ecommerce/products?category=home-garden"
          onClick={() => setIsMenuOpen(false)}
        >
          Home & Garden
        </MobileNavLink>
        <MobileNavLink
          to="/ecommerce/products?category=sports"
          onClick={() => setIsMenuOpen(false)}
        >
          Sports
        </MobileNavLink>

        {isAuthenticated ? (
          <>
            <MobileNavLink
              to="#"
              onClick={() => {
                setShowProfile(true);
                setIsMenuOpen(false);
              }}
            >
               My Profile
            </MobileNavLink>

            <MobileNavLink
              to={`${getBaseUrl()}/my-enquiries`}
              onClick={() => setIsMenuOpen(false)}
            >
               My Enquiries
            </MobileNavLink>

            <MobileNavLink
              to="#"
              onClick={() => {
                setShowProfile(true);
                setIsMenuOpen(false);
              }}
            >
               My Profile
            </MobileNavLink>

            {canAccessSeller() && (
              <MobileNavLink
                to={`${getBaseUrl()}/seller-dashboard`}
                onClick={() => setIsMenuOpen(false)}
