const OrdersTabEnhanced = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [orders, setOrders] = useState(sellerOrders);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showOrderModal, setShowOrderModal] = useState(false);
  const [trackingInfo, setTrackingInfo] = useState({
    trackingNumber: '',
    carrier: 'ups',
    notes: '',
  });

  const formatCurrency = value => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(value);
  };

  const formatDate = dateString => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const getStatusIcon = status => {
    switch (status) {
      case 'completed':
        return <FaCheck />;
      case 'shipped':
        return <FaTruck />;
      case 'processing':
        return <FaClock />;
      case 'pending':
        return <FaClock />;
      case 'cancelled':
        return <FaTimes />;
      default:
        return <FaClock />;
    }
  };

  const getStatusText = status => {
    switch (status) {
      case 'completed':
        return 'Completed';
      case 'shipped':
        return 'Shipped';
      case 'processing':
        return 'Processing';
      case 'pending':
        return 'Pending';
      case 'cancelled':
        return 'Cancelled';
      default:
        return status;
    }
  };

  const filteredOrders = orders.filter(order => {
    const matchesSearch =
      order.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.customerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.customerEmail.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus =
      statusFilter === 'all' || order.status === statusFilter;

    return matchesSearch && matchesStatus;
  });

  const statusCounts = {
    all: orders.length,
    pending: orders.filter(o => o.status === 'pending').length,
    processing: orders.filter(o => o.status === 'processing').length,
    shipped: orders.filter(o => o.status === 'shipped').length,
    completed: orders.filter(o => o.status === 'completed').length,
    cancelled: orders.filter(o => o.status === 'cancelled').length,
  };

  const handleViewOrder = order => {
    setSelectedOrder(order);
    setShowOrderModal(true);
  };

  const handleUpdateStatus = (orderId, newStatus) => {
    setOrders(
      orders.map(order =>
        order.id === orderId ? { ...order, status: newStatus } : order
      )
    );
  };

  const handleAddTracking = () => {
    if (selectedOrder && trackingInfo.trackingNumber) {
      setOrders(
        orders.map(order =>
          order.id === selectedOrder.id
            ? {
                ...order,
                status: 'shipped',
                trackingNumber: trackingInfo.trackingNumber,
                carrier: trackingInfo.carrier,
                trackingNotes: trackingInfo.notes,
              }
            : order
        )
      );
      setTrackingInfo({ trackingNumber: '', carrier: 'ups', notes: '' });
      alert('Tracking information added successfully!');
    }
  };

  const handlePrintInvoice = order => {
    alert(`Printing invoice for order ${order.id}`);
  };

  const handleExportOrders = () => {
    const csv = [
      ['Order ID', 'Customer', 'Total', 'Status', 'Date'].join(','),
      ...filteredOrders.map(order =>
        [
          order.id,
          order.customerName,
          order.total,
          order.status,
          order.orderDate,
        ].join(',')
      ),
    ].join('\n');

    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'orders.csv';
    a.click();
  };

  const orderDetailsFooter = (
    <>
      <Button variant="ghost" onClick={() => setShowOrderModal(false)}>
        Close
      </Button>
      <Button
        variant="outline"
        onClick={() => handlePrintInvoice(selectedOrder)}
      >
        <FaPrint />
        Print Invoice
      </Button>
    </>
  );

  return (
    <>
      <OrdersContainer>
        <OrdersHeader>
          <HeaderLeft>
            <HeaderTitle>Orders</HeaderTitle>
            <HeaderSubtitle>Track and manage customer orders</HeaderSubtitle>
          </HeaderLeft>

          <HeaderActions>
            <SearchBox>
              <SearchIcon />
              <SearchInput
                type="text"
                placeholder="Search orders..."
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
              />
            </SearchBox>

            <Button variant="outline" onClick={() => {}}>
              <FaFilter />
              Filter
            </Button>

            <Button variant="primary" onClick={handleExportOrders}>
              <FaDownload />
              Export
            </Button>
          </HeaderActions>
        </OrdersHeader>

        <FilterTabs>
          <FilterTab
            active={statusFilter === 'all'}
            onClick={() => setStatusFilter('all')}
          >
            All ({statusCounts.all})
          </FilterTab>
          <FilterTab
            active={statusFilter === 'pending'}
            onClick={() => setStatusFilter('pending')}
          >
            Pending ({statusCounts.pending})
          </FilterTab>
          <FilterTab
            active={statusFilter === 'processing'}
            onClick={() => setStatusFilter('processing')}
          >
            Processing ({statusCounts.processing})
          </FilterTab>
          <FilterTab
            active={statusFilter === 'shipped'}
            onClick={() => setStatusFilter('shipped')}
          >
            Shipped ({statusCounts.shipped})
          </FilterTab>
          <FilterTab
            active={statusFilter === 'completed'}
            onClick={() => setStatusFilter('completed')}
          >
            Completed ({statusCounts.completed})
          </FilterTab>
          <FilterTab
            active={statusFilter === 'cancelled'}
            onClick={() => setStatusFilter('cancelled')}
          >
            Cancelled ({statusCounts.cancelled})
          </FilterTab>
        </FilterTabs>

        <OrdersTable>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHeaderCell>Order ID</TableHeaderCell>
                <TableHeaderCell>Customer</TableHeaderCell>
                <TableHeaderCell>Products</TableHeaderCell>
                <TableHeaderCell>Total</TableHeaderCell>
                <TableHeaderCell>Status</TableHeaderCell>
                <TableHeaderCell>Date</TableHeaderCell>
                <TableHeaderCell>Actions</TableHeaderCell>
              </TableRow>
            </TableHeader>
            <tbody>
              {filteredOrders.map(order => (
                <TableRow key={order.id}>
                  <TableCell>
                    <OrderId>{order.id}</OrderId>
                  </TableCell>
                  <TableCell>
                    <CustomerInfo>
                      <CustomerName>{order.customerName}</CustomerName>
                      <CustomerEmail>{order.customerEmail}</CustomerEmail>
                    </CustomerInfo>
                  </TableCell>
                  <TableCell>
                    <ProductsList>
                      {order.products.map((product, index) => (
                        <ProductItem key={index}>
                          {product.quantity}x {product.name}
                        </ProductItem>
                      ))}
                    </ProductsList>
                  </TableCell>
                  <TableCell>
                    <TotalAmount>{formatCurrency(order.total)}</TotalAmount>
                  </TableCell>
                  <TableCell>
                    <StatusBadge status={order.status}>
                      {getStatusIcon(order.status)}
                      {getStatusText(order.status)}
                    </StatusBadge>
                  </TableCell>
                  <TableCell>{formatDate(order.orderDate)}</TableCell>
                  <TableCell>
                    <ActionButtons>
                      <ActionButton onClick={() => handleViewOrder(order)}>
                        <FaEye />
                      </ActionButton>
                      <ActionButton onClick={() => handlePrintInvoice(order)}>
                        <FaFileInvoiceDollar />
                      </ActionButton>
                    </ActionButtons>
                  </TableCell>
                </TableRow>
              ))}
            </tbody>
          </Table>
        </OrdersTable>
      </OrdersContainer>

      {/* Order Details Modal */}
      <Modal
        isOpen={showOrderModal}
        onClose={() => setShowOrderModal(false)}
        title={`Order Details - ${selectedOrder?.id}`}
        footer={orderDetailsFooter}
        size="lg"
      >
        {selectedOrder && (
          <OrderDetailsModal>
            <DetailSection>
              <DetailTitle>Order Information</DetailTitle>
              <DetailGrid>
                <DetailItem>
                  <DetailLabel>Order ID</DetailLabel>
                  <DetailValue>{selectedOrder.id}</DetailValue>
                </DetailItem>
                <DetailItem>
                  <DetailLabel>Order Date</DetailLabel>
                  <DetailValue>
                    {formatDate(selectedOrder.orderDate)}
                  </DetailValue>
                </DetailItem>
                <DetailItem>
                  <DetailLabel>Order Status</DetailLabel>
                  <DetailValue>
                    <StatusBadge status={selectedOrder.status}>
                      {getStatusIcon(selectedOrder.status)}
                      {getStatusText(selectedOrder.status)}
                    </StatusBadge>
                  </DetailValue>
                </DetailItem>
                <DetailItem>
                  <DetailLabel>Payment Status</DetailLabel>
                  <DetailValue>{selectedOrder.paymentStatus}</DetailValue>
                </DetailItem>
              </DetailGrid>
            </DetailSection>

            <DetailSection>
              <DetailTitle>Customer Information</DetailTitle>
              <DetailGrid>
                <DetailItem>
                  <DetailLabel>Customer Name</DetailLabel>
                  <DetailValue>{selectedOrder.customerName}</DetailValue>
                </DetailItem>
                <DetailItem>
                  <DetailLabel>Email</DetailLabel>
                  <DetailValue>{selectedOrder.customerEmail}</DetailValue>
                </DetailItem>
                <DetailItem>
                  <DetailLabel>Shipping Address</DetailLabel>
                  <DetailValue>{selectedOrder.shippingAddress}</DetailValue>
                </DetailItem>
              </DetailGrid>
            </DetailSection>

            <DetailSection>
              <DetailTitle>Order Items</DetailTitle>
              {selectedOrder.products.map((product, index) => (
                <div
                  key={index}
                  style={{
                    display: 'flex',
                    justifyContent: 'between',
                    alignItems: 'center',
                    padding: theme.spacing.md,
                    border: `1px solid ${theme.colors.gray200}`,
                    borderRadius: theme.borderRadius.md,
                    marginBottom: theme.spacing.sm,
                  }}
                >
                  <span>
                    {product.quantity}x {product.name}
                  </span>
                  <span>
                    {formatCurrency(product.price * product.quantity)}
                  </span>
                </div>
              ))}
              <div
                style={{
                  display: 'flex',
                  justifyContent: 'between',
                  alignItems: 'center',
                  padding: theme.spacing.lg,
                  background: theme.colors.gray50,
                  borderRadius: theme.borderRadius.md,
                  fontWeight: '600',
                  fontSize: '1.1rem',
                }}
              >
                <span>Total</span>
                <span>{formatCurrency(selectedOrder.total)}</span>
              </div>
            </DetailSection>

            <DetailSection>
              <DetailTitle>Update Order Status</DetailTitle>
              <StatusUpdateForm>
                <FormField
                  label="Change Status"
                  type="select"
                  value={selectedOrder.status}
                  onChange={e =>
                    handleUpdateStatus(selectedOrder.id, e.target.value)
                  }
                  options={[
                    { value: 'pending', label: 'Pending' },
                    { value: 'processing', label: 'Processing' },
                    { value: 'shipped', label: 'Shipped' },
                    { value: 'completed', label: 'Completed' },
                    { value: 'cancelled', label: 'Cancelled' },
                  ]}
                />
                <Button variant="primary">Update Status</Button>
              </StatusUpdateForm>
            </DetailSection>

            {selectedOrder.status !== 'shipped' &&
              selectedOrder.status !== 'completed' && (
                <DetailSection>
                  <DetailTitle>Add Tracking Information</DetailTitle>
                  <FormField
                    label="Tracking Number"
                    value={trackingInfo.trackingNumber}
                    onChange={e =>
                      setTrackingInfo(prev => ({
                        ...prev,
                        trackingNumber: e.target.value,
                      }))
                    }
                    placeholder="Enter tracking number"
                  />
                  <DetailGrid>
                    <FormField
                      label="Carrier"
                      type="select"
                      value={trackingInfo.carrier}
                      onChange={e =>
                        setTrackingInfo(prev => ({
                          ...prev,
                          carrier: e.target.value,
                        }))
                      }
                      options={[
                        { value: 'ups', label: 'UPS' },
                        { value: 'fedex', label: 'FedEx' },
                        { value: 'usps', label: 'USPS' },
                        { value: 'dhl', label: 'DHL' },
                        { value: 'other', label: 'Other' },
                      ]}
                    />
                    <div style={{ display: 'flex', alignItems: 'end' }}>
                      <Button
                        variant="primary"
                        onClick={handleAddTracking}
                        fullWidth
                      >
                        <FaShippingFast />
                        Add Tracking
                      </Button>
                    </div>
                  </DetailGrid>
                </DetailSection>
              )}
          </OrderDetailsModal>
        )}
      </Modal>
    </>
  );
