const ProductCard = ({
  product,
  onToggleWishlist,
  isInWishlist = false,
  storeSlug = '',
}) => {
  const [isEnquiryModalOpen, setIsEnquiryModalOpen] = useState(false);

  const renderStars = rating => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(<Star key={i} filled={i <= Math.floor(rating)} />);
    }
    return stars;
  };

  const calculateDiscount = (original, current) => {
    return Math.round(((original - current) / original) * 100);
  };

  const handleEnquireClick = e => {
    e.preventDefault();
    e.stopPropagation();
    setIsEnquiryModalOpen(true);
  };

  const handleWishlistToggle = e => {
    e.preventDefault();
    e.stopPropagation();
    onToggleWishlist?.(product);
  };

  const getBaseUrl = () => (storeSlug ? `/${storeSlug}` : '/ecommerce');

  const availabilityStatus = getAvailabilityStatus(product);
  const availabilityLabel = getAvailabilityLabel(availabilityStatus);
  const availabilityColor = getAvailabilityColor(availabilityStatus);

  return (
    <Link to={`/${storeSlug}/productdetail/${product.id}`}>
      <Card>
        <ImageContainer>
          <ProductImage src={product.image} alt={product.name} />

          {product.onSale && product.originalPrice && (
            <Badge type="sale">Sale</Badge>
          )}

          {product.featured && !product.onSale && (
            <Badge type="featured">Featured</Badge>
          )}

          <WishlistButton
            onClick={handleWishlistToggle}
            title={isInWishlist ? 'Remove from wishlist' : 'Add to wishlist'}
          >
            <FaHeart
              style={{
                color: isInWishlist ? theme.colors.error : 'currentColor',
              }}
            />
          </WishlistButton>
        </ImageContainer>

        <CardContent>
          <Category>{product.category.replace('-', ' ')}</Category>
          <ProductName>{product.name}</ProductName>
          <Description>{product.description}</Description>

          <RatingContainer>
            <StarRating>{renderStars(product.rating)}</StarRating>
            <ReviewCount>({product.reviews} reviews)</ReviewCount>
          </RatingContainer>

          <PriceContainer>
            <CurrentPrice>${product.price}</CurrentPrice>
            {product.originalPrice && product.originalPrice > product.price && (
              <>
                <OriginalPrice>${product.originalPrice}</OriginalPrice>
                <Discount>
                  -{calculateDiscount(product.originalPrice, product.price)}%
                </Discount>
              </>
            )}
          </PriceContainer>

          <StockIndicator
            availability={availabilityStatus}
            color={availabilityColor}
          >
            {availabilityStatus === 'in_stock' && product.stock > 0
              ? `${product.stock} in stock`
              : availabilityLabel}
          </StockIndicator>

          <ActionButtons>
            <EnquireButton
              onClick={handleEnquireClick}
              disabled={availabilityStatus === 'out_of_stock'}
            >
              <FaEnvelope />
              {availabilityStatus === 'out_of_stock'
                ? 'Not Available'
                : 'Enquire Now'}
            </EnquireButton>
          </ActionButtons>
        </CardContent>
      </Card>

      <EnquiryModal
        isOpen={isEnquiryModalOpen}
        onClose={() => setIsEnquiryModalOpen(false)}
        product={product}
      />
    </Link>
  );
};

export default ProductCard;

