              <TableHeaderCell>Actions</TableHeaderCell>
            </TableRow>
          </TableHeader>
          <tbody>
            {filteredProducts.map(product => (
              <TableRow key={product.id}>
                <TableCell>
                  <ProductInfo>
                    <ProductImage src={product.image} alt={product.name} />
