  ];

  return (
    <>
      <MobileToggle onClick={toggleSidebar}>
        {isOpen ? <FaTimes /> : <FaBars />}
      </MobileToggle>

      <Overlay isOpen={isOpen} onClick={closeSidebar} />

      <SidebarContainer isOpen={isOpen}>
        <SidebarHeader>
          <StoreBranding>
            <StoreLogo>
              <FaStore />
            </StoreLogo>
            <StoreInfo>
              <StoreName>My Store</StoreName>
              <StoreType>Premium Seller</StoreType>
            </StoreInfo>
          </StoreBranding>

          <SellerProfile>
            <ProfileAvatar>
              <FaUser />
            </ProfileAvatar>
            <ProfileInfo>
              <ProfileName>John Doe</ProfileName>
              <ProfileRole>Store Owner</ProfileRole>
            </ProfileInfo>
          </SellerProfile>
        </SidebarHeader>

        <Navigation>
          {navItems.map((section, sectionIndex) => (
            <NavSection key={sectionIndex}>
              <NavSectionTitle>{section.section}</NavSectionTitle>
              {section.items.map(item => (
                <NavItem
                  key={item.id}
                  active={activeTab === item.id}
                  onClick={() => handleTabChange(item.id)}
                >
                  <item.icon />
                  {item.label}
                </NavItem>
              ))}
            </NavSection>
          ))}
        </Navigation>

        <SidebarFooter>
          <UpgradeCard>
            <UpgradeTitle>Upgrade to Pro</UpgradeTitle>
            <UpgradeText>
              Get advanced analytics and unlimited products
            </UpgradeText>
            <UpgradeButton>Upgrade Now</UpgradeButton>
          </UpgradeCard>
        </SidebarFooter>
      </SidebarContainer>
    </>
  );
};

