                  <input
                    type="file"
                    accept="image/*"
                    onChange={e =>
                      handleImageUpload('banner', e.target.files[0])
                    }
                    disabled={!isEditing}
                  />
                </UploadButton>
              </div>
            </ImagePreview>
          </ImageUploadSection>

          <FormGrid>
            <FormGroup>
              <Label>
                <FaStore />
                Shop Name
