const ErrorMessage = styled.span`
  display: block;
  font-size: 0.8rem;
  color: ${theme.colors.error};
  margin-top: ${theme.spacing.xs};

  ${media.mobile} {
    font-size: 0.75rem;
  }
`;

const HelpText = styled.span`
  display: block;
  font-size: 0.8rem;
  color: ${theme.colors.gray500};
  margin-top: ${theme.spacing.xs};

  ${media.mobile} {
    font-size: 0.75rem;
  }
`;

const FormField = ({
  label,
  type = 'text',
  name,
  value,
  onChange,
  placeholder,
  required = false,
  error,
  helpText,
  disabled = false,
  options = [],
  rows,
  children,
  ...props
}) => {
  const renderInput = () => {
    // If children are provided, render them instead of input elements
    if (children) {
      return children;
    }

    if (type === 'textarea') {
      return (
        <Textarea
          name={name}
          value={value}
          onChange={onChange}
          placeholder={placeholder}
          required={required}
          disabled={disabled}
          hasError={!!error}
          rows={rows}
          {...props}
        />
      );
    }

    if (type === 'select') {
      return (
        <Select
          name={name}
          value={value}
          onChange={onChange}
          required={required}
          disabled={disabled}
          hasError={!!error}
          {...props}
        >
          <option value="">Select an option</option>
          {options.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </Select>
      );
    }

    return (
      <Input
        type={type}
        name={name}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        required={required}
        disabled={disabled}
        hasError={!!error}
        {...props}
      />
    );
  };

  return (
    <FieldContainer>
      {label && (
        <Label>
          {label}
          {required && <RequiredMark>*</RequiredMark>}
        </Label>
      )}
      {renderInput()}
      {error && <ErrorMessage>{error}</ErrorMessage>}
      {helpText && !error && <HelpText>{helpText}</HelpText>}
    </FieldContainer>
  );
};

export default FormField;

