    background: ${theme.colors.primaryDark};
    transform: translateY(-2px);
  }
`;

const Cart = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([
    // Mock cart items for demonstration
    {
      id: 1,
      name: 'Premium Wireless Headphones',
      category: 'electronics',
      price: 299.99,
      originalPrice: 399.99,
      image:
        'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=500&q=80',
      quantity: 1,
      stock: 15,
    },
    {
      id: 4,
      name: 'Classic Denim Jacket',
      category: 'fashion',
      price: 89.99,
      image:
        'https://images.unsplash.com/photo-1544022613-e87ca75a784a?w=500&q=80',
      quantity: 2,
      selectedSize: 'M',
      stock: 28,
    },
  ]);
  const [promoCode, setPromoCode] = useState('');
  const [appliedPromo, setAppliedPromo] = useState(null);

  const updateQuantity = (id, selectedSize, newQuantity) => {
    setCartItems(prev =>
      prev.map(item =>
        item.id === id && item.selectedSize === selectedSize
          ? {
              ...item,
              quantity: Math.max(1, Math.min(item.stock, newQuantity)),
            }
          : item
      )
    );
  };

  const removeItem = (id, selectedSize) => {
    setCartItems(prev =>
      prev.filter(
        item => !(item.id === id && item.selectedSize === selectedSize)
      )
    );
  };

  const applyPromoCode = () => {
    // Mock promo code logic
    const validCodes = {
      SAVE10: { type: 'percentage', value: 10, description: '10% off' },
      WELCOME: { type: 'fixed', value: 20, description: '$20 off' },
      FREESHIP: { type: 'shipping', value: 0, description: 'Free shipping' },
    };

    if (validCodes[promoCode.toUpperCase()]) {
      setAppliedPromo({
        code: promoCode.toUpperCase(),
        ...validCodes[promoCode.toUpperCase()],
      });
      setPromoCode('');
    } else {
      alert('Invalid promo code');
    }
  };

  const removePromoCode = () => {
    setAppliedPromo(null);
  };

  const calculateSubtotal = () => {
    return cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
  };

  const calculateDiscount = () => {
    if (!appliedPromo) return 0;

    const subtotal = calculateSubtotal();
    if (appliedPromo.type === 'percentage') {
      return subtotal * (appliedPromo.value / 100);
    } else if (appliedPromo.type === 'fixed') {
      return Math.min(appliedPromo.value, subtotal);
    }
    return 0;
  };

  const calculateShipping = () => {
    const subtotal = calculateSubtotal();
    if (appliedPromo?.type === 'shipping' || subtotal >= 99) {
      return 0;
    }
    return 9.99;
  };

  const calculateTotal = () => {
    return calculateSubtotal() - calculateDiscount() + calculateShipping();
  };

  const handleEnquireAll = () => {
    // Since this is an enquiry system, we'll convert cart to enquiries
    navigate('/ecommerce/my-enquiries');
  };

  if (cartItems.length === 0) {
    return (
      <PageContainer>
        <Navbar cartItemsCount={0} />

        <Container>
          <PageHeader>
            <BackLink to="/ecommerce/products">
              <FaArrowLeft />
              Continue Shopping
            </BackLink>
          </PageHeader>

          <EmptyCart>
            <FaShoppingCart className="icon" />
            <h2>Your cart is empty</h2>
            <p>Looks like you haven't added any items to your cart yet.</p>
            <ShopButton to="/ecommerce/products">
              Start Shopping
              <FaShoppingCart />
            </ShopButton>
          </EmptyCart>
        </Container>

        <Footer />
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <Navbar
        cartItemsCount={cartItems.reduce((sum, item) => sum + item.quantity, 0)}
      />

      <Container>
        <PageHeader>
          <BackLink to="/ecommerce/products">
            <FaArrowLeft />
            Continue Shopping
          </BackLink>
          <PageTitle>Shopping Cart ({cartItems.length} items)</PageTitle>
        </PageHeader>

        <CartContainer>
          <CartItems>
            {cartItems.map((item, index) => (
              <CartItem
                key={`${item.id}-${item.selectedSize || 'no-size'}-${index}`}
              >
                <ItemImage src={item.image} alt={item.name} />

                <ItemDetails>
                  <h3>{item.name}</h3>
                  <div className="category">
                    {item.category.replace('-', ' ')}
                  </div>
                  {item.selectedSize && (
                    <div className="size">Size: {item.selectedSize}</div>
                  )}
                  <div className="price">
                    ${item.price}
                    {item.originalPrice && item.originalPrice > item.price && (
                      <span
                        style={{
                          marginLeft: '8px',
                          fontSize: '0.9rem',
                          color: theme.colors.gray500,
                          textDecoration: 'line-through',
                        }}
                      >
                        ${item.originalPrice}
                      </span>
                    )}
                  </div>
                </ItemDetails>

                <ItemActions>
                  <QuantityControls>
                    <QuantityButton
                      onClick={() =>
                        updateQuantity(
                          item.id,
                          item.selectedSize,
                          item.quantity - 1
                        )
                      }
                      disabled={item.quantity <= 1}
                    >
                      <FaMinus />
                    </QuantityButton>
                    <QuantityInput>{item.quantity}</QuantityInput>
                    <QuantityButton
                      onClick={() =>
                        updateQuantity(
                          item.id,
                          item.selectedSize,
                          item.quantity + 1
                        )
                      }
                      disabled={item.quantity >= item.stock}
                    >
                      <FaPlus />
                    </QuantityButton>
                  </QuantityControls>

                  <RemoveButton
                    onClick={() => removeItem(item.id, item.selectedSize)}
                    title="Remove item"
                  >
                    <FaTrash />
                  </RemoveButton>
                </ItemActions>
              </CartItem>
            ))}
          </CartItems>

          <CartSummary>
            <SummaryTitle>Order Summary</SummaryTitle>

            <SummaryRow>
              <span>
                Subtotal (
                {cartItems.reduce((sum, item) => sum + item.quantity, 0)} items)
              </span>
              <span>${calculateSubtotal().toFixed(2)}</span>
            </SummaryRow>

            {appliedPromo && (
              <SummaryRow style={{ color: theme.colors.success }}>
                <span>
                  Discount ({appliedPromo.description})
                  <button
                    onClick={removePromoCode}
                    style={{
                      marginLeft: '8px',
                      background: 'none',
                      border: 'none',
                      color: theme.colors.error,
                      cursor: 'pointer',
                    }}
                  >
                    <FaTimes />
                  </button>
                </span>
                <span>-${calculateDiscount().toFixed(2)}</span>
              </SummaryRow>
            )}

            <PromoSection>
              <div
                style={{ marginBottom: theme.spacing.sm, fontWeight: '600' }}
              >
                <FaTags style={{ marginRight: theme.spacing.sm }} />
                Promo Code
              </div>
              <PromoInput>
                <PromoCodeInput
                  type="text"
                  placeholder="Enter promo code"
                  value={promoCode}
                  onChange={e => setPromoCode(e.target.value)}
                />
                <ApplyButton onClick={applyPromoCode}>Apply</ApplyButton>
              </PromoInput>
              <div style={{ fontSize: '0.8rem', color: theme.colors.gray500 }}>
                Try: SAVE10, WELCOME, or FREESHIP
              </div>
            </PromoSection>

            <ShippingInfo>
              <div className="shipping-option">
                <FaTruck />
                <span>Shipping</span>
                <span style={{ marginLeft: 'auto' }}>
                  {calculateShipping() === 0 ? (
                    <span className="free-shipping">FREE</span>
                  ) : (
                    `$${calculateShipping().toFixed(2)}`
                  )}
                </span>
              </div>
              {calculateShipping() > 0 && (
                <div
                  style={{ fontSize: '0.8rem', color: theme.colors.gray600 }}
                >
                  Free shipping on orders over $99
                </div>
              )}
            </ShippingInfo>

            <SummaryRow className="total">
              <span>Total</span>
              <span>${calculateTotal().toFixed(2)}</span>
            </SummaryRow>

            <CheckoutButton onClick={handleEnquireAll}>
              Enquire About All Items
              <FaEnvelope />
            </CheckoutButton>

            <div
              style={{
                textAlign: 'center',
                marginTop: theme.spacing.md,
                fontSize: '0.8rem',
                color: theme.colors.gray500,
              }}
            >
              Secure checkout with SSL encryption
            </div>
          </CartSummary>
        </CartContainer>
      </Container>

      <Footer />
    </PageContainer>
  );
};

export default Cart;

