const ProductDetail = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const [product, setProduct] = useState(null);
  const [selectedImage, setSelectedImage] = useState(0);
  const [selectedSize, setSelectedSize] = useState('');
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [storeSlug, setStoreSlug] = useState('');
  const [vendor, setVendor] = useState(null);
  const [isEnquiryModalOpen, setIsEnquiryModalOpen] = useState(false);

  // Detect store slug from URL
  useEffect(() => {
    const path = location.pathname;
    if (path !== `/ecommerce/product/${productId}`) {
      // Extract store slug from URL like "/techmart-downtown/product/4"
      const pathSegments = path.split('/').filter(Boolean);
      const slug = pathSegments[0];
      const foundVendor = getVendorByIdOrSlug(slug);
      if (foundVendor) {
        setStoreSlug(foundVendor.slug);
        setVendor(foundVendor);
      }
    }
  }, [location.pathname, productId]);

  useEffect(() => {
    setLoading(true);
    const foundProduct = getProductById(productId);

    if (foundProduct) {
      setProduct(foundProduct);

      // Get related products from same category
      const related = products
        .filter(
          p =>
            p.categoryId === foundProduct.categoryId &&
            p.productId !== foundProduct.productId
        )
        .slice(0, 4);
      setRelatedProducts(related);

      // Set default size if available
      if (foundProduct.sizes && foundProduct.sizes.length > 0) {
        setSelectedSize(foundProduct.sizes[0]);
      }
    }

    setLoading(false);
  }, [productId]);

  const getBaseUrl = () => (storeSlug ? `/${storeSlug}` : '/ecommerce');

  const handleEnquireClick = () => {
    setIsEnquiryModalOpen(true);
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: product.name,
        text: product.description,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      alert('Product link copied to clipboard!');
    }
  };

  const renderStars = rating => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(<Star key={i} filled={i <= Math.floor(rating)} />);
    }
    return stars;
  };

  const calculateDiscount = (original, current) => {
    return Math.round(((original - current) / original) * 100);
  };

  if (loading) {
    return (
      <PageContainer>
        <Navbar
          storeName={vendor?.name || ''}
          storeLogo={vendor?.logo || ''}
          storeSlug={storeSlug}
          theme={vendor?.theme || {}}
        />
        <LoadingSpinner fullPage text="Loading product..." />
        <Footer storeSlug={storeSlug} theme={vendor?.theme || {}} />
      </PageContainer>
    );
  }

  if (!product) {
    return (
      <PageContainer>
        <Navbar
          storeName={vendor?.name || ''}
          storeLogo={vendor?.logo || ''}
          storeSlug={storeSlug}
          theme={vendor?.theme || {}}
        />
        <Container>
          <div style={{ textAlign: 'center', padding: '4rem 0' }}>
            <h2>Product not found</h2>
            <p style={{ margin: '1rem 0', color: theme.colors.gray600 }}>
              The product you're looking for doesn't exist.
            </p>
            <Link
              to={`${getBaseUrl()}/products`}
              style={{ color: theme.colors.primary }}
            >
              Back to Products
            </Link>
          </div>
        </Container>
        <Footer storeSlug={storeSlug} theme={vendor?.theme || {}} />
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <Navbar
        storeName={vendor?.name || ''}
        storeLogo={vendor?.logo || ''}
        storeSlug={storeSlug}
        theme={vendor?.theme || {}}
      />

      <Container>
        <BackButton onClick={() => navigate(-1)}>
          <FaArrowLeft />
          Back
        </BackButton>

        <Breadcrumb>
          <Link to={getBaseUrl()}>Home</Link>
          <span>/</span>
          <Link to={`${getBaseUrl()}/products`}>Products</Link>
          <span>/</span>
          <Link to={`${getBaseUrl()}/products?category=${product.category}`}>
            {product.category.replace('-', ' ')}
          </Link>
          <span>/</span>
          <span>{product.name}</span>
        </Breadcrumb>

        <ProductContainer>
          <ImageSection>
            <MainImage
              src={
                product.images ? product.images[selectedImage] : product.image
              }
              alt={product.name}
            />

            {product.images && product.images.length > 1 && (
              <ThumbnailContainer>
                {product.images.map((image, index) => (
                  <Thumbnail
                    key={index}
                    src={image}
                    alt={`${product.name} ${index + 1}`}
                    active={selectedImage === index}
                    onClick={() => setSelectedImage(index)}
                  />
                ))}
              </ThumbnailContainer>
            )}
          </ImageSection>

          <ProductInfo>
            <ProductName>{product.name}</ProductName>

            <RatingContainer>
              <StarRating>{renderStars(product.rating)}</StarRating>
              <ReviewInfo>
                {product.rating} ({product.reviews} reviews)
              </ReviewInfo>
            </RatingContainer>

            <PriceContainer>
              <CurrentPrice>${product.price}</CurrentPrice>
              {product.originalPrice &&
                product.originalPrice > product.price && (
                  <>
                    <OriginalPrice>${product.originalPrice}</OriginalPrice>
                    <Discount>
                      -{calculateDiscount(product.originalPrice, product.price)}
                      % OFF
                    </Discount>
                  </>
                )}
            </PriceContainer>

            <Description>{product.description}</Description>

            {product.features && (
              <Features>
                <h3>Key Features:</h3>
                <FeatureList>
                  {product.features.map((feature, index) => (
                    <FeatureItem key={index}>{feature}</FeatureItem>
                  ))}
                </FeatureList>
              </Features>
            )}

            {product.sizes && (
              <VariantSection>
                <h3>Size:</h3>
                <VariantOptions>
                  {product.sizes.map(size => (
                    <VariantOption
                      key={size}
                      selected={selectedSize === size}
                      onClick={() => setSelectedSize(size)}
                    >
                      {size}
                    </VariantOption>
                  ))}
                </VariantOptions>
              </VariantSection>
            )}

            <StockInfo
              inStock={getAvailabilityStatus(product) !== 'out_of_stock'}
            >
              <FaCheck />
              {getAvailabilityLabel(getAvailabilityStatus(product))}
              {getAvailabilityStatus(product) === 'in_stock' &&
                product.stock > 0 && (
                  <span
                    style={{
                      marginLeft: '8px',
                      fontSize: '0.9rem',
                      opacity: 0.8,
                    }}
                  >
                    ({product.stock} units available)
                  </span>
                )}
            </StockInfo>

            <SellerInfo>
              <SellerHeader>
                <SellerLogo
                  src={
                    vendor?.logo ||
                    'https://images.unsplash.com/photo-1560472354-b33ff0c44a43?w=80&h=80&fit=crop&crop=center'
                  }
                  alt={vendor?.name || 'Seller'}
                />
                <SellerDetails>
                  <SellerName>{vendor?.name || 'TechMart Downtown'}</SellerName>
                  <SellerBadge>Verified Seller</SellerBadge>
                </SellerDetails>
              </SellerHeader>
              <SellerMeta>
                <SellerMetaItem>
                  <FaStore />
                  <span>Electronics Store</span>
                </SellerMetaItem>
                <SellerMetaItem>
                  <FaMapMarkerAlt />
                  <span>Downtown Area</span>
                </SellerMetaItem>
                <SellerMetaItem>
                  <FaUser />
                  <span>500+ Products</span>
                </SellerMetaItem>
                <SellerMetaItem>
                  <FaStar />
                  <span>4.8 Rating</span>
                </SellerMetaItem>
              </SellerMeta>
            </SellerInfo>

            <ActionButtons>
              <EnquireButton
                onClick={handleEnquireClick}
                disabled={
                  getAvailabilityStatus(product) === 'out_of_stock' ||
                  (product.sizes && !selectedSize)
                }
              >
                <FaEnvelope />
                {getAvailabilityStatus(product) === 'out_of_stock'
                  ? 'Not Available'
                  : 'Enquire Now'}
              </EnquireButton>

              <WishlistButton title="Add to Wishlist">
                <FaHeart />
              </WishlistButton>

              <ShareButton onClick={handleShare} title="Share Product">
                <FaShare />
              </ShareButton>
            </ActionButtons>

            <Guarantees>
              <GuaranteeItem>
                <FaTruck />
                Free shipping over $99
              </GuaranteeItem>
              <GuaranteeItem>
                <FaUndoAlt />
                30-day returns
              </GuaranteeItem>
              <GuaranteeItem>
                <FaShieldAlt />
                2-year warranty
              </GuaranteeItem>
            </Guarantees>
          </ProductInfo>
        </ProductContainer>

        {relatedProducts.length > 0 && (
          <RelatedSection>
            <SectionTitle>Related Products</SectionTitle>
            <RelatedGrid>
              {relatedProducts.map(relatedProduct => (
                <ProductCard
                  key={relatedProduct.productId}
                  product={relatedProduct}
                  storeSlug={storeSlug}
                />
              ))}
            </RelatedGrid>
          </RelatedSection>
        )}
      </Container>

      <EnquiryModal
        isOpen={isEnquiryModalOpen}
        onClose={() => setIsEnquiryModalOpen(false)}
        product={product}
      />

      <Footer storeSlug={storeSlug} theme={vendor?.theme || {}} />
    </PageContainer>
  );
};

export default ProductDetail;

