const ProductList = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const location = useLocation();
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [sortBy, setSortBy] = useState('name');
  const [view, setView] = useState('grid');
  const [loading, setLoading] = useState(true);
  const [storeSlug, setStoreSlug] = useState('');
  const [vendor, setVendor] = useState(null);

  // Detect store slug from URL
  useEffect(() => {
    const path = location.pathname;
    if (path !== '/ecommerce/products') {
      // Extract store slug from URL like "/techmart-downtown/products"
      const pathSegments = path.split('/').filter(Boolean);
      const slug = pathSegments[0];
      const foundVendor = getVendorByIdOrSlug(slug);
      if (foundVendor) {
        setStoreSlug(foundVendor.slug);
        setVendor(foundVendor);
      }
    }
  }, [location.pathname]);

  // Get URL parameters
  const category = searchParams.get('category');
  const searchQuery = searchParams.get('search');
  const featured = searchParams.get('featured');
  const sale = searchParams.get('sale');

  useEffect(() => {
    setLoading(true);
    let result = [...products];

    // Filter by category
    if (category) {
      result = getProductsByCategory(category);
    }

    // Filter by search query
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      result = result.filter(
        product =>
          product.name.toLowerCase().includes(query) ||
          product.description.toLowerCase().includes(query) ||
          product.category.toLowerCase().includes(query)
      );
    }

    // Filter by featured
    if (featured === 'true') {
      result = result.filter(product => product.featured);
    }

    // Filter by sale
    if (sale === 'true') {
      result = result.filter(product => product.onSale);
    }

    // Sort products
    result.sort((a, b) => {
      switch (sortBy) {
        case 'price-low':
          return a.price - b.price;
        case 'price-high':
          return b.price - a.price;
        case 'rating':
          return b.rating - a.rating;
        case 'newest':
          return b.id - a.id;
        default:
          return a.name.localeCompare(b.name);
      }
    });

    setFilteredProducts(result);
    setLoading(false);
  }, [category, searchQuery, featured, sale, sortBy]);

  const getPageTitle = () => {
    if (searchQuery) return `Search Results for "${searchQuery}"`;
    if (category) {
      const cat = categories.find(c => c.slug === category);
      return cat ? cat.name : 'Products';
    }
    if (featured === 'true') return 'Featured Products';
    if (sale === 'true') return 'Sale Items';
    return 'All Products';
  };

  const getActiveFilters = () => {
    const filters = [];
    if (category) {
      const cat = categories.find(c => c.slug === category);
      if (cat)
        filters.push({ type: 'category', value: cat.name, param: 'category' });
    }
    if (searchQuery)
      filters.push({ type: 'search', value: searchQuery, param: 'search' });
    if (featured === 'true')
      filters.push({ type: 'featured', value: 'Featured', param: 'featured' });
    if (sale === 'true')
      filters.push({ type: 'sale', value: 'On Sale', param: 'sale' });
    return filters;
  };

  const removeFilter = param => {
    const newParams = new URLSearchParams(searchParams);
    newParams.delete(param);
    setSearchParams(newParams);
  };

  const clearAllFilters = () => {
    setSearchParams({});
  };

  const setCategoryFilter = categorySlug => {
    const newParams = new URLSearchParams(searchParams);
    if (categorySlug) {
      newParams.set('category', categorySlug);
    } else {
      newParams.delete('category');
    }
    setSearchParams(newParams);
  };

  const activeFilters = getActiveFilters();

  if (loading) {
    return (
      <PageContainer>
        <Navbar
          storeName={vendor?.name || ''}
          storeLogo={vendor?.logo || ''}
          storeSlug={storeSlug}
          theme={vendor?.theme || {}}
        />
        <LoadingSpinner fullPage text="Loading products..." />
        <Footer storeSlug={storeSlug} theme={vendor?.theme || {}} />
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <Navbar
        storeName={vendor?.name || ''}
        storeLogo={vendor?.logo || ''}
        storeSlug={storeSlug}
        theme={vendor?.theme || {}}
      />

      <Container>
        <PageHeader>
          <PageTitle>{getPageTitle()}</PageTitle>
          <ResultsInfo>
            Showing {filteredProducts.length} product
            {filteredProducts.length !== 1 ? 's' : ''}
          </ResultsInfo>
        </PageHeader>

        <FiltersBar>
          <FilterGroup>
            <FilterButton
              className={!category ? 'active' : ''}
              onClick={() => setCategoryFilter('')}
            >
              All
            </FilterButton>
            {categories.map(cat => (
              <FilterButton
                key={cat.id}
                className={category === cat.slug ? 'active' : ''}
                onClick={() => setCategoryFilter(cat.slug)}
              >
                {cat.name}
              </FilterButton>
            ))}
          </FilterGroup>

          <FilterGroup>
            <Select value={sortBy} onChange={e => setSortBy(e.target.value)}>
              <option value="name">Sort by Name</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="rating">Highest Rated</option>
              <option value="newest">Newest First</option>
            </Select>

            <ViewToggle>
              <ViewButton
                active={view === 'grid'}
                onClick={() => setView('grid')}
                title="Grid View"
              >
                <FaTh />
              </ViewButton>
              <ViewButton
                active={view === 'list'}
                onClick={() => setView('list')}
                title="List View"
              >
                <FaList />
              </ViewButton>
            </ViewToggle>
          </FilterGroup>
        </FiltersBar>

        {activeFilters.length > 0 && (
          <ActiveFilters>
            {activeFilters.map((filter, index) => (
              <ActiveFilter key={index}>
                {filter.value}
                <RemoveFilterButton onClick={() => removeFilter(filter.param)}>
                  <FaTimes />
                </RemoveFilterButton>
              </ActiveFilter>
            ))}
            <ClearFiltersButton onClick={clearAllFilters}>
              Clear All
            </ClearFiltersButton>
          </ActiveFilters>
        )}

        {filteredProducts.length > 0 ? (
          <ProductsGrid view={view}>
            {filteredProducts.map(product => (
              <ProductCard
                key={product.id}
                product={product}
                storeSlug={storeSlug}
              />
            ))}
          </ProductsGrid>
        ) : (
          <NoResults>
            <h3>No products found</h3>
            <p>Try adjusting your filters or search terms</p>
            <ClearFiltersButton onClick={clearAllFilters}>
              Clear All Filters
            </ClearFiltersButton>
          </NoResults>
        )}
      </Container>

      <Footer storeSlug={storeSlug} theme={vendor?.theme || {}} />
    </PageContainer>
  );
};

export default ProductList;

