const StoresListing = () => {
  const navigate = useNavigate();
  const [stores, setStores] = useState([]);
  const [filteredStores, setFilteredStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentLocation, setCurrentLocation] = useState(null);
  const [activeFilter, setActiveFilter] = useState('all');
  const [sortBy, setSortBy] = useState('distance');
  const [locationLoading, setLocationLoading] = useState(false);

  const loadStoresForLocation = useCallback(location => {
    const vendorsWithDistance = updateVendorsWithDistance(
      ecommerceVendors,
      location
    );
    setStores(vendorsWithDistance);
    setFilteredStores(vendorsWithDistance);
    setLoading(false);
  }, []);

  const initializeLocation = useCallback(async () => {
    setLoading(true);
    try {
      const location = await getCurrentLocation();
      setCurrentLocation(location);
      loadStoresForLocation(location);
    } catch (error) {
      const defaultLocation = getDefaultLocation();
      setCurrentLocation(defaultLocation);
      loadStoresForLocation(defaultLocation);
    }
  }, [loadStoresForLocation]);

  const handleLocationSearch = async e => {
    e.preventDefault();
    if (!searchTerm.trim()) return;

    setLocationLoading(true);
    try {
      let newLocation;

      // Check if it's a ZIP code (5 digits)
      if (/^\d{5}$/.test(searchTerm.trim())) {
        newLocation = await getLocationFromZip(searchTerm.trim());
      } else {
        newLocation = await searchLocationByCity(searchTerm.trim());
      }

      setCurrentLocation(newLocation);
      loadStoresForLocation(newLocation);
      setSearchTerm('');
    } catch (error) {
      alert('Location not found. Please try a different city or ZIP code.');
    } finally {
      setLocationLoading(false);
    }
  };

  const handleUseCurrentLocation = async () => {
    setLocationLoading(true);
    try {
      const location = await getCurrentLocation();
      setCurrentLocation(location);
      loadStoresForLocation(location);
    } catch (error) {
      alert(
        'Unable to get your current location. Please check your browser settings.'
      );
    } finally {
      setLocationLoading(false);
    }
  };

  const applyFilters = useCallback(() => {
    let filtered = [...stores];

    // Apply category filter
    if (activeFilter === 'featured') {
      filtered = filtered.filter(store => store.featured);
    }

    // Apply sorting
    switch (sortBy) {
      case 'distance':
        filtered.sort((a, b) => a.distance - b.distance);
        break;
      case 'rating':
        filtered.sort((a, b) => b.rating - a.rating);
        break;
      case 'name':
        filtered.sort((a, b) => a.name.localeCompare(b.name));
        break;
      default:
        break;
    }

    setFilteredStores(filtered);
  }, [stores, activeFilter, sortBy]);

  useEffect(() => {
    initializeLocation();
  }, [initializeLocation]);

  useEffect(() => {
    applyFilters();
  }, [applyFilters]);

  if (loading) {
    return (
      <PageContainer>
        <NavHeader>
          <NavContent>
            <Logo> ShopMart Stores</Logo>
            <BackButton onClick={() => navigate('/')}>
              <FaHome />
              Back to Home
            </BackButton>
          </NavContent>
        </NavHeader>
        <LoadingState>
          <div className="spinner"></div>
          <h3>Finding stores near you...</h3>
          <p>Please wait while we locate nearby stores.</p>
        </LoadingState>
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <NavHeader>
        <NavContent>
          <Logo> ShopMart Stores</Logo>
          <BackButton onClick={() => navigate('/')}>
            <FaHome />
            Back to Home
          </BackButton>
        </NavContent>
      </NavHeader>

      <Container>
        <PageHeader>
          <PageTitle>Nearby Stores</PageTitle>
          <PageSubtitle>
            Discover amazing shopping experiences near you
          </PageSubtitle>
          {currentLocation && (
            <LocationDisplay>
              <FaMapMarkerAlt />
              <span>
                {currentLocation.city}, {currentLocation.state}
                {currentLocation.isDefault && ' (Default Location)'}
              </span>
            </LocationDisplay>
          )}
        </PageHeader>

        <SearchSection>
          <SearchForm onSubmit={handleLocationSearch}>
            <SearchInputContainer>
              <SearchIcon>
                <FaSearch />
              </SearchIcon>
              <SearchInput
                type="text"
                placeholder="Enter city name or ZIP code..."
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
              />
            </SearchInputContainer>

            <LocationButton
              type="button"
              onClick={handleUseCurrentLocation}
              disabled={locationLoading}
            >
              <FaLocationArrow />
              Use My Location
            </LocationButton>

            <SearchButton type="submit" disabled={locationLoading}>
              {locationLoading ? 'Searching...' : 'Search'}
            </SearchButton>
          </SearchForm>
        </SearchSection>

        <FiltersSection>
          <FilterGroup>
            <FilterButton
              active={activeFilter === 'all'}
              onClick={() => setActiveFilter('all')}
            >
              All Stores
            </FilterButton>
            <FilterButton
              active={activeFilter === 'featured'}
              onClick={() => setActiveFilter('featured')}
            >
              Featured
            </FilterButton>
          </FilterGroup>

          <FilterGroup>
            <SortSelect
              value={sortBy}
              onChange={e => setSortBy(e.target.value)}
            >
              <option value="distance">Sort by Distance</option>
              <option value="rating">Sort by Rating</option>
              <option value="name">Sort by Name</option>
            </SortSelect>
          </FilterGroup>
        </FiltersSection>

        <ResultsInfo>
          <span>
            {filteredStores.length} store
            {filteredStores.length !== 1 ? 's' : ''} found
          </span>
        </ResultsInfo>

        {filteredStores.length > 0 ? (
          <StoresGrid>
            {filteredStores.map(store => (
              <StoreCard key={store.id} store={store} category="ecommerce" />
            ))}
          </StoresGrid>
        ) : (
          <EmptyState>
            <h3>No stores found</h3>
            <p>
              Try searching for a different location or adjusting your filters.
            </p>
          </EmptyState>
        )}
      </Container>
    </PageContainer>
  );
};

export default StoresListing;

