const HotelNavbar = ({ showBackToMain = true }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // Extract hotel slug from URL path like "/taj-palace/rooms/101"
  const hotelSlug = useMemo(() => {
    const pathSegments = location.pathname.split('/').filter(Boolean);
    // For hotel routes, the first segment is usually the hotel slug
    return pathSegments.length > 0 && pathSegments[0] !== 'hotels'
      ? pathSegments[0]
      : '';
  }, [location.pathname]);

  const closeMobileMenu = () => setMobileMenuOpen(false);

  return (
    <NavbarContainer>
      <NavbarContent>
        <Logo to="/hotels">
          <FaHotel />
          HotelBooker
        </Logo>

        <NavLinks isOpen={mobileMenuOpen}>
          <NavLink to="/hotels" onClick={closeMobileMenu}>
            Find Hotels
          </NavLink>
          <NavLink to="/my-bookings" onClick={closeMobileMenu}>
            My Bookings
          </NavLink>
          <NavLink to={'hoteladmin'} onClick={closeMobileMenu}>
            Hotel Owner
          </NavLink>

          <MobileNavActions>
            {showBackToMain && (
              <MobileActionButton to="/" onClick={closeMobileMenu}>
                <FaHome />
                Main Site
              </MobileActionButton>
            )}
            <MobileActionButton to="/my-bookings" onClick={closeMobileMenu}>
              <FaCalendarAlt />
              My Bookings
            </MobileActionButton>
          </MobileNavActions>
        </NavLinks>

        <NavActions>
          {showBackToMain && (
            <BackButton to="/" title="Back to Main Site">
              <FaHome />
              Main Site
            </BackButton>
          )}

          <ActionButton
            title="My Bookings"
            onClick={() => navigate('/my-bookings')}
          >
            <FaCalendarAlt />
          </ActionButton>

          <ActionButton title="User Account">
            <FaUser />
          </ActionButton>

          <MobileMenuButton
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            title="Menu"
          >
            {mobileMenuOpen ? <FaTimes /> : <FaBars />}
          </MobileMenuButton>
        </NavActions>
      </NavbarContent>
    </NavbarContainer>
  );
};

export default HotelNavbar;

