const RoomCard = ({ room, hotelId, hotelSlug }) => {
  const [isLiked, setIsLiked] = useState(false);

  const getAmenityIcon = amenity => {
    const iconMap = {
      WiFi: FaWifi,
      TV: FaTv,
      'Mini Bar': FaCoffee,
      'Room Service': FaCoffee,
      AC: FaWind,
      'Ocean View': FaExpand,
      'City View': FaExpand,
      'Mountain View': FaExpand,
      'Heritage Decor': FaStar,
      Balcony: FaExpand,
      'Work Desk': FaTv,
      'Butler Service': FaCoffee,
      'Living Room': FaExpand,
    };
    const IconComponent = iconMap[amenity] || FaCheckCircle;
    return <IconComponent className="icon" />;
  };

  const handleLike = () => {
    setIsLiked(!isLiked);
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: room.name,
        text: room.description,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      alert('Room link copied to clipboard!');
    }
  };

  return (
    <Card>
      <CardImage
        image={
          room.images?.[0] ||
          'https://images.unsplash.com/photo-1631049307264-da0ec9d70304?ixlib=rb-4.0.3'
        }
      >
        <RoomType>{room.type}</RoomType>
        <ImageActions>
          <ActionButton
            onClick={handleLike}
            style={{ color: isLiked ? '#ef4444' : undefined }}
          >
            <FaHeart />
          </ActionButton>
          <ActionButton onClick={handleShare}>
            <FaShare />
          </ActionButton>
        </ImageActions>
        <ImageOverlay>
          <FaImages className="icon" />
          <span className="text">{room.images?.length || 3} Photos</span>
        </ImageOverlay>
      </CardImage>

      <CardContent>
        <RoomHeader>
          <RoomInfo>
            <RoomName>{room.name}</RoomName>
            <RoomDetails>
              <Detail>
                <FaBed />
                {room.bedType}
              </Detail>
              <Detail>
                <FaUsers />
                {room.maxGuests} guests
              </Detail>
            </RoomDetails>
          </RoomInfo>

          <PriceContainer>
            <Price>
              <span className="currency"></span>
              {room.price.toLocaleString()}
            </Price>
            <PriceUnit>per night</PriceUnit>
          </PriceContainer>
        </RoomHeader>

        <Description>{room.description}</Description>

        <RoomFeatures>
          <FeatureTitle>
            <FaCheckCircle className="icon" />
            Room Features
          </FeatureTitle>
          <Amenities>
            {room.amenities?.slice(0, 6).map((amenity, index) => (
              <AmenityItem key={index}>
                {getAmenityIcon(amenity)}
                {amenity}
              </AmenityItem>
            ))}
          </Amenities>
        </RoomFeatures>

        <BookingActions>
          <BookButton to={`booking/${room.id}`}>Book Now</BookButton>
          <ViewRoomButton to={`${room.id}`}>
            <FaExpand />
            View Room
          </ViewRoomButton>
        </BookingActions>
      </CardContent>
    </Card>
  );
};

export default RoomCard;

