const Booking = () => {
  const { slug, roomId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const [hotel, setHotel] = useState(null);
  const [room, setRoom] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const initialBookingData = location.state?.bookingData || {
    checkIn: '',
    checkOut: '',
    guests: '2',
  };

  const [formData, setFormData] = useState({
    // Guest Information
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    // Booking Details
    checkIn: initialBookingData.checkIn,
    checkOut: initialBookingData.checkOut,
    guests: initialBookingData.guests,
    specialRequests: '',
    // Payment Information
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardName: '',
  });

  useEffect(() => {
    const foundHotel = getHotelByIdOrSlug(slug);
    const foundRoom = foundHotel ? getRoomById(foundHotel.id, roomId) : null;
    setHotel(foundHotel);
    setRoom(foundRoom);
  }, [slug, roomId]);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors({ ...errors, [name]: '' });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    // Required fields validation
    const requiredFields = [
      'firstName',
      'lastName',
      'email',
      'phone',
      'checkIn',
      'checkOut',
      'cardNumber',
      'expiryDate',
      'cvv',
      'cardName',
    ];

    requiredFields.forEach(field => {
      if (!formData[field].trim()) {
        newErrors[field] = 'This field is required';
      }
    });

    // Email validation
    if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    // Phone validation
    if (formData.phone && !/^\+?[\d\s-()]{10,}$/.test(formData.phone)) {
      newErrors.phone = 'Please enter a valid phone number';
    }

    // Date validation
    if (formData.checkIn && formData.checkOut) {
      const checkIn = new Date(formData.checkIn);
      const checkOut = new Date(formData.checkOut);
      if (checkOut <= checkIn) {
        newErrors.checkOut = 'Check-out date must be after check-in date';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const calculateNights = () => {
    if (formData.checkIn && formData.checkOut) {
      const checkIn = new Date(formData.checkIn);
      const checkOut = new Date(formData.checkOut);
      const diffTime = Math.abs(checkOut - checkIn);
      return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    }
    return 1;
  };

  const calculateSubtotal = () => {
    return room ? room.price * calculateNights() : 0;
  };

  const calculateTaxes = () => {
    return calculateSubtotal() * 0.12; // 12% tax
  };

  const calculateTotal = () => {
    return calculateSubtotal() + calculateTaxes();
  };

  const handleSubmit = async e => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    // Simulate booking process
    try {
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Create booking object
      const booking = {
        id: Date.now(),

        roomId: parseInt(roomId),
        hotelName: hotel.name,
        roomName: room.name,
        ...formData,
        totalPrice: calculateTotal(),
        status: 'confirmed',
        bookingDate: new Date().toISOString().split('T')[0],
      };

      // Navigate to confirmation
      navigate(`/${slug}/bookingconfirmation`, {
        state: { booking },
      });
    } catch (error) {
      alert('Booking failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (!hotel || !room) {
    return (
      <PageContainer>
        <HotelNavbar />
        <Container>
          <div style={{ textAlign: 'center', padding: '4rem 0' }}>
            <h2>Booking not available</h2>
            <p>Please select a valid hotel and room.</p>
          </div>
        </Container>
        <HotelFooter />
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <HotelNavbar />

      <Container>
        <BackButton onClick={() => navigate(-1)}>
          <FaArrowLeft />
          Back to Room
        </BackButton>

        <PageTitle>Complete Your Booking</PageTitle>

        <BookingGrid>
          <BookingForm onSubmit={handleSubmit}>
            <Section>
              <SectionTitle>
                <FaUser />
                Guest Information
              </SectionTitle>
              <FormGrid>
                <FormGroup>
                  <Label htmlFor="firstName">First Name</Label>
                  <Input
                    type="text"
                    id="firstName"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleInputChange}
                    className={errors.firstName ? 'error' : ''}
                  />
                  {errors.firstName && (
                    <ErrorMessage>{errors.firstName}</ErrorMessage>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="lastName">Last Name</Label>
                  <Input
                    type="text"
                    id="lastName"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleInputChange}
                    className={errors.lastName ? 'error' : ''}
                  />
                  {errors.lastName && (
                    <ErrorMessage>{errors.lastName}</ErrorMessage>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="email">Email Address</Label>
                  <Input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className={errors.email ? 'error' : ''}
                  />
                  {errors.email && <ErrorMessage>{errors.email}</ErrorMessage>}
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="phone">Phone Number</Label>
                  <Input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    className={errors.phone ? 'error' : ''}
                  />
                  {errors.phone && <ErrorMessage>{errors.phone}</ErrorMessage>}
                </FormGroup>
              </FormGrid>
            </Section>

            <Section>
              <SectionTitle>
                <FaCalendarAlt />
                Booking Details
              </SectionTitle>
              <FormGrid>
                <FormGroup>
                  <Label htmlFor="checkIn">Check-in Date</Label>
                  <Input
                    type="date"
                    id="checkIn"
                    name="checkIn"
                    value={formData.checkIn}
                    onChange={handleInputChange}
                    min={new Date().toISOString().split('T')[0]}
                    className={errors.checkIn ? 'error' : ''}
                  />
                  {errors.checkIn && (
                    <ErrorMessage>{errors.checkIn}</ErrorMessage>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="checkOut">Check-out Date</Label>
                  <Input
                    type="date"
                    id="checkOut"
                    name="checkOut"
                    value={formData.checkOut}
                    onChange={handleInputChange}
                    min={formData.checkIn}
                    className={errors.checkOut ? 'error' : ''}
                  />
                  {errors.checkOut && (
                    <ErrorMessage>{errors.checkOut}</ErrorMessage>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="guests">Number of Guests</Label>
                  <Select
                    id="guests"
                    name="guests"
                    value={formData.guests}
                    onChange={handleInputChange}
                  >
                    {Array.from({ length: room.maxGuests }, (_, i) => (
                      <option key={i + 1} value={i + 1}>
                        {i + 1} Guest{i > 0 ? 's' : ''}
                      </option>
                    ))}
                  </Select>
                </FormGroup>
              </FormGrid>

              <FormGroup style={{ marginTop: theme.spacing.lg }}>
                <Label htmlFor="specialRequests">
                  Special Requests (Optional)
                </Label>
                <TextArea
                  id="specialRequests"
                  name="specialRequests"
                  value={formData.specialRequests}
                  onChange={handleInputChange}
                  placeholder="Any special requirements or requests..."
                />
              </FormGroup>
            </Section>

            <Section>
              <SectionTitle>
                <FaCreditCard />
                Payment Information
              </SectionTitle>
              <FormGrid>
                <FormGroup style={{ gridColumn: '1 / -1' }}>
                  <Label htmlFor="cardNumber">Card Number</Label>
                  <Input
                    type="text"
                    id="cardNumber"
                    name="cardNumber"
                    value={formData.cardNumber}
                    onChange={handleInputChange}
                    placeholder="1234 5678 9012 3456"
                    className={errors.cardNumber ? 'error' : ''}
                  />
                  {errors.cardNumber && (
                    <ErrorMessage>{errors.cardNumber}</ErrorMessage>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="expiryDate">Expiry Date</Label>
                  <Input
                    type="text"
                    id="expiryDate"
                    name="expiryDate"
                    value={formData.expiryDate}
                    onChange={handleInputChange}
                    placeholder="MM/YY"
                    className={errors.expiryDate ? 'error' : ''}
                  />
                  {errors.expiryDate && (
                    <ErrorMessage>{errors.expiryDate}</ErrorMessage>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="cvv">CVV</Label>
                  <Input
                    type="text"
                    id="cvv"
                    name="cvv"
                    value={formData.cvv}
                    onChange={handleInputChange}
                    placeholder="123"
                    className={errors.cvv ? 'error' : ''}
                  />
                  {errors.cvv && <ErrorMessage>{errors.cvv}</ErrorMessage>}
                </FormGroup>

                <FormGroup style={{ gridColumn: '1 / -1' }}>
                  <Label htmlFor="cardName">Cardholder Name</Label>
                  <Input
                    type="text"
                    id="cardName"
                    name="cardName"
                    value={formData.cardName}
                    onChange={handleInputChange}
                    className={errors.cardName ? 'error' : ''}
                  />
                  {errors.cardName && (
                    <ErrorMessage>{errors.cardName}</ErrorMessage>
                  )}
                </FormGroup>
              </FormGrid>
            </Section>

            <BookButton type="submit" disabled={loading}>
              {loading ? (
                'Processing...'
              ) : (
                <>
                  <FaLock />
                  Complete Booking
                </>
              )}
            </BookButton>

            <SecurityNote>
              <FaLock />
              Your payment information is secure and encrypted
            </SecurityNote>
          </BookingForm>

          <BookingSummary>
            <SummaryHeader>
              <HotelInfo>
                <h3>{hotel.name}</h3>
                <p>{hotel.location}</p>
              </HotelInfo>
            </SummaryHeader>

            <BookingDetails>
              <DetailRow>
                <span className="label">Room:</span>
                <span className="value">{room.name}</span>
              </DetailRow>
              <DetailRow>
                <span className="label">Check-in:</span>
                <span className="value">
                  {formData.checkIn
                    ? new Date(formData.checkIn).toLocaleDateString()
                    : '-'}
                </span>
              </DetailRow>
              <DetailRow>
                <span className="label">Check-out:</span>
                <span className="value">
