const BookingConfirmation = () => {
  const location = useLocation();
  const booking = location.state?.booking || {
    id: Date.now(),
    hotelName: 'Sample Hotel',
    roomName: 'Sample Room',
    checkIn: new Date().toISOString().split('T')[0],
    checkOut: new Date(Date.now() + 86400000).toISOString().split('T')[0],
    guests: 2,
    firstName: 'John',
    lastName: 'Doe',
    email: 'john@example.com',
    phone: '+91 9876543210',
    totalPrice: 10000,
  };

  useEffect(() => {
    // Scroll to top when component mounts
    window.scrollTo(0, 0);
  }, []);

  const handlePrint = () => {
    window.print();
  };

  const handleDownload = () => {
    // Mock download functionality
    alert('Booking confirmation download would start here');
  };

  const formatDate = dateString => {
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const calculateNights = () => {
    const checkIn = new Date(booking.checkIn);
    const checkOut = new Date(booking.checkOut);
    const diffTime = Math.abs(checkOut - checkIn);
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  };

  return (
    <PageContainer>
      <HotelNavbar />

      <Container>
        <SuccessCard>
          <SuccessIcon>
            <FaCheckCircle className="icon" />
          </SuccessIcon>

          <SuccessTitle>Booking Confirmed!</SuccessTitle>
          <SuccessMessage>
            Great news! Your hotel reservation has been successfully confirmed.
            We've sent a confirmation email to {booking.email}.
          </SuccessMessage>

          <BookingNumber>
            <div className="label">Confirmation Number</div>
            <div className="number">HTL-{booking.id}</div>
          </BookingNumber>

          <ActionButtons>
            <ActionButton onClick={handlePrint}>
              <FaPrint />
              Print Confirmation
            </ActionButton>
            <ActionButton onClick={handleDownload}>
              <FaDownload />
              Download PDF
            </ActionButton>
          </ActionButtons>

          <ContinueButton to="/hotels">
            <FaHotel />
            Browse More Hotels
          </ContinueButton>
        </SuccessCard>

        <BookingDetails>
          <SectionTitle>Booking Information</SectionTitle>

          <InfoGrid>
            <InfoCard>
              <FaHotel className="icon" />
              <div className="title">Hotel & Room</div>
              <div className="content">
                <strong>{booking.hotelName}</strong>
                <br />
                {booking.roomName}
              </div>
            </InfoCard>

            <InfoCard>
              <FaCalendarAlt className="icon" />
              <div className="title">Check-in</div>
              <div className="content">
                <strong>{formatDate(booking.checkIn)}</strong>
                <br />
                From 3:00 PM
              </div>
            </InfoCard>

            <InfoCard>
              <FaCalendarAlt className="icon" />
              <div className="title">Check-out</div>
              <div className="content">
                <strong>{formatDate(booking.checkOut)}</strong>
                <br />
                Before 11:00 AM
              </div>
            </InfoCard>

            <InfoCard>
              <FaUsers className="icon" />
              <div className="title">Guest Details</div>
              <div className="content">
                <strong>
                  {booking.firstName} {booking.lastName}
                </strong>
                <br />
                {booking.guests} Guest{booking.guests > 1 ? 's' : ''} {' '}
                {calculateNights()} Night{calculateNights() > 1 ? 's' : ''}
              </div>
            </InfoCard>
          </InfoGrid>

          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              paddingTop: theme.spacing.lg,
              borderTop: `1px solid ${theme.colors.gray200}`,
            }}
          >
